frappe.ui.form.on('Item', {
    custom_material_type: function(frm) {
        // Set query for custom_gemstone field based on material type
        frm.set_query('custom_gemstone', function() {
            return {
                filters: [
                    ['Gemstones', 'stonepearl', '=', frm.doc.item_group]
                ]
            };
        });
        
         frm.set_query('custom_sub_type', function() {
            return {
                filters: [
                    ['Material Sub Types', 'type', '=', frm.doc.item_group]
                ]
            };
        });
    },
    
    refresh: function(frm){
        // Set query for custom_gemstone field based on material type
        frm.set_query('custom_gemstone', function() {
            return {
                filters: [
                    ['Gemstones', 'stonepearl', '=', frm.doc.item_group]
                ]
            };
        });
        
        frm.set_query('custom_sub_type', function() {
            return {
                filters: [
                    ['Material Sub Types', 'type', '=', frm.doc.item_group]
                ]
            };
        });
    }
});

frappe.ui.form.on('DocType', {
    setup: function(frm){
        frm.get_docfield("fields").allow_bulk_edit = 1;
    },
});

frappe.ui.form.on('Opportunity', {
  validate: function(frm) {
    frm.doc.items.forEach(function(row) {
      if (!row.rate) {
        row.rate = 1;
        row.amount = row.qty;
      }
    });
  }
});

// ========== COPY DESCRIPTION FROM LINKED SALES ORDER ==========
frappe.ui.form.on('Purchase Order', {
    onload: function(frm) {
        if (frm.doc.from_sales_order && frm.doc.items && frm.doc.items.length > 0) {
            frappe.db.get_doc('Sales Order', frm.doc.from_sales_order)
                .then(sales_order => {
                    sales_order.items.forEach((so_item, i) => {
                        let po_item = frm.doc.items[i];
                        if (po_item) {
                            po_item.description = so_item.description;
                        }
                    });
                    frm.refresh_field('items');
                });
        }
    }
});

