// ========== SET READ-ONLY FIELDS IN custom_bom_items ==========
function set_custom_bom_items_fields_read_only(frm) {
    if (frm.fields_dict.custom_bom_items?.grid) {
        const readOnlyFields = [
            'qty',
            'item_code',
            'item_group',
            'custom_country_of_origin',
            'supplier',
            'uom',
            'custom_gemstone',
            'stone_treatment',
            'grade',
            'cut',
            'setting_type',
            'sku_metal_weight',
            'merch_color__attribute',
            'sku_carat_weight',
            'avg_carat_weight',
            'stone_pieces'
        ];

        readOnlyFields.forEach(field => {
            frm.fields_dict.custom_bom_items.grid.update_docfield_property(field, 'read_only', 1);
        });
    }
}

// ========== SET READ-ONLY ON CHILD ROW OPEN ==========
frappe.ui.form.on('custom_bom_items', {
    form_rendered: function(frm, cdt, cdn) {
        const readOnlyFields = [
            'qty',
            'item_code',
            'item_group',
            'custom_country_of_origin',
            'supplier',
            'uom',
            'custom_gemstone',
            'stone_treatment',
            'grade',
            'cut',
            'setting_type',
            'sku_metal_weight',
            'merch_color__attribute',
            'sku_carat_weight',
            'avg_carat_weight',
            'stone_pieces'
        ];

        const grid_row = frm.fields_dict.custom_bom_items.grid.grid_rows_by_docname[cdn];
        if (grid_row?.grid_form) {
            readOnlyFields.forEach(field => {
                const df = grid_row.grid_form.fields_dict[field]?.df;
                if (df) {
                    df.read_only = 1;
                    grid_row.grid_form.fields_dict[field].refresh();
                }
            });
        }
    }
});





frappe.ui.form.on('Purchase Order', {
    refresh(frm) {
        // frm.add_custom_button(__('Fetch BOM from Sales Order'), () => {
        //     frm.trigger('custom_fetch_bom_from_sales_order');
        // });
    },

    async custom_fetch_bom_from_sales_order(frm) {
        const first_item = frm.doc.items && frm.doc.items.length > 0 ? frm.doc.items[0] : null;

        if (!first_item || !first_item.sales_order) {
            frappe.msgprint('First row of Items table must have a Sales Order linked.');
            return;
        }

        const sales_order_name = first_item.sales_order;

        try {
            const so = await frappe.db.get_doc('Sales Order', sales_order_name);

            if (so.custom_item_bom) {
                frm.set_value('custom_item_bom', so.custom_item_bom);
            }

            frm.clear_table('custom_bom_items');

            const field_map = {
                item_code: 'item_code',
                item_group: 'custom_item_group',
                item_description: 'description',
                qty: 'qty',
                uom: 'uom',
                rate: 'rate',
                amount: 'amount',
                custom_gemstone: 'custom_gemstone',
                our_responsibility: 'custom_our_responsibility',
                item_group: 'custom_item_group',
                sku_carat_weight: 'custom_sku_weight',
                avg_carat_weight: 'custom_avg_carat_weight',
                setting_type: 'custom_setting_type',
                merch_color__attribute: 'custom_merch_color__attribute',
                stone_treatment: 'custom_stone_treatment',
                stone_pieces: 'custom_stone_pieces',
                min_weight: 'custom_min_weight',
                custom_country_of_origin: 'custom_country_of_origin',
                metal_weight: 'custom_metal_weight',
                cut: 'custom_cut',
                length: 'custom_length',
                grade: 'custom_grade',
                weight_per_piece: 'custom_weight_per_piece',
                merch_color: 'custom_merch_color',
                avg_metal_weight: 'custom_metal_weight',
                sku_metal_weight: 'custom_sku_metal_weight'
            };

            for (let row of so.custom_bom_items || []) {
                const new_row = frm.add_child('custom_bom_items');
                for (let target_field in field_map) {
                    const source_field = field_map[target_field];
                    new_row[target_field] = row[source_field];
                }
            }

            frm.refresh_field('custom_bom_items');
            // frappe.msgprint(`Copied BOM items from Sales Order: ${sales_order_name}`);

        } catch (err) {
            console.error(err);
            frappe.msgprint('Error fetching Sales Order or processing data.');
        }
    }
});

// ================Metal Cost============================================
frappe.ui.form.on('Purchase Order', {
    refresh: async function(frm) {
        frm.add_custom_button(__('Metal Rate'), async function() {
            let table_data = [];
            frappe.call({
                method: "get_gold_rate",
                args: {
                    "docname": frm.doc.name
                },
                callback: function(r) {
                    if (r.data && r.data.length > 0) {
                        const dialog = new frappe.ui.Dialog({
                            title: 'Metal Rate',
                            fields: [
                                {
                                    fieldname: 'metal_rate_table',
                                    fieldtype: 'Table',
                                    label: 'Metal Rates',
                                    cannot_add_rows: 1,
                                    data: r.data,
                                    fields: [
                                        {
                                            fieldtype: 'Time',
                                            fieldname: 'time',
                                            label: 'Time',
                                            reqd: 1,
                                            in_list_view: true
                                        },
                                        {
                                            fieldtype: 'Data',
                                            fieldname: 'metal',
                                            label: 'Metal',
                                            reqd: 1,
                                            in_list_view: true
                                        },
                                        {
                                            fieldtype: 'Currency',
                                            fieldname: 'rate',
                                            label: 'Metal Rate',
                                            reqd: 1,
                                            in_list_view: true
                                        },
                                        /*{
                                            fieldtype: 'Check',
                                            fieldname: 'selected',
                                            label: 'Select',
                                            in_list_view: true
                                        },*/
                                        {
                                            fieldtype: 'Link',
                                            fieldname: 'item',
                                            label: 'Item',
                                            options: "Item",
                                            hidden: 1
                                        }
                                    ]
                                }
                            ],
                            primary_action_label: 'Insert',
                            primary_action(values) {
                                const table_data = values.metal_rate_table || [];

                                if (!table_data.length) {
                                    frappe.msgprint('Please enter at least one metal rate.');
                                    return;
                                }
                                console.log(table_data)
                                table_data.forEach(r => {
                                    if (r.__checked) {
                                        let matched_item = frm.doc.items.find(item => item.item_code === r.item);
                                        if (matched_item) {
                                            frappe.model.set_value(matched_item.doctype, matched_item.name, "rate", r.rate);
                                        }
                                    }
                                });

                                frm.refresh_field("items");
                                dialog.hide(); // Close after insert
                            }
                        });

                        // Add a "Close" button
                        dialog.set_secondary_action_label('Close');
                        dialog.set_secondary_action(() => {
                            dialog.hide();
                        });

                        dialog.show();
                    }
                }
            });
        });
    }
});

// --- Main: Sales Order triggers ---
frappe.ui.form.on('Sales Order', {
    refresh: function (frm) {
        if (frm.doc.docstatus === 0 && frm.doc.name) {
            frm.add_custom_button(__('Create Purchase Order'), function () {
                show_items_popup(frm);
            });
        }
    },

    custom_stone_vendor(frm) {
        update_bom_suppliers(frm);
    },

    custom_other_vendor(frm) {
        update_bom_suppliers(frm);
    },

    validate(frm) {
        update_bom_suppliers(frm);
    }
});

function update_bom_suppliers(frm) {
    (frm.doc.custom_bom_items || []).forEach(row => {
        if (!row.custom_is_supplier_overridden) {
            if (row.custom_our_responsibility == 1) {
                row.custom_supplier = row.custom_item_group === 'Stone'
                    ? frm.doc.custom_stone_vendor || ''
                    : frm.doc.custom_stone_vendor || '';
            } else {
                row.custom_supplier = frm.doc.custom_factory_vendor || '';
            }
        }
    });
    frm.refresh_field('custom_bom_items');
}


// --- Step 1: Items Dialog ---
function show_items_popup(frm, preselected_items = [], preselected_supplier = '') {
    const item_data = (frm.doc.items || [])
        .filter(row => !row.custom_purchase_order_no)
        .map(row => ({
            item_code: row.item_code,
            item_name: row.item_name,
            qty: row.qty,
            warehouse: row.warehouse || "All Warehouses",
            item_group: row.item_group
        }));

    const has_items = item_data.length > 0;

    let item_dialog = new frappe.ui.Dialog({
        title: 'Select Items for PO',
        fields: [
            {
                fieldname: 'supplier',
                label: 'Supplier',
                fieldtype: 'Link',
                options: 'Supplier',
                reqd: has_items,
                default: preselected_supplier || frm.doc.custom_factory_vendor || ''
            },
            {
                fieldname: 'item_selection',
                label: 'Items',
                fieldtype: 'Table',
                cannot_add_rows: true,
                in_place_edit: true,
                data: item_data,
                get_data: () => item_data,
                fields: [
                    { fieldname: 'item_code', fieldtype: 'Data', label: 'Item Code', read_only: 1, in_list_view: 1 },
                    { fieldname: 'item_name', fieldtype: 'Data', label: 'Item Name', read_only: 1, in_list_view: 1 },
                    { fieldname: 'qty', fieldtype: 'Float', label: 'Qty', read_only: 1, in_list_view: 1 },
                    { fieldname: 'item_group', fieldtype: 'Link', options: 'Item Group', label: 'Item Group', read_only: 1, in_list_view: 1 },
                    { fieldname: 'warehouse', fieldtype: 'Link', options: 'Warehouse', label: 'Warehouse', in_list_view: 1 }
                ]
            }
        ],
        primary_action_label: 'Proceed',
        primary_action() {
            const supplier = item_dialog.get_value('supplier');
            const selected_items = item_dialog.fields_dict.item_selection.grid.get_selected_children();

            if (has_items && !supplier) {
                frappe.throw(__('Please select a Supplier before proceeding.'));
                return;
            }

            selected_items.forEach(row => {
                row.supplier = supplier;
            });

            item_dialog.hide();
            show_bom_popup(frm, selected_items, supplier);
        },
        secondary_action_label: 'Cancel',
        secondary_action() {
            item_dialog.hide();
        }
    });

    item_dialog.show();

    frappe.after_ajax(() => {
        const grid = item_dialog.fields_dict.item_selection.grid;
        const supplier_field = item_dialog.get_field('supplier');

        function auto_check_items() {
            if (supplier_field.get_value()) {
                grid.grid_rows.forEach(row => {
                    row.doc.__checked = 1;
                    row.refresh();
                });
            }
        }

        auto_check_items();

        supplier_field.df.onchange = auto_check_items;

        // Prevent row deletion
        $(grid.wrapper).find('.grid-remove-rows').hide();
        grid.wrapper.addEventListener('click', () => {
            $(grid.wrapper).find('.grid-remove-rows').hide();
        });
    });
}

// --- Step 2: BOM Items Dialog ---
function show_bom_popup(frm, selected_items, supplier) {
    const bom_data = (frm.doc.custom_bom_items || [])
        .filter(row => !row.custom_purchase_order_id)
        .map(row => ({
            item_code: row.item_code,
            item_name: row.item_name,
            qty: row.qty,
            supplier: row.custom_supplier || "",
            description: row.description || "",
            _row_idx: row.idx,
            __checked:1
        }));

    let bom_dialog = new frappe.ui.Dialog({
        title: 'Select BOM Items for PO',
        fields: [
            {
                fieldname: 'bom_selection',
                label: 'BOM Items',
                fieldtype: 'Table',
                cannot_add_rows: true,
                in_place_edit: true,
                data: bom_data,
                get_data: () => bom_data,
                fields: [
                    { fieldname: 'item_code', fieldtype: 'Data', label: 'Item Code', read_only: 1, in_list_view: 1 },
                    { fieldname: 'item_name', fieldtype: 'Data', label: 'Item Name', read_only: 1, in_list_view: 1 },
                    { fieldname: 'qty', fieldtype: 'Float', label: 'Qty', read_only: 1, in_list_view: 1 },
                    { fieldname: 'supplier', fieldtype: 'Link', options: 'Supplier', label: 'Supplier', in_list_view: 1 }
                ]
            }
        ],
        primary_action_label: 'Create',
        primary_action(values) {
            const selected_bom = bom_dialog.fields_dict.bom_selection.grid.get_selected_children();
            (values.bom_selection || []).forEach(row => {
            let existing_row = frm.doc.custom_bom_items.find(r => r.item_code === row.item_code);
                if (existing_row) {
                     frappe.model.set_value(existing_row.doctype, existing_row.name, 'custom_supplier', row.supplier);
                }
            });
            bom_dialog.hide();
            create_purchase_orders(frm, selected_items, selected_bom);
        },
        secondary_action_label: 'Back',
        secondary_action() {
            bom_dialog.hide();
            show_items_popup(frm, selected_items, supplier);
        },
        tertiary_action_label: 'Cancel',
        tertiary_action() {
            bom_dialog.hide();
        }
    });

    bom_dialog.show();

    frappe.after_ajax(() => {
        const grid = bom_dialog.fields_dict.bom_selection.grid;

        setTimeout(() => {
            const debounce = (func, wait) => {
                let timeout;
                return (...args) => {
                    clearTimeout(timeout);
                    timeout = setTimeout(() => func.apply(this, args), wait);
                };
            };

            const save_form = debounce(() => {
                frm.refresh_field("custom_bom_items");
                frm.save();
            }, 500);

            grid.grid_rows.forEach(row => {
                row.doc.__checked = 1;
                row.refresh();

                row.fields_dict.supplier.$wrapper.on('input', () => {
                    const new_supplier = row.fields_dict.supplier.get_value();
                    const bom_row = frm.doc.custom_bom_items.find(r => r.idx === row.doc._row_idx);

                    if (bom_row) {
                        bom_row.custom_supplier = new_supplier;
                        bom_row.custom_is_supplier_overridden = new_supplier ? 1 : 0;
                        save_form();
                    }
                });
            });

            $(grid.wrapper).find('.grid-remove-rows').hide();
            grid.wrapper.addEventListener('click', () => {
                $(grid.wrapper).find('.grid-remove-rows').hide();
            });
        }, 100);
    });
}


function create_purchase_orders(frm, items, bom_items) {
    const all_items = [...items, ...bom_items];
    const items_by_supplier = {};

    const get_company_abbr = (company_name) => {
        if (!company_name) return "UNDEF";
        const known_abbrs = {
            "Bright Blue Jewels": "BBJ",
            "Some Other Company": "SOC"
        };
        return known_abbrs[company_name] || company_name.split(" ").map(w => w[0]).join("").toUpperCase();
    };

    const company_abbr = get_company_abbr(frm.doc.company);
    const default_warehouse = "Stores - " + company_abbr;

    all_items.forEach(row => {
        if (row.item_code && row.supplier) {
            if (!items_by_supplier[row.supplier]) {
                items_by_supplier[row.supplier] = [];
            }

            const warehouse = row.warehouse || frm.doc.set_warehouse || default_warehouse;
            const so_item = frm.doc.items.find(i => i.item_code === row.item_code && !i.custom_purchase_order_no && i.qty === row.qty);
            const bom_item = (frm.doc.custom_bom_items || []).find(i => i.item_code === row.item_code && i.qty === row.qty && i.custom_supplier === row.supplier);

            items_by_supplier[row.supplier].push({
                item_code: row.item_code,
                qty: row.qty,
                schedule_date: frappe.datetime.now_date(),
                uom: "Nos",
                rate: 1,
                warehouse: warehouse,
                sales_order:  frm.doc.name,
                description: so_item?.description || bom_item?.description || ""
            });
        }
    });

    const suppliers = Object.keys(items_by_supplier);
    const created_po_names = [];

    const create_next_po = function(index) {
        if (index >= suppliers.length) {
            frm.refresh_field("custom_bom_items");
            frm.refresh_field("items");

            if (created_po_names.length) {
                frappe.confirm(
                    'View Purchase Orders created?',
                    function() {
                        frappe.set_route('List', 'Purchase Order', {
                            name: ["in", created_po_names]
                        });
                    },
                    function() {
                        const links = created_po_names.map(name =>
                            `<a href="/app/purchase-order/${name}" target="_blank">${name}</a>`
                        ).join('<br>');

                        frappe.msgprint({
                            title: __('Purchase Orders Created'),
                            message: links,
                            indicator: 'green'
                        });
                    }
                );
            } else {
                frappe.msgprint(__('No Purchase Orders were created.'));
            }
            return;
        }

        const supplier = suppliers[index];
        const doc = {
            doctype: 'Purchase Order',
            supplier: supplier,
            schedule_date: frappe.datetime.now_date(),
            items: items_by_supplier[supplier],
            company: frm.doc.company,
            sales_order: frm.doc.title
,
            set_warehouse: frm.doc.set_warehouse || default_warehouse
        };

        frappe.call({
            method: 'frappe.client.insert',
            args: { doc },
            callback: function(r) {
                if (r.message && r.message.name) {
                    const po_name = r.message.name;
                    created_po_names.push(po_name);

                    (frm.doc.custom_bom_items || []).forEach(row => {
                        if (
                            row.custom_supplier === supplier &&
                            !row.custom_purchase_order_id
                        ) {
                            row.custom_purchase_order_id = po_name;
                        }
                    });

                    (frm.doc.items || []).forEach(row => {
                        if (
                            row.supplier === supplier &&
                            !row.custom_purchase_order_no
                        ) {
                            frappe.model.set_value(row.doctype, row.name, "custom_purchase_order_no", po_name);
                        }
                        frm.save();
                    });

                }

                create_next_po(index + 1);
            },
            error: function(err) {
                frappe.msgprint(__('Error creating PO for ') + supplier);
                create_next_po(index + 1);
            }
        }); 
    };

    create_next_po(0);
}



frappe.ui.form.on('Sales Order', {
    setup(frm) {
        frm.fields_dict.custom_bom_items.grid.get_field("custom_gemstone").onchange = function (doc, cdt, cdn) {
            const row = locals[cdt][cdn];
            if (row.custom_gemstone) {
                frappe.call({
                    method: "get_country_of_origin",
                    args: {
                        gemstone: row.custom_gemstone
                    },
                    callback: function (r) {
                        if (r.data) {
                            const allowed_countries = r.data.map(entry => entry[1]);
                            const unique_countries = [...new Set(allowed_countries)];
                            frm.fields_dict.custom_bom_items.grid.update_docfield_property(
                                "custom_country_of_origin",
                                "options",
                                unique_countries
                            );
                        }
                    }
                });
            }
        };
    },

    onload(frm) {
        frm.fields_dict.custom_bom_items.grid.get_field('custom_country_of_origin').reqd = false;
    },

    validate(frm) {
        let has_error = false;
        (frm.doc.custom_bom_items || []).forEach(row => {
            if (row.custom_gemstone && !row.custom_country_of_origin) {
                has_error = true;
                frappe.msgprint(`Please enter Country of Origin for gemstone: ${row.custom_gemstone}`);
            }
        });
        if (has_error) frappe.validated = false;
    }
});


frappe.ui.form.on('BOM Item', {
    custom_gemstone: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        frappe.meta.get_docfield("BOM Item", "custom_country_of_origin", frm.doc.name).reqd = !!row.custom_gemstone;

        if (row.custom_gemstone) {
            frappe.call({
                method: "get_country_of_origin",
                args: {
                    gemstone: row.custom_gemstone
                },
                callback: function(r) {
                    if (r.data) {
                        const allowed_countries = [...new Set(r.data.map(entry => entry[1]))];

                        frm.fields_dict.custom_bom_items.grid.update_docfield_property(
                            'custom_country_of_origin',
                            'options',
                            allowed_countries
                        );

                        frappe.model.set_value(cdt, cdn, "custom_country_of_origin", null);
                        frm.fields_dict.custom_bom_items.grid.refresh();
                    }
                }
            });
        } else {
            // Reset field if gemstone is cleared
            frappe.model.set_value(cdt, cdn, "custom_country_of_origin", null);
            frm.fields_dict.custom_bom_items.grid.refresh();
        }
    },

    form_render: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        if (row.custom_gemstone) {
            frappe.call({
                method: "get_country_of_origin",
                args: {
                    gemstone: row.custom_gemstone
                },
                callback: function(r) {
                    if (r.data) {
                        const allowed_countries = [...new Set(r.data.map(entry => entry[1]))];

                        frm.fields_dict.custom_bom_items.grid.update_docfield_property(
                            'custom_country_of_origin',
                            'options',
                            allowed_countries
                        );
                    }
                }
            });
        }

        if (row.custom_country) {
            frappe.model.set_value(cdt, cdn, "custom_country_of_origin", row.custom_country);
        }
    },

    custom_country: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        if (row.custom_country) {
            frappe.model.set_value(cdt, cdn, "custom_country_of_origin", row.custom_country);
        }
    },
    custom_supplier: function (frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        const has_value = row.custom_supplier && row.custom_supplier.trim() !== '';

        frappe.model.set_value(cdt, cdn, 'custom_is_supplier_overridden', has_value ? 1 : 0);
    },
    
    custom_gemstone: function (frm, cdt, cdn) {
        update_bom_description(frm, cdt, cdn);
    },
    custom_country_of_origin: function (frm, cdt, cdn) {
        update_bom_description(frm, cdt, cdn);
    }
});

function update_bom_description(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    let original_description = row.description || '';

    const gemstone = row.custom_gemstone || '';
    const country = row.custom_country_of_origin || '';

    let updated_description = original_description;

    if (updated_description.includes("Gemstone:")) {
        updated_description = updated_description.replace(/(Gemstone:\s*)([^|]*)/, `$1${gemstone}`);
    } else {
        updated_description += ` | Gemstone: ${gemstone}`;
    }

    if (updated_description.includes("Country:")) {
        updated_description = updated_description.replace(/(Country:\s*)([^|]*)/, `$1${country}`);
    } else {
        updated_description += ` | Country: ${country}`;
    }

    frappe.model.set_value(cdt, cdn, 'description', updated_description);
}


frappe.ui.form.on('Sales Order', {
    custom_factory_vendor: function(frm) {
        update_supplier_in_items(frm);
    },
    onload: function(frm) {
        update_supplier_in_items(frm);
        set_warehouse_from_company(frm);
    },
    refresh: function(frm) {
        update_supplier_in_items(frm);
    },
    company: function(frm) {
        set_warehouse_from_company(frm);
    }
});

frappe.ui.form.on('Sales Order Item', {
    items_add: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        row.supplier = frm.doc.custom_factory_vendor || '';
        frm.refresh_field('items');
    }
});

function update_supplier_in_items(frm) {
    if (!frm.doc.custom_factory_vendor) return;

    (frm.doc.items || []).forEach(item => {
        item.supplier = frm.doc.custom_factory_vendor;
    });

    frm.refresh_field('items');
}

async function set_warehouse_from_company(frm) {
    if (!frm.doc.company) {
        frm.set_value('set_warehouse', '');
        return;
    }

    try {
        const res = await frappe.db.get_value('Company', frm.doc.company, 'abbr');
        const abbr = res?.message?.abbr;

        if (abbr) {
            frm.set_value('set_warehouse', `Stores - ${abbr}`);
        }
    } catch (err) {
        console.error("Error fetching company abbreviation:", err);
        frm.set_value('set_warehouse', '');
    }
}

