});

frappe.ui.form.on('Sales Order', {
    onload: function(frm) {
        if (!frm.doc.custom_item_group_to_filter_items) {
            frm.set_value('custom_item_group_to_filter_items', 'SKU');
        }
        frm.trigger('apply_item_group_filter');
    },
    refresh: function(frm) {
        frm.trigger('apply_item_group_filter');
        frm.trigger('set_custom_bom_query');
    },
    custom_item_group_to_filter_items: function(frm) {
        frm.trigger('apply_item_group_filter');
    },
    items_on_form_rendered: function(frm) {
        frm.trigger('set_custom_bom_query');
    },
    set_custom_bom_query: function(frm) {
        frm.set_query('custom_item_bom', function(doc) {
            if (doc.items && doc.items.length > 0 && doc.items[0].item_code) {
                return {
                    filters: [
                        ['BOM', 'item', '=', doc.items[0].item_code]
                    ]
                };
            }
            return {};
        });
    },
    apply_item_group_filter: function(frm) {
        frm.fields_dict['items'].grid.get_field('item_code').get_query = function() {
            return {
                filters: [
                    ["Item", "item_group", "descendants of (inclusive)", frm.doc.custom_item_group_to_filter_items || "SKU"]
                ]
            };
        };

        if (frm.doc.items && frm.doc.items.length > 0) {
            frm.fields_dict['items'].grid.refresh();
        }
    }
});

frappe.ui.form.on('Sales Order Item', {
    item_code: function(frm, cdt, cdn) {
        frm.trigger('set_custom_bom_query');
    }
});

