frappe.ui.form.on('Sales Order Item', {
    item_code(frm, cdt, cdn) {
        const child = locals[cdt][cdn];
        if (!child.item_code) return;

        frappe.db.get_value('Item', child.item_code, ['item_group', 'item_name']).then(r => {
            const item_group = r.message.item_group;
            const item_name = r.message.item_name;

            frappe.model.set_value(cdt, cdn, 'item_group', item_group);
            frappe.model.set_value(cdt, cdn, 'item_name', item_name);

            const ALLOWED_ITEM_GROUPS = ['Ring', 'Set'];
            const has_target_item = frm.doc.items.some(i =>
                ALLOWED_ITEM_GROUPS.includes(i.item_group) && !i.custom_size
            );

            if (has_target_item && !frm.custom_ring_button_added) {
                add_ring_button(frm, ALLOWED_ITEM_GROUPS);
            }
        });
    }
});

function add_ring_button(frm, ALLOWED_ITEM_GROUPS) {
    frm.add_custom_button('Add Sizes for Ring/Set', function () {
        const has_invalid = frm.doc.items.some(i =>
            ALLOWED_ITEM_GROUPS.includes(i.item_group) &&
            (!i.rate || i.rate <= 0 || !i.qty || i.qty <= 0) &&
            !i.custom_size
        );

        const target_item = frm.doc.items.find(i =>
            ALLOWED_ITEM_GROUPS.includes(i.item_group) && !i.custom_size
        );

        if (!target_item) {
            frappe.msgprint(__('No valid Ring or Set item found.'));
            return;
        }

        if (has_invalid) {
            frappe.confirm(
                __("Some 'Ring/Set' items have rate = 0 or qty  0. Proceed anyway?"),
                function () {
                    show_ring_size_dialog(frm, target_item, ALLOWED_ITEM_GROUPS);
                },
                function () { }
            );
        } else {
            show_ring_size_dialog(frm, target_item, ALLOWED_ITEM_GROUPS);
        }
    });

    frm.custom_ring_button_added = true;
}

function show_ring_size_dialog(frm, item, ALLOWED_ITEM_GROUPS) {
    const ordered_qty = frm.doc.total_qty || item.qty;
    const size_list = [5, 6, 7, 8, 9, 10, 11, 12];

    const dialog = new frappe.ui.Dialog({
        title: 'Enter Sizes & Quantities for Ring/Set',
        fields: [
            { fieldtype: 'HTML', fieldname: 'header_section' },
            { fieldtype: 'HTML', fieldname: 'ring_table' }
        ],
        primary_action_label: 'Update',
        primary_action() {
            let data = [];
            let updated_qty = 0;

            $(dialog.$wrapper).find('.ring-row').each(function () {
                const size = $(this).find('.ring-size').val();
                const qty = parseInt($(this).find('.ring-qty').val()) || 0;
                if (size && qty > 0) {
                    data.push({ size, qty });
                    updated_qty += qty;
                }
            });

            if (data.length === 0) {
                frappe.msgprint(__('Please enter at least one valid size and quantity.'));
                return;
            }

            const index = frm.doc.items.findIndex(i =>
                i.item_code === item.item_code &&
                i.qty === item.qty &&
                i.rate === item.rate &&
                !i.custom_size
            );
            if (index !== -1) {
                frm.doc.items.splice(index, 1);
            }

            data.forEach(row => {
                frm.add_child('items', {
                    item_code: item.item_code,
                    item_name: item.item_name,
                    qty: row.qty,
                    rate: item.rate,
                    custom_size: row.size,
                    uom: item.uom,
                    description: item.description,
                    delivery_date: item.delivery_date,
                    item_group: item.item_group,
                    custom_purchase_order_no: item.custom_purchase_order_no
                });
            });

            frm.refresh_field('items');
            dialog.hide();
            frm.clear_custom_buttons();
            frm.custom_ring_button_added = false;
        }
    });

    // Header HTML
    const header_html = `
        <div class="row" style="display: flex; gap: 10px;">
            <div class="col" style="background-color:#ffebee; padding: 10px; border-radius: 4px; width: 100%;">
                <b>Item:</b> ${item.item_code}
                <span style="float:right"><b>Ordered Qty:</b> ${ordered_qty}</span>
            </div>
            <div class="col" style="background-color:#e8f5e9; padding: 10px; border-radius: 4px; width: 100%;">
                <b>Updated Qty:</b> <span class="updated-qty">0</span>
                <span style="float:right"><b>Difference:</b> <span class="qty-diff">${-ordered_qty}</span></span>
            </div>
        </div><br/>
    `;
    dialog.fields_dict.header_section.$wrapper.html(header_html);

    // Ring rows HTML
    const rows_html = size_list.map(size => `
        <div class="ring-row" style="display: flex; gap: 10px; margin-bottom: 8px;">
            <input type="text" class="ring-size form-control" value="${size}" placeholder="Size" style="width: 100%;">
            <input type="number" class="ring-qty form-control" placeholder="Enter qty" style="width: 100%;">
        </div>
    `).join('');

    dialog.fields_dict.ring_table.$wrapper.html(`
        <div>
            <div style="display: flex; font-weight: bold; gap: 10px; margin-bottom: 8px;">
                <div style="width: 100%;">Size</div>
                <div style="width: 100%;">Order Qty</div>
            </div>
            <div class="ring-rows">
                ${rows_html}
            </div>
            <button type="button" class="btn btn-sm btn-secondary add-ring-row" style="margin: 10px 0;">Add Row</button>
            <div style="margin-top:10px; font-size: 12px; color: #996600; background-color: #fff8e1; padding: 8px; border-radius: 4px;">
                <b>Note:</b> You can edit or add any sizes as needed. Ensure total matches ordered quantity.
            </div>
        </div>
    `);

    function update_qty_status() {
        let total = 0;
        dialog.$wrapper.find('.ring-qty').each(function () {
            total += parseInt($(this).val()) || 0;
        });
        const diff = total - ordered_qty;
        dialog.$wrapper.find('.updated-qty').text(total);
        dialog.$wrapper.find('.qty-diff').text(diff);

        if (diff === 0) {
            dialog.get_primary_btn().removeClass('btn-disabled').show();
        } else {
            dialog.get_primary_btn().hide();
        }
    }

    dialog.$wrapper.on('input', '.ring-qty', update_qty_status);

    dialog.$wrapper.on('click', '.add-ring-row', function () {
        const new_row = `
            <div class="ring-row" style="display: flex; gap: 10px; margin-bottom: 8px;">
                <input type="text" class="ring-size form-control" placeholder="Size" style="width: 100%;">
                <input type="number" class="ring-qty form-control" placeholder="Enter qty" style="width: 100%;">
            </div>
        `;
        dialog.$wrapper.find('.ring-rows').append(new_row);
        update_qty_status();
    });

    dialog.get_primary_btn().hide();
    dialog.show();
}

frappe.ui.form.on('Sales Order', {
    onload(frm) {
        frm.set_query('custom_item_bom', () => {
            const item_codes = (frm.doc.items || []).map(row => row.item_code);
            if (!item_codes.length) return { filters: { docstatus: 1 } };
    
            return {
                filters: {
                    item: ["in", item_codes],
                    is_default: 1
                }
            };
        });
    },

    onload_post_render(frm) {
        frm.trigger("recalculate_bom_quantities");
        set_custom_bom_items_fields_read_only(frm);
    },
    
    refresh(frm) {
        frm.trigger("recalculate_bom_quantities");
    },
    
    total_qty(frm) {
        frm.trigger("recalculate_bom_quantities");
    },

    before_save(frm) {
        const firstItem = frm.doc.items?.[0];
        if (firstItem?.image) {
            frm.set_value('custom_image_product', firstItem.image);
        }
    },

    validate(frm) {
        frm.trigger("recalculate_bom_quantities");

        if (!frm.doc.custom_item_bom) {
            for (let item of frm.doc.items || []) {
                if (item.bom_no) {
                    frm.set_value('custom_item_bom', item.bom_no);
                    break;
                }
            }
        }
    },

    recalculate_bom_quantities(frm) {
        const multiplier = frm.doc.total_qty || 1;
    
        frm.doc.custom_bom_items?.forEach(bom_item => {
            const qty = flt(multiplier) * flt(bom_item.custom_quantity || 1);
            const rate = flt(bom_item.rate || 0);
    
            bom_item.qty = qty;
            bom_item.amount = qty * rate;
        });
    
        frm.refresh_field('custom_bom_items');
    },


    async custom_item_bom(frm) {
        if (!frm.doc.custom_item_bom) return;

        try {
            const { message } = await frappe.db.get_value('BOM', frm.doc.custom_item_bom, 'docstatus');
            if (!message || message.docstatus === 2) return;

            frm.clear_table('custom_bom_items');
            const total_qty = frm.doc.total_qty || 1;

            const { message: bomDoc } = await frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "BOM",
                    name: frm.doc.custom_item_bom
                }
            });
            

            const bom_qty = bomDoc.quantity || 1;

            for (const bom_item of bomDoc.items || []) {
                const row = frm.add_child('custom_bom_items');
                row.item_code = bom_item.item_code;
                row.item_name = bom_item.item_name;
                row.base_qty = bom_item.qty;
                row.custom_quantity = bom_item.qty;
                row.uom = bom_item.uom;
                row.stock_uom = bom_item.stock_uom;
                row.rate = bom_item.rate || 0;
                row.qty = total_qty * bom_item.qty;
                row.amount = row.qty * row.rate;
                
                row.description = bom_item.description || '';
                row.custom_selling_rate = bom_item.custom_selling_rate || '',
                row.custom_selling_amount = bom_item.custom_selling_amount || '',
                row.custom_stone_treatment = bom_item.custom_stone_treatment || '';
                row.custom_sku_weight = bom_item.custom_sku_weight || 0;
                row.custom_stone_pieces = bom_item.custom_stone_pieces || 0;
                row.custom_min_weight = bom_item.custom_min_weight || 0;
                row.custom_gemstone = bom_item.custom_gemstone || '';
                row.custom_country_of_origin = bom_item.custom_country_of_origin || '';
                row.custom_merch_color__attribute = bom_item.custom_merch_color__attribute || '';
                row.custom_metal_weight = bom_item.custom_metal_weight || 0;
                row.custom_avg_carat_weight = bom_item.custom_avg_carat_weight || 0;
                row.custom_cut = bom_item.custom_cut || '';
                row.custom_length = bom_item.custom_length || 0;
                row.custom_oh = bom_item.custom_oh || 0;
                row.custom_ext_qty_required = bom_item.custom_ext_qty_required || 0;
                row.custom_net_balance_required = bom_item.custom_net_balance_required || 0;
                row.custom_grade = bom_item.custom_grade || '';
                row.custom_weight_per_piece = bom_item.custom_weight_per_piece || 0;
                row.custom_merch_color = bom_item.custom_merch_color || '';
                row.supplier = bom_item.supplier || '';
                row.custom_our_responsibility = bom_item.custom_our_responsibility || 0;
                row.custom_item_group = bom_item.custom_item_group || '';
                row.custom_supplier = bom_item.custom_supplier || '';
                row.custom_setting_type = bom_item.custom_setting_type || '';
                row.custom_sku_metal_weight = bom_item.custom_sku_metal_weight || 0;

                // if (bom_item.item_code) {
                //     frappe.call({
                //         method: "get_latest_po_for_item",
                //         args: { item_code: bom_item.item_code },
                //         callback(r) {
                //             if (r?.data?.parent) {
                //                 frappe.model.set_value(row.doctype, row.name, "custom_purchase_order_id", r.data.parent);
                //             }
                //         }
                //     });
                // }
            }
            frm.set_value('custom_automated', 1);
            frm.refresh_field('custom_bom_items');
            frm.trigger('update_specs_from_bom');
            set_custom_bom_items_fields_read_only(frm);

        } catch (err) {
            console.error('Error loading BOM items:', err);
            frappe.msgprint(__('Failed to load BOM items. Please check the console for details.'));
        }
    },

    update_specs_from_bom(frm) {
        frm.clear_table('custom_specs');

        if (!frm.doc.custom_item_bom || !frm.doc.custom_bom_items?.length) return;

        for (const bom_item of frm.doc.custom_bom_items) {
            const specs_row = frm.add_child('custom_specs');
            specs_row.item_code = bom_item.item_code || '';
            specs_row.grade = bom_item.custom_grade || '';
            specs_row.country_of_origin = bom_item.custom_country_of_origin || '';
            specs_row.merch_color = bom_item.custom_merch_color || '';
            specs_row.item_group = bom_item.item_group || '';
            specs_row.qty = bom_item.qty || 0;
            specs_row.uom = bom_item.uom || '';
            specs_row.rate = bom_item.rate || 0;
        }

        frm.refresh_field('custom_specs');
    }
});

