    if (frm.fields_dict.custom_bom_items?.grid) {
        const readOnlyFields = [
            'qty',
            'item_code',
            'custom_item_group',
            'uom',
            'custom_stone_treatment',
            'custom_grade',
            'custom_cut',
            'custom_setting_type',
            'custom_sku_metal_weight',
            'custom_merch_color__attribute',
            'custom_sku_weight',
            'custom_avg_carat_weight',
            'custom_stone_pieces'
