function initialize_base_qty(frm) {
        if (frm.doc.__islocal){
            return
        }
        (frm.doc.custom_bom_items || []).forEach(row => {
            if (!row.base_qty && row.custom_quantity) {
                row.base_qty = row.custom_quantity;
            }
            if (!row.custom_quantity && row.base_qty) {
                row.custom_quantity = row.base_qty;
            }
            if (!row.custom_quantity && !row.base_qty) {
                row.custom_quantity = 1;
                row.base_qty = 1;
            }
        });
}


