async function update_custom_item_details(frm) {
    let html = "";
    let show_field = false;
    const mainStone = frm.doc.custom_main_stone || "";

    const firstItem = (frm.doc.items || [])[0];
    if (!firstItem) {
        frm.set_df_property('custom_item_details', 'hidden', 1);
        return;
    }

    const itemCode = firstItem.item_code;
    const itemGroup = firstItem.item_group;
    let parentGroup = "";

    try {
        const res = await frappe.db.get_value("Item Group", itemGroup, "parent_item_group");
        parentGroup = res?.message?.parent_item_group;
    } catch (err) {
        console.error("Error getting parent_item_group for", itemGroup, err);
    }

    const vendorId = firstItem.custom_vendor_product_id;
    const customerSku = firstItem.custom_customer_sku;

    if ((itemGroup === "SKU" || parentGroup === "SKU") && (vendorId || customerSku)) {
        show_field = true;

        let imageUrl = "";
        try {
            const res = await frappe.db.get_value("Item", itemCode, "image");
            imageUrl = res?.message?.image;
        } catch (err) {
            console.warn("No image found for item:", itemCode);
        }

        html += `<div style="margin-bottom:15px; padding-bottom:10px;">`;
        if (imageUrl) {
            html += `<img src="${imageUrl}" alt="${itemCode}" style="margin-top:8px; max-height:140px;"><br>`;
        }
        if (vendorId) {
            html += `<div style="padding-top:6px;"><strong>Vendor ID:</strong> ${vendorId}</div>`;
        }
        if (customerSku) {
            html += `<div style="padding-top:6px;"><strong>Customer SKU:</strong> ${customerSku}</div>`;
        }
        if (mainStone) {
            html += `<div style="padding-top:6px;"><strong>Main Stone:</strong> ${mainStone}</div>`;
        }
        html += `</div>`;
    }

    if (show_field) {
        frm.set_df_property('custom_item_details', 'options', html);
        frm.set_df_property('custom_item_details', 'hidden', 0);
    } else {
        frm.set_df_property('custom_item_details', 'hidden', 1);
    }

    frm.refresh_field('custom_item_details');
}

function show_progress_bar(frm) {
    const steps = ['Sales Order', 'Delivery Note', 'Sales Invoice', 'Payment Entry'];
    let currentStep = 0;

    if (frm.doc.payment_status === 'Paid') {
        currentStep = 3;
    } else if (frm.doc.per_billed >= 100) {
        currentStep = 2;
    } else if (frm.doc.per_delivered >= 100) {
        currentStep = 1;
    } else {
        currentStep = 0;
    }

    let progressHTML = `<div style="display: flex; justify-content: space-between; align-items: center; margin: 20px 0; position: relative;">`;

    steps.forEach((step, index) => {
        const isCompleted = index <= currentStep;

        if (index > 0) {
            progressHTML += `
                <div style="
                    flex-grow: 1;
                    height: 2px;
                    background-color: ${index <= currentStep ? '#007bff' : '#ccc'};
                "></div>
            `;
        }

        progressHTML += `
            <div style="text-align: center; position: relative;">
                <div style="
                    width: 12px;
                    height: 12px;
                    border-radius: 50%;
                    background-color: ${isCompleted ? '#007bff' : '#ccc'};
                    margin: 0 auto;
                    position: relative;
                    z-index: 1;
                "></div>
                <div style="
                    margin-top: 6px;
                    font-size: 12px;
                    color: ${isCompleted ? '#007bff' : '#666'};
                    font-weight: ${isCompleted ? '600' : 'normal'};
                ">${step}</div>
            </div>
        `;
    });

    progressHTML += `</div>`;

    if (frm.fields_dict.custom_progress_status) {
        frm.fields_dict.custom_progress_status.$wrapper.html(progressHTML);
    }
}

