function updateBOMTotals(frm) {
    const items = frm.doc.custom_bom_items || [];
    const row_count = items.length;

    if (!row_count) return;

    let total_selling = 0;

    items.forEach(row => {
        total_selling += flt(row.custom_selling_amount || 0);
    });

    if (frm.doc.customer) {
        frappe.db.get_value("Customer", frm.doc.customer, "default_currency", (customerData) => {
            const currency = customerData.default_currency;

            frappe.db.get_value("Currency", currency, "symbol", (currencyData) => {
                const symbol = currencyData.symbol;

                if (frm.fields_dict.custom_bom_item_total?.$wrapper) {
                    frm.fields_dict.custom_bom_item_total.$wrapper.html(`
                        <div style="padding: 8px;">
                            <b>Total BOM Items:</b> ${row_count}<br>
                            <b>Selling Amount:</b> ${symbol} ${total_selling.toFixed(2)}
                        </div>
                    `);
                }
            });
        });
    }
}

