frappe.ui.form.on('BOM', {
    setup: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn]
        frappe.call({
            method: "get_country_of_origin",
            args: {
                gemstone: row.custom_gemstone
            },
            callback: function(r) {
                if (r.data) {
                    const allowed_countries = r.data.map(entry => entry[1]);
                    let unique_countries = [...new Set(allowed_countries)];
                    console.log(unique_countries);
                    cur_frm.fields_dict.items.grid.update_docfield_property(
                        'custom_country_of_origin',
                        'options',
                        unique_countries
                    );
                }
            }
        });
    },

    onload: function(frm) {
        // Set query for item filter
        frm.set_query('item', function() {
            if (frm.doc.custom_sku_filter) {
                return {
                    filters: {
                        item_group: 'SKU'
                    }
                };
            } else {
                return {}; 
            }
        });

        // Set country of origin field requirements
        frm.fields_dict.items.grid.get_field('custom_country_of_origin').reqd = false;

        // Add default items for new BOM
        if (frm.is_new()) {
            frm.clear_table('items');

            const default_items = ['CPF', 'Setting', 'Plating'];

            default_items.forEach(item_code => {
                let row = frm.add_child('items');
                row.item_code = item_code;
                row.qty = 1;

                frappe.db.get_value('Item', item_code, 'stock_uom')
                    .then(r => {
                        if (r && r.message) {
                            row.uom = r.message.stock_uom;
                            row.rate = r.message.valuation_rate;
                            row.custom_item_group = r.message.item_group;
                            frm.refresh_field('items');
                        }
                    });
            });
        }
    },

    custom_sku_filter: function(frm) {
        frm.fields_dict['item'].get_query = function() {
            if (frm.doc.custom_sku_filter) {
                return {
                    filters: {
                        item_group: 'SKU'
                    }
                };
            } else {
                return {};
            }
        };
        frm.refresh_field('item');
    },

    refresh: function(frm) {
        if (!frm.is_new()) {
            // Add spec integration buttons
            add_spec_integration_buttons(frm);
        
        }
    },

    validate: function(frm) {
            let has_error = false;
            const required_groups = ["Stone", "Pearl", "Bead"];
    
            (frm.doc.items || []).forEach(row => {
                if (
                    required_groups.includes(row.custom_item_group) &&
                    row.custom_gemstone &&
                    !row.custom_country_of_origin
                ) {
                    has_error = true;
                    frappe.msgprint(`Please enter Country of Origin for gemstone "${row.custom_gemstone}" in item "${row.item_name}".`);
                }
    
                // Stone-specific validations
                if (row.custom_item_group === "Stone") {
                    if (row.uom === "Carat" && (!row.custom_avg_carat_weight || row.custom_avg_carat_weight === 0)) {
                        frappe.throw(`Row ${row.idx}: "Avg Carat Weight" is required when UOM is Carat for Stone items.`);
                    }
    
                    if (row.uom === "Piece" && (!row.custom_stone_pieces || row.custom_stone_pieces === 0)) {
                        frappe.throw(`Row ${row.idx}: "Stone Pieces" is required when UOM is Piece for the Stone items.`);
                    }
                }
            });
    
            if (has_error) frappe.validated = false;
        }
    });

