        calculate_ext_qty_and_weight(frm, cdt, cdn);
        calculate_selling_amount(cdt, cdn);
        update_bom_item_description_from_spec(frm, cdt, cdn);
    },

    custom_metal_weight: function(frm, cdt, cdn) {
        calculate_ext_qty_and_weight(frm, cdt, cdn);
        update_bom_item_description_from_spec(frm, cdt, cdn);
    },

    custom_ext_qty_required: function(frm, cdt, cdn) {
        calculate_net_balance(frm, cdt, cdn);
    },

    custom_oh: function(frm, cdt, cdn) {
        calculate_net_balance(frm, cdt, cdn);
    },

    custom_selling_rate: function(frm, cdt, cdn) {
        calculate_selling_amount(cdt, cdn);
    },

    custom_avg_carat_weight: function(frm, cdt, cdn) {
        calculate_qty_for_stones(frm, cdt, cdn);
        const row = locals[cdt][cdn];
        if (row.custom_item_group === "Stone" && row.uom === "Carat") {
            frappe.model.set_value(cdt, cdn, 'qty', row.custom_avg_carat_weight || 0);
        }
        update_bom_item_description_from_spec(frm, cdt, cdn);
    },

    uom: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        frm.fields_dict.items.grid.toggle_reqd('custom_avg_carat_weight', false, cdn);
        frm.fields_dict.items.grid.toggle_reqd('custom_stone_pieces', false, cdn);

        if (row.custom_item_group === "Stone") {
            if (row.uom === "Carat") {
                frm.fields_dict.items.grid.toggle_reqd('custom_avg_carat_weight', true, cdn);
                frappe.model.set_value(cdt, cdn, 'qty', row.custom_avg_carat_weight || 0);
            } else if (row.uom === "Piece") {
                frm.fields_dict.items.grid.toggle_reqd('custom_stone_pieces', true, cdn);
                frappe.model.set_value(cdt, cdn, 'qty', row.custom_stone_pieces || 0);
            }
        }

        calculate_qty_for_stones(frm, cdt, cdn);
        update_bom_item_description_from_spec(frm, cdt, cdn);
    },

    custom_item_group: function(frm, cdt, cdn) {
        calculate_qty_for_stones(frm, cdt, cdn);
        update_bom_item_description_from_spec(frm, cdt, cdn);
    }
});

// ============ DESCRIPTION FUNCTIONS - UPDATED FOR BOM ITEM SPEC ============

// Core description function that fetches from BOM Item Spec
function update_bom_item_description_from_spec(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    if (!row || !row.name) return;

    console.log("Generating description from BOM Item Spec fields...");

    // Get parent BOM name
    let bom_name = null;
    if (frm && frm.doc) {
        bom_name = frm.doc.name;
    } else {
        bom_name = row.parent;
    }

    if (!bom_name) {
        console.log("Cannot determine BOM name, falling back to basic description");
        build_basic_description(frm, cdt, cdn, row);
        return;
    }

    // Get BOM Item Spec for this BOM Item
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'BOM Item Spec',
            filters: {
                'bom': bom_name,
                'bom_item_id': row.name
            },
            fields: [
                'name', 'item', 'item_group', 'gemstone', 'stone_treatments', 
                'grade', 'cut', 'setting_type', 'merch_color_attribute', 
                'stone_pieces', 'country_of_origin', 'sku_carat_weight', 
                'avg_carat_weight', 'avg_metal_weight', 'sku_metal_weight',
                'length', 'weight_per_piece', 'metal_group'
            ],
            limit: 1
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                const spec = r.message[0];
                build_description_from_spec(frm, cdt, cdn, row, spec);
            } else {
                // Fallback to basic item info if no spec exists
                build_basic_description(frm, cdt, cdn, row);
            }
        }
    });
}

function build_description_from_spec(frm, cdt, cdn, row, spec) {
    let parts = [];

    // Use BOM Item Spec fields instead of custom fields
    if (row.item_name) parts.push(`Item Name: ${row.item_name}`);
    if (spec.item_group) parts.push(`Item Group: ${spec.item_group}`);
    if (spec.gemstone) parts.push(`Gemstone: ${spec.gemstone}`);
    //if (spec.stone_treatment) parts.push(`Treatment: ${spec.stone_treatment}`);
	// Handle stone_treatments multi-select table
    if (spec.stone_treatments && spec.stone_treatments.length > 0) {
        const treatments = spec.stone_treatments.map(t => t.stone_treatment).filter(t => t).join(', ');
        if (treatments) {
            parts.push(`Stone Treatments: ${treatments}`);
        }
    }
    if (spec.grade) parts.push(`Grade: ${spec.grade}`);
    if (spec.cut) parts.push(`Cut: ${spec.cut}`);
    if (spec.setting_type) parts.push(`Setting Type: ${spec.setting_type}`);
    if (spec.merch_color_attribute) parts.push(`Merch Color: ${spec.merch_color_attribute}`);
    if (spec.stone_pieces) parts.push(`Pieces: ${spec.stone_pieces}`);
    if (spec.country_of_origin) parts.push(`Country: ${spec.country_of_origin}`);
    if (spec.sku_carat_weight) parts.push(`SKU Weight: ${spec.sku_carat_weight}`);
    if (spec.avg_carat_weight) parts.push(`Avg Carat: ${spec.avg_carat_weight}`);
    if (spec.avg_metal_weight) parts.push(`Metal Weight: ${spec.avg_metal_weight}`);
    if (spec.sku_metal_weight) parts.push(`SKU Metal Weight: ${spec.sku_metal_weight}`);
    if (spec.length) parts.push(`Length: ${spec.length}`);
    if (spec.weight_per_piece) parts.push(`Weight/Piece: ${spec.weight_per_piece}`);
    if (spec.metal_group) parts.push(`Metal Group: ${spec.metal_group}`);

    const full_description = parts.join(' | ');

    console.log("Setting description to:", full_description);
    frappe.model.set_value(cdt, cdn, 'description', full_description);
    
    if (frm && frm.fields_dict && frm.fields_dict["items"]) {
        frm.fields_dict["items"].grid.refresh_row(cdn);
    }
}

function build_basic_description(frm, cdt, cdn, row) {
    // Fallback when no BOM Item Spec exists
    let parts = [];
    
    if (row.item_name) parts.push(`Item Name: ${row.item_name}`);
    if (row.item_code) parts.push(`Item Code: ${row.item_code}`);
    
    const basic_description = parts.join(' | ');
    
    console.log("Setting basic description to:", basic_description);
    frappe.model.set_value(cdt, cdn, 'description', basic_description);
    
    if (frm && frm.fields_dict && frm.fields_dict["items"]) {
        frm.fields_dict["items"].grid.refresh_row(cdn);
    }
}

// BOM Description from Specs
function update_bom_description_from_specs(frm) {
    if (!frm.doc.name) {
        frappe.msgprint('Please save the BOM first');
        return;
    }

    frappe.show_alert({message: "Updating BOM description from specs...", indicator: "blue"});

    // Get all BOM Item Specs for this BOM
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'BOM Item Spec',
            filters: {'bom': frm.doc.name},
            fields: [
                'name', 'bom_item_id', 'item', 'item_group', 'gemstone', 'stone_treatment', 
                'merch_color_attribute', 'country_of_origin', 'cut', 'stone_pieces', 'setting_type', 
                'grade', 'avg_carat_weight', 'sku_carat_weight', 'metal_group', 'sku_metal_weight', 
                'avg_metal_weight', 'finding_pieces', 'length', 'weight_per_piece', 'length_uom', 'total_weight'
            ]
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                build_bom_description_from_specs(frm, r.message);
            } else {
                // Fallback to BOM Item descriptions if no specs
                build_bom_description_from_items(frm);
            }
        },
        error: function(err) {
            console.error("Error fetching BOM Item Specs:", err);
            frappe.msgprint("Error fetching specifications. Please try again.");
        }
    });
}

function build_bom_description_from_specs(frm, bom_item_specs) {
    let parts = [];
    
    // Add main BOM info
    if (frm.doc.item_name) parts.push(`<b>${frm.doc.item_name}</b>`);
    if (frm.doc.item_group) parts.push(`Item Group: ${frm.doc.item_group}`);
    
    // Group specs by item type for better organization
    let stone_specs = [];
    let metal_specs = [];
    let finding_specs = [];
    let other_specs = [];
    
    bom_item_specs.forEach(function(spec) {
        if (spec.gemstone || spec.stone_pieces || spec.avg_carat_weight) {
            stone_specs.push(spec);
        } else if (spec.metal_group || spec.sku_metal_weight || spec.avg_metal_weight) {
            metal_specs.push(spec);
        } else if (spec.finding_pieces || spec.length || spec.weight_per_piece) {
            finding_specs.push(spec);
        } else {
            other_specs.push(spec);
        }
    });
    
    // Add stone specifications
    if (stone_specs.length > 0) {
        let stone_desc = stone_specs.map(spec => {
            let stone_parts = [];
            if (spec.gemstone) stone_parts.push(spec.gemstone);
            if (spec.cut) stone_parts.push(spec.cut);
            if (spec.merch_color_attribute) stone_parts.push(spec.merch_color_attribute);
            if (spec.grade) stone_parts.push(`Grade ${spec.grade}`);
            if (spec.stone_pieces) stone_parts.push(`${spec.stone_pieces} pcs`);
            if (spec.avg_carat_weight) stone_parts.push(`${spec.avg_carat_weight}ct`);
            if (spec.country_of_origin) stone_parts.push(`(${spec.country_of_origin})`);
            return stone_parts.join(' ');
        }).filter(desc => desc.trim()).join(', ');
        
        if (stone_desc) parts.push(`Stones: ${stone_desc}`);
    }
    
    // Add metal specifications
    if (metal_specs.length > 0) {
        let metal_desc = metal_specs.map(spec => {
            let metal_parts = [];
            if (spec.metal_group) metal_parts.push(spec.metal_group);
            if (spec.avg_metal_weight) metal_parts.push(`${spec.avg_metal_weight}g`);
            else if (spec.sku_metal_weight) metal_parts.push(`${spec.sku_metal_weight}g`);
            return metal_parts.join(' ');
        }).filter(desc => desc.trim()).join(', ');
        
        if (metal_desc) parts.push(`Metal: ${metal_desc}`);
    }
    
    // Add finding/chain specifications
    if (finding_specs.length > 0) {
        let finding_desc = finding_specs.map(spec => {
            let finding_parts = [];
            if (spec.finding_pieces) finding_parts.push(`${spec.finding_pieces} findings`);
            if (spec.length && spec.length_uom) finding_parts.push(`${spec.length}${spec.length_uom}`);
            else if (spec.length) finding_parts.push(`${spec.length} length`);
            if (spec.weight_per_piece) finding_parts.push(`${spec.weight_per_piece}g/pc`);
            if (spec.total_weight) finding_parts.push(`${spec.total_weight}g total`);
            return finding_parts.join(' ');
        }).filter(desc => desc.trim()).join(', ');
        
        if (finding_desc) parts.push(`Findings: ${finding_desc}`);
    }
    
    // Add other specifications
    if (other_specs.length > 0) {
        let other_desc = other_specs.map(spec => {
            return spec.item || 'Component';
        }).filter(desc => desc.trim()).join(', ');
        
        if (other_desc) parts.push(`Components: ${other_desc}`);
    }
    
    // Add setting type if available
    let setting_types = bom_item_specs.map(spec => spec.setting_type).filter(st => st);
    if (setting_types.length > 0) {
        let unique_settings = [...new Set(setting_types)];
        parts.push(`Setting: ${unique_settings.join(', ')}`);
    }
    
    // Add stone treatment if available
    let treatments = bom_item_specs.map(spec => spec.stone_treatment).filter(st => st);
    if (treatments.length > 0) {
        let unique_treatments = [...new Set(treatments)];
        parts.push(`Treatment: ${unique_treatments.join(', ')}`);
    }
    
    // Update BOM description
    let new_description = parts.join(' | ');
    frm.set_value('description', new_description);
    
    frappe.show_alert({
        message: __('BOM description updated from specifications'),
        indicator: 'green'
    });
}

function build_bom_description_from_items(frm) {
    // Fallback: build description from BOM Items when no specs exist
    let parts = [];
    
    if (frm.doc.item_name) parts.push(`<b>${frm.doc.item_name}</b>`);
    if (frm.doc.item_group) parts.push(`Item Group: ${frm.doc.item_group}`);
    
    // Get unique item groups from BOM items
    let item_groups = [];
    let item_names = [];
    
    if (frm.doc.items) {
        frm.doc.items.forEach(function(item) {
            let item_group = item.custom_item_group || item.item_group;
            if (item_group && !item_groups.includes(item_group)) {
                item_groups.push(item_group);
            }
            
            if (item.item_name && !item_names.includes(item.item_name)) {
                item_names.push(item.item_name);
            }
        });
    }
    
    if (item_groups.length > 0) {
        parts.push(`Contains: ${item_groups.join(', ')}`);
    }
    
    if (item_names.length > 0 && item_names.length <= 5) {
        parts.push(`Items: ${item_names.slice(0, 5).join(', ')}`);
    }
    
    let new_description = parts.join(' | ');
    frm.set_value('description', new_description);
    
    frappe.show_alert({
        message: __('BOM description updated from item details'),
        indicator: 'blue'
    });
}

