            ]
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                let updated_count = 0;
                const total_specs = r.message.length;
                
                frappe.show_alert({
                    message: `Updating ${total_specs} BOM Item descriptions...`,
                    indicator: "blue"
                });
                
                r.message.forEach((spec, index) => {
                    setTimeout(() => {
                        const description = build_description_from_spec_data(spec);
                        
                        frappe.call({
                            method: 'frappe.client.set_value',
                            args: {
                                doctype: 'BOM Item',
                                name: spec.bom_item_id,
                                fieldname: 'description',
                                value: description
                            },
                            callback: function() {
                                updated_count++;
                                if (updated_count === total_specs) {
                                    frappe.show_alert({
                                        message: `Updated ${updated_count} BOM Item descriptions from specs`,
                                        indicator: "green"
                                    });
                                    
                                    // Refresh the current form
                                    cur_frm.refresh_field('items');
                                }
                            }
                        });
                    }, index * 100);
                });
            } else {
                frappe.msgprint('No BOM Item Specs found for this BOM');
            }
        }
    });
}

function build_description_from_spec_data(spec) {
