    if (spec.item) parts.push(`Item: ${spec.item}`);
    if (spec.item_group) parts.push(`Item Group: ${spec.item_group}`);
    if (spec.gemstone) parts.push(`Gemstone: ${spec.gemstone}`);
    //if (spec.stone_treatment) parts.push(`Treatment: ${spec.stone_treatment}`);
	// Handle stone_treatments multi-select table
    if (spec.stone_treatments && spec.stone_treatments.length > 0) {
        const treatments = spec.stone_treatments.map(t => t.stone_treatment).filter(t => t).join(', ');
        if (treatments) {
            parts.push(`Stone Treatments: ${treatments}`);
        }
    }
    if (spec.grade) parts.push(`Grade: ${spec.grade}`);
    if (spec.cut) parts.push(`Cut: ${spec.cut}`);
    if (spec.setting_type) parts.push(`Setting Type: ${spec.setting_type}`);
    if (spec.merch_color_attribute) parts.push(`Merch Color: ${spec.merch_color_attribute}`);
    if (spec.stone_pieces) parts.push(`Pieces: ${spec.stone_pieces}`);
    if (spec.country_of_origin) parts.push(`Country: ${spec.country_of_origin}`);
    if (spec.sku_carat_weight) parts.push(`SKU Weight: ${spec.sku_carat_weight}`);
    if (spec.avg_carat_weight) parts.push(`Avg Carat: ${spec.avg_carat_weight}`);
    if (spec.avg_metal_weight) parts.push(`Metal Weight: ${spec.avg_metal_weight}`);
    if (spec.sku_metal_weight) parts.push(`SKU Metal Weight: ${spec.sku_metal_weight}`);
    if (spec.length) parts.push(`Length: ${spec.length}`);
    if (spec.weight_per_piece) parts.push(`Weight/Piece: ${spec.weight_per_piece}`);
    if (spec.metal_group) parts.push(`Metal Group: ${spec.metal_group}`);

    return parts.join(' | ');
}

function create_missing_bom_item_specs(frm) {
    if (!frm.doc.items || frm.doc.items.length === 0) {
        frappe.msgprint('No BOM Items found');
        return;
    }
    
    // Get existing specs
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'BOM Item Spec',
            filters: {'bom': frm.doc.name},
            fields: ['bom_item_id']
        },
        callback: function(r) {
            const existing_specs = r.message ? r.message.map(spec => spec.bom_item_id) : [];
            
            // Find BOM Items without specs
            const items_without_specs = frm.doc.items.filter(item => 
                !existing_specs.includes(item.name)
            );
            
            if (items_without_specs.length === 0) {
                frappe.msgprint('All BOM Items already have specs');
                return;
            }
            
            frappe.show_alert({
                message: `Creating specs for ${items_without_specs.length} items...`,
                indicator: "blue"
            });
            
            let created_count = 0;
            
            items_without_specs.forEach((item, index) => {
                setTimeout(() => {
                    create_spec_for_bom_item(frm.doc.name, item).then(() => {
                        created_count++;
                        if (created_count === items_without_specs.length) {
                            frappe.show_alert({
                                message: `Created ${created_count} BOM Item Specs`,
                                indicator: "green"
                            });
                        }
                    });
                }, index * 200);
            });
        }
    });
}

// REPLACE the create_spec_for_bom_item function with this corrected version:

function create_spec_for_bom_item(bom_name, bom_item) {
    return new Promise((resolve, reject) => {
        
        const spec_doc = {
            doctype: 'BOM Item Spec',
            bom: bom_name,
            bom_item_id: bom_item.name,
            item: bom_item.item_code,
            
            // Map custom fields if they exist
            item_group: bom_item.custom_item_group,
            gemstone: bom_item.custom_gemstone,
            grade: bom_item.custom_grade,
            cut: bom_item.custom_cut,
            setting_type: bom_item.custom_setting_type,
            merch_color_attribute: bom_item.custom_merch_color__attribute,
            stone_pieces: bom_item.custom_stone_pieces,
            country_of_origin: bom_item.custom_country_of_origin,
            avg_carat_weight: bom_item.custom_avg_carat_weight,
            sku_carat_weight: bom_item.custom_sku_weight,
            metal_group: bom_item.custom_metal_group,
            avg_metal_weight: bom_item.custom_metal_weight,
            sku_metal_weight: bom_item.custom_sku_metal_weight,
            length: bom_item.custom_length,
            weight_per_piece: bom_item.custom_weight_perpiece
        };
        
        // Handle stone_treatment conversion: single field  multi-select table
        if (bom_item.custom_stone_treatment) {
            spec_doc.stone_treatments = [
                {
                    stone_treatment: bom_item.custom_stone_treatment
                }
            ];
        }
        
        frappe.call({
            method: 'frappe.client.insert',
            args: {
                doc: spec_doc
            },
            callback: function(insert_r) {
                if (insert_r.message) {
                    console.log(`Created spec: ${insert_r.message.name}`);
                    resolve(insert_r.message);
                } else {
                    reject('Failed to create spec');
                }
            },
            error: function(err) {
                console.error('Error creating spec:', err);
                reject(err);
            }
        });
    });
}

