// ============ CALCULATION FUNCTIONS ============

function calculate_ext_qty_and_weight(frm, cdt, cdn) {
    const row = locals[cdt][cdn];

    const stonePieces = flt(row.custom_stone_pieces);
    const qty = flt(row.qty);
    const metalWeight = flt(row.custom_metal_weight);

    const extQty = stonePieces * qty;
    const skuWeight = metalWeight * 0.8;

    frappe.model.set_value(cdt, cdn, 'custom_ext_qty_required', extQty);
    frappe.model.set_value(cdt, cdn, 'custom_sku_metal_weight', skuWeight);

    calculate_net_balance(frm, cdt, cdn);
}

function calculate_net_balance(frm, cdt, cdn) {
    const row = locals[cdt][cdn];

    const extQty = flt(row.custom_ext_qty_required);
    const onHand = flt(row.custom_oh);
    const netBalance = extQty - onHand;

    frappe.model.set_value(cdt, cdn, 'custom_net_balance_required', netBalance);
}

function calculate_qty_for_stones(frm, cdt, cdn) {
    let row = locals[cdt][cdn];

    let new_qty = 0;

    if (row.custom_item_group === "Stone") {
        if (row.uom === "Carat") {
            new_qty = flt(row.custom_avg_carat_weight);
        } else {
            new_qty = flt(row.custom_stone_pieces);
        }
    } else if (row.custom_item_group === "Pearl") {
        new_qty = flt(row.custom_avg_carat_weight);
    }

    if (new_qty && flt(row.qty) !== new_qty) {
