frappe.ui.form.on('Purchase Order', {
    refresh: function(frm) {
        const steps = [
            { name: 'Purchase Order', condition: frm.doc.docstatus === 1 },
            { name: 'Purchase Receipt', condition: frm.doc.per_received > 0 },
            { name: 'Purchase Invoice', condition: frm.doc.per_billed > 0 },
            { name: 'Payment Entry', condition: frm.doc.status === 'Paid' }
        ];

        let progressHTML = `
            <div style="display: flex; align-items: center; justify-content: space-between; margin: 20px 0; position: relative;">
        `;

        steps.forEach((step, index) => {
            const isActive = step.condition;
            const dotColor = isActive ? '#3498db' : '#ccc';
            const textColor = isActive ? '#000' : '#888';

            progressHTML += `
                <div style="flex: 1; text-align: center; position: relative;">
                    <div style="
                        width: 16px;
                        height: 16px;
                        border-radius: 50%;
                        background-color: ${dotColor};
                        margin: 0 auto;
                        position: relative;
                        z-index: 2;
                    "></div>
                    <div style="margin-top: 8px; font-size: 12px; color: ${textColor}; white-space: nowrap;">${step.name}</div>
                    ${index < steps.length - 1 ? `
                        <div style="
                            position: absolute;
                            top: 8px;
                            left: 50%;
                            width: 100%;
                            height: 2px;
                            background-color: #ccc;
                            z-index: 1;
                        "></div>` : ''}
                </div>
            `;
        });

        progressHTML += '</div>';

        frm.fields_dict.custom_progress_status.$wrapper.html(progressHTML);
