            frm.refresh_field('customer_bom_items');
            return;
        }
    
        frm.clear_table('customer_bom_items');
    
        
        const uniqueItemCodes = [...new Set((frm.doc.customer_worksheet_items || []).map(row => row.item_code))];
    
        const bomFetchPromises = uniqueItemCodes.map(item_code => {
            return frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'BOM',
                    filters: {
                        item: item_code,
                        is_active: 1,
                        is_default: 1,
                        // docstatus: 1
                    },
                    limit_page_length: 1,
                    order_by: 'creation desc'
                }
            });
        });
    
        Promise.all(bomFetchPromises).then(results => {
            const bomCalls = [];
    
            results.forEach(bomList => {
                if (bomList.message && bomList.message.length > 0) {
                    const bomName = bomList.message[0].name;
