                }
            });
    
            Promise.all(bomCalls).then(bomResults => {
                const addedItems = new Set(); 
    
                bomResults.forEach(res => {
                    if (res.message && res.message.items) {
                        res.message.items.forEach(item => {
                            const uniqueKey = `${res.message.name}::${item.item_code}`;
                            if (addedItems.has(uniqueKey)) return;
    
                            let row = frm.add_child('customer_bom_items');
                            row.item_code = item.item_code;
                            row.item_name = item.item_name;
                            row.custom_item_group = item.custom_item_group;
                            row.bom_no = res.message.name;
                            row.custom_our_responsibility = item.custom_our_responsibility;
                            row.qty = item.qty;
                            row.uom = item.uom;
                            row.rate = item.rate;
                            row.amount = item.amount || 0;
                            row.custom_selling_rate = item.custom_selling_rate || 0;
                            row.custom_selling_amount = item.custom_selling_amount || 0;
                            row.custom_gemstone = item.custom_gemstone;
                            row.custom_stone_treatment = item.custom_stone_treatment;
                            row.custom_merch_color__attribute = item.custom_merch_color__attribute;
                            row.custom_country_of_origin = item.custom_country_of_origin;
                            row.custom_avg_carat_weight = item.custom_avg_carat_weight;
                            row.custom_sku_weight = item.custom_sku_weight;
                            row.custom_grade = item.custom_grade;
                            row.custom_cut = item.custom_cut;
                            row.custom_setting_type = item.custom_setting_type;
                            row.custom_stone_pieces = item.custom_stone_pieces;
                            row.custom_metal_weight = item.custom_metal_weight;
                            row.custom_sku_metal_weight = item.custom_sku_metal_weight;
                            row.custom_metal_group = item.custom_metal_group;
                            row.description = item.description;
    
                            addedItems.add(uniqueKey);
                        });
                    }
                });
    
                frm.refresh_field('customer_bom_items');
            });
        });
    }
});

