function calculate_qty_and_all_items(frm, cdt, cdn) {
    calculate_qty_items(frm, cdt, cdn);
    calculate_all_items(frm, cdt, cdn);
}

function calculate_qty_items(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    let new_qty = 0;

    const item_group = (row.custom_item_group || "").trim();
    const uom = (row.uom || "").toLowerCase();

    if (item_group === "Stone") {
        if (uom === "carat") {
            new_qty = flt(row.custom_avg_carat_weight);
        } else {
            new_qty = flt(row.custom_stone_pieces);
        }
    } else if (item_group === "Pearl") {
        new_qty = flt(row.custom_avg_carat_weight);
    } else if (item_group === "Metal") {
        new_qty = flt(row.custom_metal_weight);
    }

    if (flt(row.qty) !== flt(new_qty)) {
        frappe.model.set_value(cdt, cdn, 'qty', new_qty);
    }
}

function calculate_all_items(frm, cdt, cdn) {
    const row = locals[cdt][cdn];

    const qty = flt(row.qty);
    const pieces = flt(row.custom_stone_pieces);
    const weight = flt(row.custom_metal_weight);
    const rate = flt(row.rate);
    const sellingRate = flt(row.custom_selling_rate);
    const sellingAmount = sellingRate * qty;

    const onHand = flt(row.custom_oh);
    const avgCarat = flt(row.custom_avg_carat_weight);
    const uom = (row.uom || "").toLowerCase();

    const extQty = pieces * qty;
    const skuWeight = weight * 0.8;
    const netBalance = extQty - onHand;

    const amount = rate * qty;

    if (flt(row.custom_ext_qty_required) !== extQty)
        frappe.model.set_value(cdt, cdn, 'custom_ext_qty_required', extQty);

    if (flt(row.custom_sku_metal_weight) !== skuWeight)
        frappe.model.set_value(cdt, cdn, 'custom_sku_metal_weight', skuWeight);

    if (flt(row.custom_net_balance_required) !== netBalance)
        frappe.model.set_value(cdt, cdn, 'custom_net_balance_required', netBalance);

    if (flt(row.amount) !== amount)
        frappe.model.set_value(cdt, cdn, 'amount', amount);

    if (flt(row.custom_selling_amount) !== sellingAmount)
        frappe.model.set_value(cdt, cdn, 'custom_selling_amount', sellingAmount);
}




function calculate_net_balance_items(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    const extQty = flt(row.custom_ext_qty_required);
    const onHand = flt(row.custom_oh);
    const netBalance = extQty - onHand;

    frappe.model.set_value(cdt, cdn, 'custom_net_balance_required', netBalance);
}

function update_country_of_origin_options_items(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    const grid = frm.fields_dict["customer_bom_items"].grid;

    if (!row.custom_gemstone) {
        frappe.meta.get_docfield("Customer Worksheet BOM Items", "custom_country_of_origin").get_query = () => ({});
        frappe.model.set_value(cdt, cdn, "custom_country_of_origin", null);
        grid.refresh();
        return;
    }

    frappe.call({
        method: "get_country_of_origin",
        args: {
            gemstone: row.custom_gemstone
        },
        callback: function (r) {
            if (r.data) {
                const countries = [...new Set(r.data.map(entry => entry[1]))];
                grid.update_docfield_property("custom_country_of_origin", "options", countries);
            }
        }
    });
}

