function update_description(frm) {
    let parts = [];
    let std_desc = frm.doc.description || "";
    let lastPipeIndex = std_desc.lastIndexOf('|');
    let additional_desc;

    if (lastPipeIndex !== -1) {
        additional_desc = std_desc.slice(lastPipeIndex + 1).trim();
    } else {
        additional_desc = std_desc.trim();
    }

    if (frm.doc.item_name) parts.push(`<b>${frm.doc.item_name}</b>`);
    if (frm.doc.item_group) parts.push(`Item Group: ${frm.doc.item_group}`);
    if (frm.doc.gst_hsn_code) parts.push(`HSN/SAC: ${frm.doc.gst_hsn_code}`);
    if (frm.doc.brand) parts.push(`Brand: ${frm.doc.brand}`);
    if (frm.doc.custom_customer_sku) parts.push(`Customer SKU: ${frm.doc.custom_customer_sku}`);
    if (frm.doc.stock_uom) parts.push(`Stock UOM: ${frm.doc.default_stock_uom}`);
    if (frm.doc.custom_vendor_product_id) parts.push(`Vendor Product ID: ${frm.doc.custom_vendor_product_id}`);

    if (frm.doc.custom_gemstone) parts.push(`Gemstone: ${frm.doc.custom_gemstone}`);
    if (frm.doc.custom_shape) parts.push(`Shape: ${frm.doc.custom_shape}`);
    if (frm.doc.custom_composition) parts.push(`Composition: ${frm.doc.custom_composition}`);
    if (frm.doc.custom_form) parts.push(`Form: ${frm.doc.custom_form}`);
    if (frm.doc.custom_dimensions) parts.push(`Dimensions: ${frm.doc.custom_dimensions}`);
    if (frm.doc.custom_color) parts.push(`Color: ${frm.doc.custom_color}`);

    frm.set_value("description", parts.join(" | "));
    frm.refresh_field("description");
}




