    fetch_item_with_bom: function(frm) {
        const raw_items = (frm.doc.customer_worksheet_items || []).filter(row => !row.default_bom);
    
        const unique_items_map = {};
        const items_without_bom = [];
    
        raw_items.forEach(row => {
            if (!unique_items_map[row.item_code]) {
                unique_items_map[row.item_code] = true;
                items_without_bom.push(row);
            }
        });
    
        if (items_without_bom.length === 0) {
            frappe.msgprint("All items already have a BOM set in 'Default BOM'.");
            return;
        }
    
        if (items_without_bom.length === 1) {
            frm.set_value("finished_goods_item", items_without_bom[0].item_code);
            return;
        }
    
        const fields = items_without_bom.map((item, i) => ({
            fieldname: `item_${i}`,
            label: `${item.item_code} : ${item.item_name}`,
            fieldtype: 'Check',
            default: 0
        }));
    
        const dialog = new frappe.ui.Dialog({
            title: "Select One Item (No BOM)",
            fields: fields,
            primary_action_label: "Insert to Finished Goods",
            primary_action(values) {
                const selected = items_without_bom.filter((item, i) => values[`item_${i}`]);
    
                if (selected.length > 0) {
                    frm.set_value("finished_goods_item", selected[0].item_code);
                } else {
                    frappe.msgprint("No item selected.");
                }
    
                dialog.hide();
            }
        });
    
        dialog.show();
    
        setTimeout(() => {
            items_without_bom.forEach((item, i) => {
                const $field = dialog.fields_dict[`item_${i}`].$wrapper.find('input[type="checkbox"]');
                $field.on('change', function () {
                    if ($(this).is(':checked')) {
                        items_without_bom.forEach((_, j) => {
                            if (j !== i) {
                                dialog.fields_dict[`item_${j}`].set_value(0);
                            }
                        });
                    }
                });
            });
        }, 100);
    }

,

