function handle_sales_order_creation(frm) {
    const all_items = frm.doc.customer_worksheet_items || [];
    const bom_items = frm.doc.custom_bom_items || [];

    const items = all_items.filter(i => !i.sales_order);

    if (!items.length) {
        frappe.msgprint('All items already have Sales Orders.');
        return;
    }

    const dialog = new frappe.ui.Dialog({
        title: 'Select Items to Create Sales Orders',
        size: 'large',
        fields: [{ fieldtype: 'HTML', fieldname: 'items_html' }],
        primary_action_label: 'Create',
        primary_action: () => process_sales_order_dialog(frm, dialog, items, bom_items)
    });

    dialog.fields_dict.items_html.$wrapper.html(render_items_table(items));
    dialog.show();
}

function render_items_table(items) {
    const grouped = {};
    items.forEach(item => {
        if (!grouped[item.item_code]) {
            grouped[item.item_code] = {
                ...item,
                count: 1,
                total_qty: parseFloat(item.qty) || 0
            };
        } else {
            grouped[item.item_code].count += 1;
            grouped[item.item_code].total_qty += parseFloat(item.qty) || 0;
        }
    });

    const unique_items = Object.values(grouped);

    return `
        <div style="max-height: 300px; overflow-y: auto;">
            <table class="table table-bordered">
                <thead>
                    <tr>
                        <th></th><th>Item Code</th><th>Item Name</th>
                        <th>Qty</th><th>Item Group</th><th>Order Type</th><th>Rows/Size</th>
                    </tr>
                </thead>
                <tbody>
                    ${unique_items.map((item, i) => `
                        <tr>
                            <td><input type="checkbox" class="so-item-checkbox" data-code="${item.item_code}" checked></td>
                            <td>${item.item_code}</td>
                            <td>${item.item_name}</td>
                            <td>${item.total_qty}</td>
                            <td>${item.item_group || ''}</td>
                            <td>${item.order_type || ''}</td>
                            <td>${item.count}</td>
                        </tr>
                    `).join('')}
                </tbody>
            </table>
        </div>`;
}

function process_sales_order_dialog(frm, dialog, all_items, bom_items) {
    const selected_codes = [];
    dialog.fields_dict.items_html.$wrapper.find('.so-item-checkbox:checked').each(function () {
        const code = $(this).data('code');
        selected_codes.push(code);
    });

    if (!selected_codes.length) {
        return frappe.msgprint('Please select at least one item.');
    }

    const selected_items = all_items.filter(item => selected_codes.includes(item.item_code));

    for (const item of selected_items) {
        if (!item.order_type) {
            return frappe.msgprint(`Order Type is missing for item: ${item.item_code}`);
        }
    }

    dialog.hide();

    const sales_order_names = [];

    const sized_items = selected_items.filter(i => i.size);
    const unsized_items = selected_items.filter(i => !i.size);

    let i = 0;

    const next_unsized = () => {
        if (i < unsized_items.length) {
            create_sales_order(frm, [unsized_items[i++]], bom_items, sales_order_names, next_unsized);
        } else {
            show_sales_orders(sales_order_names);
        }
    };

    if (sized_items.length) {
        create_sales_order(frm, sized_items, bom_items, sales_order_names, next_unsized);
    } else {
        next_unsized();
    }
}


async function create_sales_order(frm, items_list, bom_items, sales_order_names, callback) {
    const company = await frappe.db.get_value("Company", frm.doc.company, "abbr");
    const abbr = company.message.abbr;
    const warehouse = `Stores - ${abbr}`;

    let groupedItems = {};
    for (const item of items_list) {
        if (!groupedItems[item.item_code]) {
            groupedItems[item.item_code] = [];
        }
        groupedItems[item.item_code].push(item);
    }

    for (const [item_code, grouped_list] of Object.entries(groupedItems)) {
        const so_doc = {
            doctype: "Sales Order",
            customer: frm.doc.customer,
            custom_department: frm.doc.department,
            factory_vendor: frm.doc.custom_factory_vendor,
            company: frm.doc.company,
            transaction_date: frappe.datetime.nowdate(),
            custom_sales_order_type: grouped_list[0].order_type,
            custom_factory_vendor: frm.doc.factory_vendor,
            custom_stone_vendor: frm.doc.stone_vendor,
            custom_other_vendor: frm.doc.other_vendor,
            items: [],
            custom_bom_items: []
        };

        const bomItemPromises = [];
        let is_first_item = true;

        for (const item of grouped_list) {
            const matching_bom = bom_items.find(b =>
                b.item_code === item.item_code &&
                (!item.size || b.custom_size === item.size)
            );

            so_doc.items.push({
                item_code: item.item_code,
                item_name: item.item_name,
                uom: item.uom,
                custom_size: item.size,
                bom_no: item.default_bom,
                custom_customer_worksheet_no: frm.doc.name,
                rate: item.rate,
                delivery_date: item.eta,
                image_view: item.image_view,
                custom_product_image: item.image_view,
                custom_supporting_documents: frm.doc.supporting_documents,
                qty: matching_bom ? matching_bom.qty : item.qty,
                warehouse: warehouse,
                bom: matching_bom ? matching_bom.bom_no : item.default_bom,
                custom_item_bom: item.default_bom || (matching_bom ? matching_bom.bom_no : null),
                custom_our_responsibility: matching_bom ? matching_bom.our_responsibility : null
            });

            if (is_first_item && item.default_bom) {
                so_doc.custom_item_bom = item.default_bom;

                const bomPromise = frappe.db.get_doc('BOM', item.default_bom).then(doc => {
                    doc.items.forEach(bomItem => {
                        so_doc.custom_bom_items.push({
                            item_code: bomItem.item_code,
                            item_name: bomItem.item_name,
                            qty: bomItem.qty || 1,
                            uom: bomItem.uom,
                            rate: bomItem.rate || 0,
                            amount: bomItem.amount || 0,
                            description: bomItem.description,
                            custom_item_group: bomItem.custom_item_group || '',
                            custom_selling_amount: bomItem.custom_selling_amount || '',
                            custom_gemstone: bomItem.custom_gemstone,
                            custom_stone_treatment: bomItem.custom_stone_treatment,
                            custom_merch_color__attribute: bomItem.custom_merch_color__attribute,
                            custom_country_of_origin: bomItem.custom_country_of_origin,
                            custom_grade: bomItem.custom_grade,
                            custom_stone_pieces: bomItem.custom_stone_pieces,
                            custom_avg_carat_weight: bomItem.custom_avg_carat_weight,
                            custom_sku_carat_weight: bomItem.custom_sku_carat_weight,
                            custom_cut: bomItem.custom_cut,
                            custom_setting_type: bomItem.custom_setting_type,
                            custom_metal_weight: bomItem.custom_metal_weight,
                            custom_sku_metal_weight: bomItem.custom_sku_metal_weight,
                            custom_metal_group: bomItem.custom_metal_group,
                            custom_selling_rate: bomItem.custom_selling_rate,
                            custom_sku_weight: bomItem.custom_sku_weight,
                            custom_ext_qty_required: bomItem.custom_ext_qty_required,
                            custom_oh: bomItem.custom_oh,
                            custom_net_balance_required: bomItem.custom_net_balance_required,
                            custom_our_responsibility: bomItem.custom_our_responsibility  
                        });
                    });
                });

                bomItemPromises.push(bomPromise);
                is_first_item = false;
            }
        }

        await Promise.all(bomItemPromises);

        await frappe.call({
            method: "frappe.client.insert",
            args: { doc: so_doc },
            callback: r => {
                if (r.message) {
                    const sales_order_name = r.message.name;
                    sales_order_names.push(sales_order_name);

                    if (frm.doc.supporting_documents) {
                        frappe.call({
                            method: "frappe.client.insert",
                            args: {
                                doc: {
                                    doctype: "File",
                                    file_url: frm.doc.supporting_documents,
                                    attached_to_doctype: "Sales Order",
                                    attached_to_name: sales_order_name
                                }
                            }
                        });
                    }

                    grouped_list.forEach(item => {
                        const matching_rows = frm.doc.customer_worksheet_items.filter(i =>
                            i.item_code === item.item_code &&
                            (i.size === item.size || !item.size) &&
                            i.qty === item.qty
                        );
                        matching_rows.forEach(row => {
                            row.sales_order_no = sales_order_name;
                        });
                    });

                    frm.refresh_field('customer_worksheet_items');
                    toggle_create_so_button(frm);
                }
            }
        });
    }

    callback(); 
}


