        // Only show for submitted POs
        if (frm.doc.docstatus === 1) {
            // Hide the standard inter-company button from the Create dropdown
            setTimeout(function() {
                // This is a more reliable way to find and hide dropdown items
                $('.dropdown-menu .dropdown-item:contains("Inter Company Sales Order")').hide();
                
                // Also try to hide by data attribute if the above doesn't work
                $('.dropdown-menu [data-label="Inter Company Sales Order"]').parent().hide();
                
                // Hide any similar items like "Internal Sales Order" too
                $('.dropdown-menu .dropdown-item:contains("Internal Sales Order")').hide();
            }, 500); // Give more time for the dropdown menu to be rendered
            
            // Check if inter-company sales order doesn't exist or was cancelled
            frappe.db.get_value('Sales Order', {
                'inter_company_order_reference': frm.doc.name
            }, ['name', 'docstatus'], function(r) {
                // If no SO exists or the SO was cancelled (docstatus=2), show the button
                if (!r || !r.name || r.docstatus === 2) {
                    // Only show button for group company suppliers
                    if (is_group_company_supplier(frm)) {
                        // Add button directly to the Create dropdown
                        frm.add_custom_button(__('Create Inter-Company Sales Order'), function() {
                            create_multi_currency_inter_company_so(frm);
                        }, __('Create'));
                    }
                } else if (r && r.name) {
                    // Add button to view the existing SO
                    frm.add_custom_button(__('View Inter-Company Sales Order'), function() {
                        frappe.set_route('Form', 'Sales Order', r.name);
                    }, __('View'));
                }
            });
        }
    }
});

/**
 * Check if supplier is a group company where inter-company transaction should be created
 * @param {Object} frm - Form object
 * @returns {Boolean} - True if this is a group company supplier
 */
function is_group_company_supplier(frm) {
    // Define your group companies here
    const group_companies = ["MCGI", "D&K", "BBJ", "D&K Global"];
    
    // Check supplier name
    if (group_companies.includes(frm.doc.supplier)) {
        return true;
    }
    
    // Check supplier group if defined
    if (frm.doc.supplier_group === "Group Companies") {
        return true;
    }
    
    // Check if this is a stone PO which needs an inter-company transaction
    if (frm.doc.custom_is_stone_po === 1 || 
        (frm.doc.custom_purchase_order_type && 
         frm.doc.custom_purchase_order_type === "Stone")) {
        return true;
    }
    
    return false;
}

/**
 * Create multi-currency inter-company sales order
 * @param {Object} frm - Form object
 */
function create_multi_currency_inter_company_so(frm) {
    frappe.confirm(
        __('Do you want to create an Inter-Company Sales Order for {0}?', [frm.doc.name]),
        function() {
            frappe.call({
                method: 'create_multi_currency_intercompany_sales_order',  // Match your server script API method name
                args: {
                    'purchase_order_name': frm.doc.name
                },
                freeze: true,
                freeze_message: __("Creating Sales Order..."),
                callback: function(r) {
                    console.log("API Response:", r);
                    
                    // Check for explicit error
                    if (r.exc) {
                        frappe.msgprint({
                            title: __('Error'),
                            indicator: 'red',
                            message: __('Error creating Sales Order: {0}', [r.exc])
                        });
                        return;
                    }
                    
                    // Handle various response formats
                    let sales_order_name = '';
                    if (r.message) {
                        if (typeof r.message === 'string') {
                            sales_order_name = r.message;
                        } else if (r.message.sales_order) {
                            sales_order_name = r.message.sales_order;
                        }
                    }
                    
                    if (sales_order_name) {
                        frappe.show_alert({
                            message: __('Sales Order {0} created', [sales_order_name]),
                            indicator: 'green'
                        }, 5);
                        
                        // Ask if user wants to view the created SO
                        frappe.confirm(
                            __('Sales Order {0} has been created. Do you want to view it?', [sales_order_name]),
                            function() {
                                frappe.set_route('Form', 'Sales Order', sales_order_name);
                            },
                            function() {
                                frm.reload_doc();
                            }
                        );
                    } else {
                        frappe.show_alert({
                            message: __('Sales Order created successfully'),
                            indicator: 'green'
                        }, 5);
                        frm.reload_doc();
                    }
                },
                error: function(err) {
                    frappe.msgprint({
                        title: __('Error'),
                        indicator: 'red',
                        message: __('Error in API call: {0}', [err.statusText || "Unknown error"])
                    });
                }
            });
        }
    );
}

