function create_quickbase_trigger_direct(frm) {
    // Prepare the payload for Quickbase with hardcoded values
    const payload = {
        "to": "bu3naihjq",  // The Quickbase trigger table ID
        "data": [
            {
                "6": {  // Field ID for Sales Order
                    "value": "3"
                },
                "14": {  // Field ID for Purchase Order
                    "value": "3.0"
                },
                "7": {  // Field ID for Date
                    "value": "[[\"name\", \"=\", \"" + frm.doc.name +"\"], [\"company\", \"=\", \"MCGI\"]]"
                }
            }
        ]
    };
    
    // Show a message that we're creating a trigger
    frappe.show_alert({
        message: __("Creating Quickbase trigger..."),
        indicator: 'blue'
    }, 3);
    
    // Make the direct API call
    $.ajax({
        url: "https://api.quickbase.com/v1/records",
        type: "POST",
        contentType: "application/json",
        headers: {
            "QB-Realm-Hostname": "jewelry.quickbase.com",
            "Authorization": "QB-USER-TOKEN b75cz2_tv9_0_bdw4b63bksngicch3ftaxvdi5gg"
        },
        data: JSON.stringify(payload),
        success: function(response) {
            frappe.show_alert({
                message: __("Quickbase trigger created successfully"),
                indicator: 'green'
            }, 5);
            
            console.log("Quickbase response:", response);
        },
        error: function(xhr, status, error) {
            frappe.show_alert({
                message: __("Failed to create Quickbase trigger: " + (xhr.responseJSON?.message || error)),
                indicator: 'red'
            }, 5);
            
            console.error("Quickbase error:", xhr.responseText);
        }
    });
}

