function open_split_po_dialog(frm) {
    const supplier = frm.doc.supplier;

    const items_data = frm.doc.items.map(item => ({
        item_code: item.item_code || '',
        qty: item.qty || 0,
        selected_qty: 0,
        balance_qty: item.qty || 0
    }));

    const dialog = new frappe.ui.Dialog({
        title: 'Select Items for PO',
        size: 'large',
        fields: [
            {
                label: 'Supplier',
                fieldname: 'supplier',
                fieldtype: 'Link',
                options: 'Supplier',
                reqd: 1,
                default: supplier
                // read_only: 1
            },
            {
                label: 'Items',
                fieldname: 'items_table',
                fieldtype: 'Table',
                cannot_add_rows: true,
                in_place_edit: true,
                fields: [
                    {
                        label: 'Item Code',
                        fieldname: 'item_code',
                        fieldtype: 'Data',
                        read_only: 1,
                        in_list_view: 1
                    },
                    {
                        label: 'Total Qty',
                        fieldname: 'qty',
                        fieldtype: 'Float',
                        read_only: 1,
                        in_list_view: 1
                    },
                    {
                        label: 'Selected Qty',
                        fieldname: 'selected_qty',
                        fieldtype: 'Float',
                        in_list_view: 1
                    },
                    {
                        label: 'Balance Qty',
                        fieldname: 'balance_qty',
                        fieldtype: 'Float',
                        read_only: 1,
                        in_list_view: 1
                    }
                ]
            },
    {
        fieldtype: 'HTML',
        options: '<div style="margin: 10px 0; font-weight: bold;text-align:center;">Select items to split by entering quantities in the <u>Selected Qty</u> fields.</div>'
    }
        ],
        primary_action_label: 'Proceed',
        primary_action(values) {
            const selected_items = values.items_table.filter(row => row.selected_qty > 0);

            if (!selected_items.length) {
                frappe.msgprint(__('Please enter selected quantity for at least one item.'));
                return;
            }
            selected_items.forEach(dialog_item => {
                const original_row = frm.doc.items.find(item => item.item_code === dialog_item.item_code);
                if (original_row) {
                    original_row.qty = dialog_item.balance_qty;
                }
            });            
            frm.dirty();
            frm.save();
            frm.refresh_field('items');
            
            frappe.call({
            method: 'frappe.client.insert',
            args: {
                doc: {
                    doctype: 'Purchase Order',
                    supplier: values.supplier,
                    schedule_date: frm.doc.schedule_date,
                    transaction_date: frappe.datetime.get_today(),
                    set_warehouse: frm.doc.set_warehouse,
                    items: selected_items.map(item => {
                        const original_item = frm.doc.items.find(orig => orig.item_code === item.item_code);
                        return {
                            item_code: item.item_code,
                            qty: item.selected_qty,
                            schedule_date: frm.doc.schedule_date,
                            po_reference: frm.doc.name,
                            sales_order: original_item?.sales_order || frm.doc.sales_order,
                            uom: original_item?.uom,
                            rate: original_item?.rate,
                            stock_uom: original_item?.stock_uom,
                            description: original_item?.description
                        };
                    }),
                }
            },
            callback: (r) => {
                if (!r.exc) {
                    const message = `New Purchase Order <a href="/app/purchase-order/${r.message.name}">${r.message.name}</a> created.`;
            
                    const custom_dialog = new frappe.ui.Dialog({
                        title: "Success",
                        fields: [
                            {
                                fieldtype: "HTML",
                                options: `<div style="padding: 10px;">${message}</div>`
                            }
                        ],
                        primary_action_label: "Close",
                        primary_action() {
                            custom_dialog.hide();
                        }
                    });
            
                    custom_dialog.show();
            
                    setTimeout(() => {
                        custom_dialog.hide();
                    }, 30000);
            
                    dialog.hide();
                }
            }

        });

        }
    });

    dialog.show();

    dialog.fields_dict.items_table.df.data = items_data;
    dialog.fields_dict.items_table.grid.refresh();
    
    const grid = dialog.fields_dict['items_table'].grid;

    grid.wrapper.on('change', 'input[data-fieldname="selected_qty"]', function (e) {
    const row_idx = $(this).closest('.grid-row').data('idx') - 1;
    const row = dialog.fields_dict.items_table.df.data[row_idx];

    const total_qty = parseFloat(row.qty) || 0;
    const raw_val = $(this).val().replace(/,/g, ''); 
    const selected_qty = parseFloat(raw_val) || 0;

    if (selected_qty > total_qty) {
        frappe.msgprint(__('Selected Qty cannot be greater than Total Qty.'));
        row.selected_qty = 0;
        row.balance_qty = total_qty;
        dialog.fields_dict.items_table.grid.refresh();
        return;
    }

    row.balance_qty = total_qty - selected_qty;
    dialog.fields_dict.items_table.grid.refresh();
});

}

