refresh: function (frm) {
    set_default_boms(frm);
    frm.__created_items = [];
    frm.__inserted_items = [];
    frm.fields_dict.item_status.$wrapper.html("");

    frm.fields_dict.create_insert.$wrapper.find('button').on('click', async function () {
        update_description(frm);

        const item_group = frm.doc.item_group;
        if (!item_group) {
            frappe.msgprint("Item Group is missing.");
            return;
        }

        const getShortCode = (name) => {
            const words = name.trim().split(/\s+/);
            if (words.length === 1) return name.slice(0, 3).toUpperCase();
            return words.map(w => w[0]).join('').toUpperCase().slice(0, 4);
        };

        const item_group_prefix = getShortCode(item_group);

        let item_code_response = await frappe.call({
            method: "frappe.client.get_list",
            args: {
                doctype: "Item",
                filters: [["item_code", "like", item_group_prefix + "-%"]],
                fields: ["name"],
                limit_page_length: 1000
            }
        });

        let existing_codes = item_code_response.message.map(d => d.name);
        let max_number = 0;
        existing_codes.forEach(code => {
            let parts = code.split("-");
            if (parts.length === 2 && parts[0] === item_group_prefix) {
                let num = parseInt(parts[1]);
                if (!isNaN(num) && num > max_number) max_number = num;
            }
        });

        let next_number = max_number + 1;
        const new_item_code = `${item_group_prefix}-${String(next_number).padStart(4, '0')}`;

        const r = await frappe.call({
            method: "frappe.client.insert",
            args: {
                doc: {
                    doctype: "Item",
                    item_code: new_item_code,
                    item_name: frm.doc.item_name || "",
                    gst_hsn_code: frm.doc.gst_hsn_code,
                    item_group: item_group,
                    brand: frm.doc.brand,
                    description: frm.doc.description,
                    custom_customer_sku: frm.doc.custom_customer_sku,
                    stock_uom: frm.doc.default_stock_uom,
                    custom_vendor_product_id: frm.doc.custom_vendor_product_id,
                    custom_gemstone: frm.doc.custom_gemstone,
                    custom_sub_type: frm.doc.custom_sub_type,
                    custom_shape: frm.doc.custom_shape,
                    custom_form: frm.doc.custom_form,
                    custom_metal_group: frm.doc.custom_metal_group,
                    custom_composition: frm.doc.custom_composition,
                    custom_dimensions: frm.doc.custom_dimensions,
                    custom_finding_group: frm.doc.custom_finding_group,
                    custom_finding_type: frm.doc.custom_finding_type,
                    custom_plating_group: frm.doc.custom_plating_group,
                    custom_plating_thickness: frm.doc.custom_plating_thickness,
                    custom_metal_purity_plating: frm.doc.custom_metal_purity_plating,
                    custom_thickness_unit_of_measure: frm.doc.custom_thickness_unit_of_measure
                }
            }
        });

        if (r.message) {
            const created_code = r.message.name;

            const d = new frappe.ui.Dialog({
                title: 'Item Created',
                fields: [{
                    fieldtype: 'HTML',
                    options: `Item <b><a href="/app/item/${created_code}" target="_blank">${created_code}</a></b> created and inserted successfully.`
                }],
                primary_action_label: 'Next',
                primary_action() {
                    d.hide();

                    const imageDialog = new frappe.ui.Dialog({
                        title: 'Attach Image to Item',
                        fields: [{
                            label: 'Item Image',
                            fieldname: 'item_image',
                            fieldtype: 'Attach Image',
                            reqd: true
                        }],
                        primary_action_label: 'Upload',
                        primary_action: async function () {
                            const values = imageDialog.get_values();
                            if (!values || !values.item_image) {
                                frappe.msgprint({
                                    title: __('Warning'),
                                    message: __('Please attach an image before continuing.'),
                                    indicator: 'orange'
                                });
                                return;
                            }

                            await frappe.call({
                                method: "frappe.client.set_value",
                                args: {
                                    doctype: "Item",
                                    name: created_code,
                                    fieldname: "image",
                                    value: values.item_image
                                },
                                callback: function () {
                                    imageDialog.hide();
                                }
                            });
                        }
                    });

                    imageDialog.show();
                }
            });

            d.show();

            frappe.db.get_value('Item', created_code, 'description').then(r => {
                const description = r.message.description || "";
                frm.add_child("customer_worksheet_items", {
                    item_code: created_code,
                    item_name: created_code,
                    item_group: item_group,
                    description: description
                });
                frm.refresh_field("customer_worksheet_items");
            });

            if (!frm.__created_items) frm.__created_items = [];
            frm.__created_items.push(created_code);

            const itemCreatorFields = [
                "item_group", "item_name", "item_code_id", "gst_hsn_code", "brand", "stock_uom",
                "custom_vendor_product_id", "custom_gemstone", "custom_shape", "custom_form",
