function update_item_status(frm) {
    const created = frm.__created_items || [];
    const inserted = frm.__inserted_items || [];

    const lastCreated = created.at(-1);
    const insertCount = inserted.length;

    let html = `<div style="padding: 8px 0; line-height: 1.6;">`;

    if (lastCreated) {
        html += `<span style="color: green;">Item Created:</span> ${frappe.utils.escape_html(lastCreated)}<br>`;
    }

    if (insertCount > 0) {
        html += `<span style="color: blue;">Items Inserted:</span> ${insertCount}`;
    }

    html += `</div>`;

    frm.fields_dict.item_status.$wrapper.html(html);
}



