function get_default_bom(item_code) {
    return frappe.call({
        method: "frappe.client.get_value",
        args: {
            doctype: "BOM",
            filters: {
                item: item_code,
                docstatus: ["in", [0, 1]],
                is_default: 1
            },
            fieldname: ["name", "item"]
        }
    }).then(res => res.message);
}

function set_default_boms(frm) {
    const rows = frm.doc.customer_worksheet_items || [];

    const promises = rows.map(row => {
        if (row.item_code && !row.default_bom) {
            return get_default_bom(row.item_code).then(bom => {
                if (bom) {
                    row.default_bom = bom.name;
                } else {
                    row.default_bom = '';
                }
            });
        }
    });

    Promise.all(promises).then(() => {
        frm.refresh_field('customer_worksheet_items');
    });
}


async function bind_search_logic(frm) {
    let {
        item_group,
        item_name,
        item_code,
        custom_vendor_product_id,
        custom_customer_sku
    } = frm.doc;

    if (!item_group && !item_name && !item_code && !custom_vendor_product_id && !custom_customer_sku) {
        frappe.msgprint(__('Please fill at least one of the search fields.'));
        return;
    }

    let filters = {};
    if (item_group) filters["item_group"] = item_group;
    if (item_name) filters["item_name"] = ["like", `%${item_name}%`];
    if (item_code) filters["name"] = ["like", `%${item_code}%`];
    if (custom_vendor_product_id) filters["custom_vendor_product_id"] = ["like", `%${custom_vendor_product_id}%`];
    if (custom_customer_sku) filters["custom_customer_sku"] = ["like", `%${custom_customer_sku}%`];

    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Item",
            filters: filters,
            fields: ["name", "item_name", "item_group", "description", "stock_uom"]
        },
        callback: function (r) {
            if (r.message && r.message.length > 0) {
                frm.__matched_items = r.message;

                let rows = r.message.map((item, index) => `
                    <tr data-index="${index}">
                        <td><input type="checkbox" class="item-select" data-index="${index}"></td>
                        <td>${frappe.utils.escape_html(item.name)}</td>
                        <td>${frappe.utils.escape_html(item.item_name)}</td>
                        <td>${frappe.utils.escape_html(item.item_group)}</td>
                        <td>
                            <button class="btn btn-sm toggle-desc" data-index="${index}" style="background:none; border:none;">
                                
                            </button>
                        </td>
                    </tr>
                    <tr class="desc-row" id="desc-${index}" style="display:none; background:#f9f9f9;">
                        <td colspan="5">
                            <b>Description:</b><br>
                            ${frappe.utils.escape_html(item.description || 'No description')}
                        </td>
                    </tr>
                `).join('');

                let html = `
                    <div style="max-height: 300px; overflow-y: auto;">
                        <table class="table table-bordered">
                            <thead>
                                <tr>
                                    <th>Select</th>
                                    <th>Item Code</th>
                                    <th>Item Name</th>
                                    <th>Item Group</th>
                                    <th>Details</th>
                                </tr>
                            </thead>
                            <tbody>${rows}</tbody>
                        </table>
                    </div>
                `;

                let d = new frappe.ui.Dialog({
                    title: 'Matching Items Found',
                    fields: [
                        {
                            fieldtype: 'HTML',
                            fieldname: 'items_html',
                            options: html
                        }
                    ],
                    primary_action_label: 'Insert Selected Items',
                    primary_action: async function () {
                        const selectedIndexes = [];
                        d.$wrapper.find('.item-select:checked').each(function () {
                            selectedIndexes.push($(this).data('index'));
                        });

                        if (selectedIndexes.length === 0) {
                            frappe.msgprint(__('Please select at least one item to insert.'));
                            return;
                        }

                        for (const index of selectedIndexes) {
                            const matched = frm.__matched_items[index];
                            if (!matched) continue;

                            let bom = await frappe.db.get_value('BOM', { item: matched.name, is_default: 1 }, 'name');

                            const child = frappe.model.add_child(frm.doc, "Customer Worksheet Items", "customer_worksheet_items");
                            child.item_code = matched.name;
                            child.item_name = matched.item_name;
                            child.item_group = matched.item_group;
                            child.uom = matched.stock_uom;
                            child.description = matched.description;

                            if (bom?.message?.name) {
                                child.default_bom = bom.message.name;
                            }
                        }

                        frm.refresh_field("customer_worksheet_items");
                        d.hide();
                        frappe.msgprint({
                            message: '<span style="color:green; font-weight:400;">Items Inserted Successfully into Worksheet</span>',
                            indicator: 'green'
                        });
                    }
                });

                d.show();

                setTimeout(() => {
                    d.$wrapper.find('.toggle-desc').on('click', function () {
                        const index = $(this).data('index');
                        const $descRow = d.$wrapper.find(`#desc-${index}`);
                        $descRow.toggle();
                        const isVisible = $descRow.is(':visible');
                        $(this).text(isVisible ? '' : '');
                    });
                }, 100);
            } else {
                frm.__matched_items = [];
                frappe.msgprint({
                    title: __('No Match Found'),
                    message: __('No matching item exists. Please create a new item.'),
                    indicator: 'red'
                });
            }
        }
    });
}

