frappe.ui.form.on('Item', {
    refresh: function(frm) {
        // Check if item has SKU item group or its descendants before showing buttons
        if (frm.doc.name && frm.doc.item_group) {
            check_item_group_and_show_buttons(frm);
        }
    },
    
    item_group: function(frm) {
        // Refresh buttons when Item Group changes
        if (frm.doc.name && frm.doc.item_group) {
            check_item_group_and_show_buttons(frm);
        }
    },
    
    custom_customer_sku: function(frm) {
        // Refresh buttons when Customer SKU changes
        frm.trigger('refresh');
    }
});

function check_item_group_and_show_buttons(frm) {
    // Check if the item group is "SKU" or a descendant of "SKU"
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Item Group',
            name: frm.doc.item_group
        },
        callback: function(r) {
            if (r.message) {
                const current_item_group = r.message;
				 if (frm.doc.custom_customer_sku && frm.doc.custom_customer_sku.trim() != '') {
					check_if_sku_descendant(frm, current_item_group);
				 }
            }
        }
    });
}

function check_if_sku_descendant(frm, current_item_group) {
    // First check if current item group is "SKU"
    if (current_item_group.name === "SKU") {
        show_quickbase_buttons(frm);
        return;
    }
    
    // Get the "SKU" item group to check if current item group is its descendant
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Item Group',
            name: 'SKU'
        },
        callback: function(r) {
            if (r.message) {
                const sku_item_group = r.message;
                
                // Check if current item group is a descendant of SKU using nested set model
                // In ERPNext, if lft > parent.lft AND rgt < parent.rgt, then it's a descendant
                if (current_item_group.lft > sku_item_group.lft && 
                    current_item_group.rgt < sku_item_group.rgt) {
                    show_quickbase_buttons(frm);
                } else {
                    // Remove any existing QuickBase buttons if item group doesn't qualify
                    remove_quickbase_buttons(frm);
                }
            } else {
                // SKU item group doesn't exist, check manually by traversing parent chain
                check_parent_chain_for_sku(frm, current_item_group);
            }
        },
        error: function() {
            // SKU item group doesn't exist, check manually by traversing parent chain
            check_parent_chain_for_sku(frm, current_item_group);
        }
    });
}

function check_parent_chain_for_sku(frm, current_item_group) {
    // Recursive function to check if any parent in the chain is "SKU"
    if (!current_item_group.parent_item_group || current_item_group.parent_item_group === "All Item Groups") {
        // Reached root without finding SKU
        remove_quickbase_buttons(frm);
        return;
    }
    
    if (current_item_group.parent_item_group === "SKU") {
        show_quickbase_buttons(frm);
        return;
    }
    
    // Continue checking parent
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Item Group',
            name: current_item_group.parent_item_group
        },
        callback: function(r) {
            if (r.message) {
                check_parent_chain_for_sku(frm, r.message);
            } else {
                remove_quickbase_buttons(frm);
            }
        }
    });
}

function show_quickbase_buttons(frm) {
    // Remove existing buttons first to avoid duplicates
    remove_quickbase_buttons(frm);
    
    // Add custom button for QuickBase BOM sync
    frm.add_custom_button(__('Fetch BOM Data from QB'), function() {
        fetch_bom_data_from_quickbase(frm);
    }, __("QuickBase"));
    
    // Add a button to view existing BOMs
    frm.add_custom_button(__('View BOMs'), function() {
        frappe.set_route('List', 'BOM', {'item': frm.doc.name});
    }, __("QuickBase"));
    
    // Add button to view QB sync history
    frm.add_custom_button(__('Sync History'), function() {
        show_sync_history(frm);
    }, __("QuickBase"));
    
    // Make the button group more prominent
    frm.page.set_inner_btn_group_as_primary(__('QuickBase'));
}

function remove_quickbase_buttons(frm) {
    // Remove QuickBase button group if it exists
    if (frm.custom_buttons && frm.custom_buttons['QuickBase']) {
        frm.page.remove_inner_button(__('Fetch BOM Data from QB'), __('QuickBase'));
        frm.page.remove_inner_button(__('View BOMs'), __('QuickBase'));
        frm.page.remove_inner_button(__('Sync History'), __('QuickBase'));
    }
}

function fetch_bom_data_from_quickbase(frm) {
    // Validate Customer SKU exists
    if (!frm.doc.custom_customer_sku || frm.doc.custom_customer_sku.trim() === '') {
        frappe.msgprint({
            title: __('Missing Customer SKU'),
            message: __('Please enter a Customer SKU before fetching BOM data from QuickBase.'),
            indicator: 'orange'
        });
        return;
    }
    
    // Check if BOMs exist for this item
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'BOM',
            filters: {
                item: frm.doc.name
            },
            fields: ['name', 'is_default', 'docstatus']
        },
        callback: function(r) {
            let existing_boms = r.message || [];
            show_bom_sync_confirmation(frm, existing_boms);
        }
    });
}

function show_bom_sync_confirmation(frm, existing_boms) {
    let message = `
        <div style="padding: 15px;">
            <h4> Fetch BOM Data from QuickBase</h4>
            <p><strong>Item:</strong> ${frm.doc.item_code} - ${frm.doc.item_name || ''}</p>
            <p><strong>Item Group:</strong> ${frm.doc.item_group}</p>
            <p><strong>Customer SKU:</strong> <code>${frm.doc.custom_customer_sku}</code></p>
            <hr>
    `;
    
    if (existing_boms.length > 0) {
        message += `<p><strong>Existing BOMs for this item:</strong></p><ul>`;
        existing_boms.forEach(bom => {
            let status = bom.docstatus === 1 ? 'Submitted' : bom.docstatus === 0 ? 'Draft' : 'Cancelled';
            let defaultText = bom.is_default ? ' (Default)' : '';
            message += `<li><a href="/app/bom/${bom.name}" target="_blank">${bom.name}</a> - ${status}${defaultText}</li>`;
        });
        message += `</ul>`;
        message += `<p><em> Fetching data from QuickBase may update or create new BOM records.</em></p>`;
    } else {
        message += `<p><em>No existing BOMs found for this item. A new BOM may be created based on QuickBase data.</em></p>`;
    }
    
    message += `
            <hr>
            <p>This will create a trigger in QuickBase to fetch the latest BOM data for Customer SKU: <strong>${frm.doc.custom_customer_sku}</strong>. This is working on an experimental basis and the data may be incomplete.</p>
        </div>
    `;
    
    frappe.confirm(
		message,
		function() {
			// Yes button action
			create_quickbase_bom_sync_trigger(frm);
		},
		function() {
			// No button action (can be empty or add a small alert)
			// frappe.show_alert({message: __("BOM sync cancelled"), indicator: 'orange'}, 2);
		},
		__('Fetch BOM Data')  // Title as 4th parameter
	);
}

function create_quickbase_bom_sync_trigger(frm) {
    const customer_sku = frm.doc.custom_customer_sku;
    const qb_app = frm.doc.custom_app;
    let custom_query = "";
    let qbsync_mapping_id = "";
    
    if (qb_app == "New JD") {
        custom_query = `{'137'.EX.'${customer_sku}'}`;
        qbsync_mapping_id = "12";
    }
    else if (qb_app == "D&K OMS") {
        custom_query = `{'136'.EX.'${customer_sku}'}`;
        qbsync_mapping_id = "4";
    }
    
    // Prepare the payload for Quickbase trigger
    const payload = {
        "to": "bu3naihjq",  // The Quickbase trigger table ID
        "data": [
            {
                "6": {  // Field ID for Related ERPNext Data Sync Table Mapping
                    "value": qbsync_mapping_id  // BOM table mapping ID (removed $())
                },
                "7": {  // Field ID for Custom Query
                    "value": custom_query  // Removed $()
                },
                "8": {  // Field ID for Skip (optional)
                    "value": ""
                },
                "9": {  // Field ID for Top (optional)
                    "value": ""
                },
                "10": {  // Field ID for Custom Date/Time From (optional)
                    "value": ""
                },
                "11": {  // Field ID for Custom Date/Time To (optional)
                    "value": ""
                },
                "12": {  // Field ID for Batch # (optional)
                    "value": ""
                },
                "13": {  // Field ID for Ignore Date Ranges?
                    "value": true
                },
                "14": {  // Field ID for Pipeline Version
                    "value": "2.0"
                },
                "28": {  // Field ID for Trigger Child Data Sync
                    "value": "1"
                }
            }
        ]
    };
    
    // Show processing message
    frappe.show_alert({
        message: __("Creating QuickBase BOM sync trigger for Customer SKU: {0}...", [customer_sku]),
        indicator: 'blue'
    }, 3);
    
    // Make the API call to create QuickBase trigger
    $.ajax({
        url: "https://api.quickbase.com/v1/records",
        type: "POST",
        contentType: "application/json",
        headers: {
            "QB-Realm-Hostname": "jewelry.quickbase.com",
            "Authorization": "QB-USER-TOKEN b75cz2_tv9_0_bdw4b63bksngicch3ftaxvdi5gg"
        },
        data: JSON.stringify(payload),
        success: function(response) {
            frappe.show_alert({
                message: __("QuickBase BOM sync trigger created successfully!"),
                indicator: 'green'
            }, 5);
            
            console.log("QuickBase trigger response:", response);
            
            // Show success dialog with next steps
            frappe.msgprint({
                title: __('BOM Sync Triggered'),
                message: __(`
                    <div style="padding: 15px;">
                        <h4> QuickBase BOM Sync Started</h4>
                        <p><strong>Item:</strong> ${frm.doc.item_code} - ${frm.doc.item_name || ''}</p>
                        <p><strong>Customer SKU:</strong> ${customer_sku}</p>
                        <p><strong>Query:</strong> ${custom_query}</p>
                        <hr>
                        <h5>What happens next:</h5>
                        <ol>
                            <li>QuickBase will process the BOM data for this Customer SKU</li>
                            <li>BOM records will be created/updated in ERPNext</li>
                            <li>You will receive notifications when the sync is complete</li>
                        </ol>
                        <p><em>This usually takes 1-2 minutes to complete.</em></p>
                        <p><a href="/app/bom?item=${frm.doc.name}" target="_blank"> View BOMs for this Item</a></p>
                    </div>
                `),
                indicator: 'green'
            });
            
            // Add a comment to track the sync
            add_sync_comment(frm, customer_sku);
        },
        error: function(xhr, status, error) {
            const errorMessage = xhr.responseJSON?.message || error;
            
            frappe.show_alert({
                message: __("Failed to create QuickBase BOM sync trigger: {0}", [errorMessage]),
                indicator: 'red'
            }, 8);
            
            console.error("QuickBase error:", xhr.responseText);
            
            // Show detailed error dialog
            frappe.msgprint({
                title: __('BOM Sync Failed'),
                message: __(`
                    <div style="padding: 15px;">
                        <h4> QuickBase BOM Sync Failed</h4>
                        <p><strong>Item:</strong> ${frm.doc.item_code}</p>
                        <p><strong>Customer SKU:</strong> ${customer_sku}</p>
                        <p><strong>Error:</strong> ${errorMessage}</p>
                        <p><strong>Status:</strong> ${status}</p>
                        <hr>
                        <h5>Troubleshooting:</h5>
                        <ul>
                            <li>Verify the Customer SKU exists in QuickBase</li>
                            <li>Check QuickBase API permissions</li>
                            <li>Contact your system administrator if the problem persists</li>
                        </ul>
                    </div>
                `),
                indicator: 'red'
            });
        }
    });
}

function add_sync_comment(frm, customer_sku) {
    // Add a comment to track the sync trigger
    frappe.call({
        method: 'frappe.client.insert',
        args: {
            doc: {
                doctype: 'Comment',
                comment_type: 'Info',
                reference_doctype: 'Item',
                reference_name: frm.doc.name,
                content: `QuickBase BOM sync triggered for Customer SKU: ${customer_sku} at ${frappe.datetime.now_datetime()}`
            }
        },
        callback: function(r) {
            if (r.message) {
                console.log('Sync comment added:', r.message.name);
            }
        }
    });
}

function show_sync_history(frm) {
    // Show sync history from comments
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Comment',
            filters: {
                reference_doctype: 'Item',
                reference_name: frm.doc.name,
                content: ['like', '%QuickBase BOM sync%']
            },
            fields: ['content', 'creation', 'owner'],
            order_by: 'creation desc',
            limit: 10
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                let history_html = '<div style="padding: 15px;"><h4> QuickBase BOM Sync History</h4>';
                
                r.message.forEach(comment => {
                    history_html += `
                        <div style="border-bottom: 1px solid #eee; padding: 10px 0;">
                            <p><strong>${frappe.datetime.str_to_user(comment.creation)}</strong> by ${comment.owner}</p>
                            <p><em>${comment.content}</em></p>
                        </div>
                    `;
                });
                
                history_html += '</div>';
                
                frappe.msgprint({
                    title: __('Sync History'),
                    message: history_html,
                    wide: true
                });
            } else {
                frappe.msgprint(__('No QuickBase BOM sync history found for this item.'));
            }
        }
    });
}

