function toggle_item_spec_display(frm) {
    const item_spec_value = frm.doc.item_spec;
    
    console.log('toggle_item_spec_display called, value:', item_spec_value);
    
    if (!item_spec_value || item_spec_value.trim() === '') {
        // Hide the field when empty
        frm.set_df_property('item_spec', 'hidden', 1);
        frm.refresh_field('item_spec');
        
        // Remove existing buttons
        frm.remove_custom_button(__('Update Item Spec'));
        frm.remove_custom_button(__('Refresh Description'));
        
        // Add the "Update Item Spec" button
        const btn = frm.add_custom_button(__('Update Item Spec'), function() {
            console.log('Update Item Spec button clicked');
            
            // Check if Item Spec already exists for this item
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Item Spec',
                    filters: {
                        'item': frm.doc.name
                    },
                    fields: ['name'],
                    limit: 1
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        // Item Spec exists, open it for editing
                        const existing_spec = r.message[0].name;
                        frappe.set_route('Form', 'Item Spec', existing_spec);
                    } else {
                        // Set route options to prefill the new Item Spec form
                        frappe.route_options = {
                            'item': frm.doc.name,
                            'item_name': frm.doc.item_name
                        };
                        
                        // Navigate to NEW Item Spec form
                        frappe.set_route('Form', 'Item Spec', 'new-item-spec-1');
                    }
                },
                error: function(r) {
                    frappe.msgprint(__('Error checking for existing Item Spec: {0}', [r.message || 'Unknown error']));
                }
            });
        });
        
        if (btn) {
            btn.addClass('btn-primary');
        }
        
    } else {
        // Remove the Update Item Spec button since field has value
        frm.remove_custom_button(__('Update Item Spec'));
        
        // Show the field normally when it has content (leave as link)
        frm.set_df_property('item_spec', 'hidden', 0);
        frm.refresh_field('item_spec');
        
        // Add custom button to refresh description from Item Spec
        if (!frm.is_new()) {
            frm.remove_custom_button(__('Refresh Description')); // Remove existing first
            frm.add_custom_button(__('Refresh Description'), function() {
                update_description(frm);
                frappe.show_alert({
                    message: __('Description refreshed from Item Spec'),
                    indicator: 'green'
                });
            });
        }
    }
}

function update_description(frm) {
    if (!frm.doc.item_spec) {
        // No Item Spec linked, build description with just Item fields
        build_description_without_spec(frm);
        return;
    }

    // Fetch the linked Item Spec document
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Item Spec',
            name: frm.doc.item_spec
        },
        callback: function(r) {
            if (r.message) {
                build_description_with_spec(frm, r.message);
            } else {
                build_description_without_spec(frm);
            }
        }
    });
}

function build_description_with_spec(frm, item_spec) {
    let parts = [];
    let std_desc = frm.doc.description || "";
    let lastPipeIndex = std_desc.lastIndexOf('|');
    let additional_desc;
    
    if (lastPipeIndex !== -1) {
        // Extract text after the last '|'
        additional_desc = std_desc.slice(lastPipeIndex + 1).trim();
    } else {
        // No '|' found, use the whole description
        additional_desc = std_desc.trim();
    }
    
    // Add Item fields to the description
    if (frm.doc.item_name) parts.push(`<b>${frm.doc.item_name}</b>`);
    if (frm.doc.item_group) parts.push(`Item Group: ${frm.doc.item_group}`);
    if (frm.doc.gst_hsn_code) parts.push(`HSN/SAC: ${frm.doc.gst_hsn_code}`);
    
    // Add Item Spec fields that were moved from Item
    if (item_spec.material_id) parts.push(`Material ID: ${item_spec.material_id}`);
    if (item_spec.customer_sku) parts.push(`Customer SKU: ${item_spec.customer_sku}`);
    if (item_spec.vendor_product_id) parts.push(`Vendor Product ID: ${item_spec.vendor_product_id}`);
    if (item_spec.color) parts.push(`Color: ${item_spec.color}`);

    // Add Item Spec fields to the description (without custom_ prefix)
    if (item_spec.gemstone) parts.push(`Gemstone: ${item_spec.gemstone}`);
    if (item_spec.form) parts.push(`Form: ${item_spec.form}`);
    if (item_spec.composition) parts.push(`Composition: ${item_spec.composition}`);
    if (item_spec.dimensions) parts.push(`Dimensions: ${item_spec.dimensions}`);
    if (item_spec.shape) parts.push(`Shape: ${item_spec.shape}`);
    if (item_spec.metal_group) parts.push(`Metal Group: ${item_spec.metal_group}`);
    if (item_spec.purity_multiplier) parts.push(`Purity Multiplier: ${item_spec.purity_multiplier}`);
    if (item_spec.clasp) parts.push(`Clasp: ${item_spec.clasp}`);
    if (item_spec.extender) parts.push(`Extender: ${item_spec.extender}`);
    if (item_spec.diamond_cut) parts.push(`Diamond Cut: ${item_spec.diamond_cut}`);
    if (item_spec.chain_length) parts.push(`Chain Length: ${item_spec.chain_length}`);
    if (item_spec.finding_type) parts.push(`Finding Type: ${item_spec.finding_type}`);
    if (item_spec.plating_group) parts.push(`Plating Group: ${item_spec.plating_group}`);
    if (item_spec.metal_purity_plating) parts.push(`Metal Purity Plating: ${item_spec.metal_purity_plating}`);
    if (item_spec.plating_thickness) parts.push(`Plating Thickness: ${item_spec.plating_thickness}`);
    if (item_spec.thickness_unit_of_measure) parts.push(`Thickness UOM: ${item_spec.thickness_unit_of_measure}`);

    frm.set_value("description", parts.join(" | "));
}

function build_description_without_spec(frm) {
    let parts = [];
    let std_desc = frm.doc.description || "";
    let lastPipeIndex = std_desc.lastIndexOf('|');
    let additional_desc;
    
    if (lastPipeIndex !== -1) {
        // Extract text after the last '|'
        additional_desc = std_desc.slice(lastPipeIndex + 1).trim();
    } else {
        // No '|' found, use the whole description
        additional_desc = std_desc.trim();
    }
    
    // Add only Item fields to the description when no Item Spec is linked
    if (frm.doc.item_name) parts.push(`<b>${frm.doc.item_name}</b>`);
    if (frm.doc.item_group) parts.push(`Item Group: ${frm.doc.item_group}`);
    if (frm.doc.gst_hsn_code) parts.push(`HSN/SAC: ${frm.doc.gst_hsn_code}`);

    frm.set_value("description", parts.join(" | "));
}

