function add_update_spec_button(frm) {
    if (frm.is_new()) return;
    
    // Check if BOM Item Spec column exists
    let grid = frm.fields_dict.items?.grid;
    if (!grid) return;
    
    let has_spec_column = false;
    if (grid.doctype && frappe.meta.get_docfield(grid.doctype, 'bom_item_spec')) {
        has_spec_column = true;
    }
    
    // Only proceed if BOM Item Spec column exists
    if (!has_spec_column) {
        return;
    }
    
    // Remove existing button
    grid.grid_buttons.find('.btn-update-spec').remove();
    
    // Add "Update Spec" button to grid toolbar
    let $button = $(`
        <button class="btn btn-xs btn-default btn-update-spec" style="margin-left: 10px;">
            <i class="fa fa-edit"></i> ${__('Update Spec')}
        </button>
    `);
    
    grid.grid_buttons.append($button);
    
    $button.on('click', function() {
        handle_update_spec(frm);
    });
}

function handle_update_spec(frm) {
    let grid = frm.fields_dict.items.grid;
    let selected_items = grid.get_selected_children();
    
    if (selected_items.length === 0) {
        frappe.msgprint(__('Please select at least one item from the BOM items table.'));
        return;
    }
    
    if (selected_items.length === 1) {
        // Single item selected - open directly
        let item = selected_items[0];
        open_item_spec(item, frm.doc.name);
    } else {
        // Multiple items selected - show confirmation
        frappe.confirm(
            __('You have selected {0} items. Do you want to open specifications for all selected items?', [selected_items.length]),
            function() {
                // Open specs for all selected items
                selected_items.forEach(function(item, index) {
                    setTimeout(function() {
                        open_item_spec(item, frm.doc.name);
                    }, index * 300); // Small delay to avoid conflicts
                });
            }
        );
    }
}

function open_item_spec(bom_item, bom_name) {
    // Check if BOM Item Spec already exists
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'BOM Item Spec',
            filters: {'bom_item_id': bom_item.name},
            fields: ['name'],
            limit: 1
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                // Open existing spec
                frappe.set_route('Form', 'BOM Item Spec', r.message[0].name);
            } else {
                // Create new spec

                frappe.route_options = {
                    'bom_item_id': bom_item.name,
                    'bom': bom_name,
                    'item': bom_item.item_code,
                    'item_name': bom_item.item_name || bom_item.description || ''
                };
				//frappe.new_doc('BOM Item Spec');
				
                frappe.set_route('Form', 'BOM Item Spec', 'new-bom-item-spec-1');

				//create_bom_item_spec_from_bom_item(bom_name, bom_item.name)
            }
        }
    });
}

