// ============ DEBOUNCE MECHANISM FOR CONCURRENT UPDATES ============

let updateTimeout = null;

function debounced_update_parent_bom_item_description(spec_frm) {
    // Don't update if form is not ready or in transitional state
    if (!spec_frm || !spec_frm.doc || spec_frm.is_new() || spec_frm._updating_country_options) {
        return;
    }
    
    // Clear any existing timeout to prevent multiple simultaneous updates
    if (updateTimeout) {
        clearTimeout(updateTimeout);
    }
    
    // Set a new timeout - only update after 800ms of no field changes
    updateTimeout = setTimeout(() => {
        // Double-check form state before updating
        if (spec_frm && spec_frm.doc && !spec_frm._updating_country_options) {
            update_parent_bom_item_description(spec_frm);
        }
        updateTimeout = null;
    }, 800);
}



