    },
    
    setting_type: function(frm) {
        debounced_update_parent_bom_item_description(frm);
    },
    
    merch_color_attribute: function(frm) {
        debounced_update_parent_bom_item_description(frm);
    },
    
    stone_pieces: function(frm) {
