// ============ GEMSTONE AND COUNTRY OF ORIGIN LOGIC ============

function handle_gemstone_change(frm) {
    if (frm.doc.gemstone) {
        // Update country of origin options based on gemstone
        update_country_of_origin_options(frm);
        
        // Only clear country of origin if it's not already being processed
        if (!frm._updating_country_options) {
            frm._updating_country_options = true;
            setTimeout(() => {
                // Clear current country of origin since options changed
                frm.set_value('country_of_origin', null);
                frm._updating_country_options = false;
            }, 100);
        }
        
        // Set requirement for country of origin
        set_country_of_origin_requirements(frm);
        
        console.log(`Gemstone changed to: ${frm.doc.gemstone}`);
    } else {
        // Clear country of origin options and value
        if (!frm._updating_country_options) {
            frm.set_value('country_of_origin', null);
        }
        set_country_of_origin_requirements(frm);
    }
}

function update_country_of_origin_options(frm) {
    if (!frm.doc.gemstone) return;

    frappe.call({
        method: "get_country_of_origin",
        args: {
            gemstone: frm.doc.gemstone
        },
        callback: function(r) {
            if (r.data) {
                const allowed_countries = r.data.map(entry => entry[1]);
                let unique_countries = [...new Set(allowed_countries)];
                console.log("Available countries for", frm.doc.gemstone, ":", unique_countries);
                
                // Update field options
                frm.set_df_property('country_of_origin', 'options', unique_countries);
                frm.refresh_field('country_of_origin');
            }
        },
        error: function(err) {
            console.error("Error fetching country of origin options:", err);
        }
    });

    // Also get from Gemstones doctype as fallback/additional validation
    frappe.db.get_doc("Gemstones", frm.doc.gemstone)
        .then(gemstone => {
            if (gemstone && gemstone.country_of_origin) {
                const allowed_countries = gemstone.country_of_origin.map(co => co.country);
                console.log("Gemstone doc countries:", allowed_countries);
            }
        })
        .catch(err => {
            console.log("Gemstone document not found or no country data");
        });
}

function set_country_of_origin_requirements(frm) {
    const required_groups = ["Stone", "Pearl", "Bead"];
    const is_required = frm.doc.item_group && required_groups.includes(frm.doc.item_group) && !!frm.doc.gemstone;
    
    frm.set_df_property('country_of_origin', 'reqd', is_required);
    frm.refresh_field('country_of_origin');
    
    console.log(`Country of origin required: ${is_required} (Item Group: ${frm.doc.item_group || 'undefined'}, Gemstone: ${frm.doc.gemstone || 'undefined'})`);
}

// ============ ITEM GROUP AND UOM REQUIREMENTS ============

function handle_item_group_requirements(frm) {
    // Set field requirements based on item group
    if (frm.doc.item_group === "Stone") {
        frm.set_df_property('stone_pieces', 'reqd', false);
        frm.set_df_property('avg_carat_weight', 'reqd', false);
        
        // UOM-specific requirements will be handled in handle_uom_requirements
        if (frm.doc.uom) {
            handle_uom_requirements(frm);
        }
    } else {
        // For non-stone items, these fields are not required
        frm.set_df_property('stone_pieces', 'reqd', false);
        frm.set_df_property('avg_carat_weight', 'reqd', false);
    }
}

function handle_uom_requirements(frm) {
    // Reset requirements
    frm.set_df_property('avg_carat_weight', 'reqd', false);
    frm.set_df_property('stone_pieces', 'reqd', false);

    if (frm.doc.item_group === "Stone") {
        if (frm.doc.uom === "Carat") {
            frm.set_df_property('avg_carat_weight', 'reqd', true);
            // Set qty to avg_carat_weight if available
            if (frm.doc.avg_carat_weight) {
                frm.set_value('qty', frm.doc.avg_carat_weight);
            }
        } else if (frm.doc.uom === "Piece") {
            frm.set_df_property('stone_pieces', 'reqd', true);
            // Set qty to stone_pieces if available
            if (frm.doc.stone_pieces) {
                frm.set_value('qty', frm.doc.stone_pieces);
            }
        }
    }
    
    frm.refresh_fields();
}

// ============ DESCRIPTION UPDATE LOGIC ============

function update_parent_bom_item_description(spec_frm) {
    if (!spec_frm.doc.bom || !spec_frm.doc.bom_item_id) {
        return;
    }
    
    // Check BOM status before updating
    frappe.db.get_value('BOM', spec_frm.doc.bom, 'docstatus')
        .then(r => {
            if (r && r.message && r.message.docstatus !== 0) {
                // Only show alert if user manually clicked update button
                // Don't show for automatic updates
                return;
            }
            
            // Build description from current spec form data
            const description = build_description_from_current_spec(spec_frm.doc);
            
            // Update BOM Item description in database (silently for auto-updates)
            frappe.call({
                method: 'frappe.client.set_value',
                args: {
                    doctype: 'BOM Item',
                    name: spec_frm.doc.bom_item_id,
                    fieldname: 'description',
                    value: description
                },
                callback: function(r) {
                    if (r.message) {
                        // Refresh only the specific row in parent BOM form if it's open
                        refresh_specific_bom_item_row(spec_frm.doc.bom, spec_frm.doc.bom_item_id);
                    }
                }
            });
        });
}


function build_description_from_current_spec(spec_doc) {
    let parts = [];

    // Helper function to format decimal values
    function format_number(value, decimals = 2) {
        if (!value || value === 0) return null;
        return parseFloat(value).toFixed(decimals).replace(/\.?0+$/, '');
    }

    // Helper function to format weight with unit
    function format_weight(value, unit = 'g') {
        const formatted = format_number(value);
        return formatted ? `${formatted}${unit}` : null;
    }

    // Helper function to format carat weight
    function format_carat(value) {
        const formatted = format_number(value);
        return formatted ? `${formatted}ct` : null;
    }

    // Helper function to format length
    function format_length(value) {
        const formatted = format_number(value, 1);
        return formatted ? `${formatted}mm` : null;
    }

    // Build description with proper formatting
    if (spec_doc.item) {
        parts.push(`Item: ${spec_doc.item}`);
    }
    
    if (spec_doc.item_group) {
        parts.push(`Item Group: ${spec_doc.item_group}`);
    }
    
    if (spec_doc.gemstone) {
        parts.push(`Gemstone: ${spec_doc.gemstone}`);
    }
    
    // Handle stone_treatments multi-select table
    if (spec_doc.stone_treatments && spec_doc.stone_treatments.length > 0) {
        const treatments = spec_doc.stone_treatments.map(t => t.stone_treatment).filter(t => t).join(', ');
        if (treatments) {
            parts.push(`Stone Treatments: ${treatments}`);
        }
    }
    
    if (spec_doc.grade) {
        parts.push(`Grade: ${spec_doc.grade}`);
    }
    
    if (spec_doc.cut) {
        parts.push(`Cut: ${spec_doc.cut}`);
    }
    
    if (spec_doc.setting_type) {
        parts.push(`Setting Type: ${spec_doc.setting_type}`);
    }
    
    if (spec_doc.merch_color_attribute) {
        parts.push(`Merch Color: ${spec_doc.merch_color_attribute}`);
    }
    
    // Format stone pieces with "pcs" unit
    if (spec_doc.stone_pieces) {
        parts.push(`Pieces: ${spec_doc.stone_pieces} pcs`);
    }
    
    if (spec_doc.country_of_origin) {
        parts.push(`Country: ${spec_doc.country_of_origin}`);
    }
    
    // Format weights with proper units and decimal places
    const sku_weight = format_carat(spec_doc.sku_carat_weight);
    if (sku_weight) {
        parts.push(`SKU Weight: ${sku_weight}`);
    }
    
    const avg_carat = format_carat(spec_doc.avg_carat_weight);
    if (avg_carat) {
        parts.push(`Avg Carat: ${avg_carat}`);
    }
    
    const metal_weight = format_weight(spec_doc.avg_metal_weight);
    if (metal_weight) {
        parts.push(`Metal Weight: ${metal_weight}`);
    }
    
    const sku_metal_weight = format_weight(spec_doc.sku_metal_weight);
    if (sku_metal_weight) {
        parts.push(`SKU Metal Weight: ${sku_metal_weight}`);
    }
    
    if (spec_doc.metal_group) {
        parts.push(`Metal Group: ${spec_doc.metal_group}`);
    }
    
    // Format length with mm unit
    const length = format_length(spec_doc.length);
    if (length) {
        parts.push(`Length: ${length}`);
    }
    
    // Format weight per piece
    const weight_per_piece = format_weight(spec_doc.weight_per_piece);
    if (weight_per_piece) {
        parts.push(`Weight/Piece: ${weight_per_piece}`);
    }

    return parts.join(' | ');
}

function refresh_specific_bom_item_row(bom_name, bom_item_id) {
    // Check if the parent BOM form is currently open and refresh only the specific row
    if (window.cur_frm && window.cur_frm.doc && window.cur_frm.doc.name === bom_name) {
        console.log(`Refreshing specific BOM Item row: ${bom_item_id} in BOM: ${bom_name}`);
        
        // Find the specific row in the items table
        if (window.cur_frm.doc.items) {
            const item_index = window.cur_frm.doc.items.findIndex(item => item.name === bom_item_id);
            if (item_index >= 0) {
                // Refresh only the specific row
                window.cur_frm.fields_dict.items.grid.grid_rows[item_index].refresh();
                console.log(`Refreshed BOM Item row at index: ${item_index}`);
            } else {
                // Fallback: refresh entire items field if specific row not found
                window.cur_frm.refresh_field('items');
                console.log("Specific row not found, refreshed entire items table");
            }
        }
    } else {
        console.log(`Parent BOM form (${bom_name}) is not currently open`);
    }
}

