// ============ FETCH FROM BOM ITEM LOGIC ============

function fetch_fields_from_bom_item(frm) {
    if (!frm.doc.bom_item_id) {
        frappe.msgprint({
            title: 'No BOM Item Linked',
            message: 'Please link a BOM Item first before fetching data.',
            indicator: 'orange'
        });
        return;
    }
    
    // Check if there are existing values that might be overwritten
    const existing_fields = [];
    const field_list = [
        'item', 'item_group', 'gemstone', 'grade', 'cut', 'setting_type', 
        'merch_color_attribute', 'stone_pieces', 'country_of_origin', 'avg_carat_weight', 
        'sku_carat_weight', 'avg_metal_weight', 'sku_metal_weight', 'metal_group', 
        'length', 'weight_per_piece'
    ];
    
    field_list.forEach(field => {
        if (frm.doc[field]) {
            existing_fields.push(field);
        }
    });
    
    // Check stone_treatments table
    if (frm.doc.stone_treatments && frm.doc.stone_treatments.length > 0) {
        existing_fields.push('stone_treatments');
    }
    
    // Show confirmation ONLY if there are existing values
    if (existing_fields.length > 0) {
        frappe.confirm(
            `<strong>Overwrite ${existing_fields.length} existing fields?</strong><br><br>
            <small>Fields: ${existing_fields.join(', ')}</small>`,
            function() {
                // User confirmed - proceed with fetch
                perform_fetch_from_bom_item_via_api(frm);
            }
            // No cancel message needed - silence is better
        );
    } else {
        // No existing data - proceed directly without any alerts
        perform_fetch_from_bom_item_via_api(frm);
    }
}

function perform_fetch_from_bom_item_via_api(frm) {
    const fields_list = [
        'item_code', 'item_name',
        'custom_item_group', 'custom_gemstone', 'custom_grade',
        'custom_cut', 'custom_setting_type', 'custom_merch_color__attribute',
        'custom_stone_pieces', 'custom_country_of_origin', 'custom_avg_carat_weight', 
        'custom_sku_weight', 'custom_metal_group', 'custom_metal_weight', 
        'custom_sku_metal_weight', 'custom_length', 'custom_weight_perpiece',
        'custom_stone_treatment'
    ];

    const requestData = {
        doctype: 'BOM Item',
        name: frm.doc.bom_item_id,
        fields: fields_list,
        limit: 500
    };

    fetch('/api/method/get_doc_with_children', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-Frappe-CSRF-Token': frappe.csrf_token
        },
        body: JSON.stringify(requestData)
    })
    .then(response => response.json())
    .then(data => {
        if (data.success && data.result && data.result.length > 0) {
            let bom_item_data = data.result[0];
            populate_spec_fields_from_bom_data_api(frm, bom_item_data);
        } else if (data.success === false) {
            frappe.msgprint({
                title: 'Error',
                message: data.error || 'Failed to fetch BOM Item data.',
                indicator: 'red'
            });
        } else {
            frappe.msgprint('No data found in BOM Item');
        }
    })
    .catch(error => {
        console.error('JSON API Error:', error);
        frappe.msgprint({
            title: 'API Error',
            message: 'Failed to fetch BOM Item data. Please try again.',
            indicator: 'red'
        });
    });
}

function populate_spec_fields_from_bom_data_api(frm, bom_data) {
    let populated_fields = [];
    
    // Temporarily disable alerts during field population
    const original_show_alert = frappe.show_alert;
    frappe.show_alert = function(alert) {
        // Suppress valuation rate alerts during fetch
        if (alert && alert.message && 
            (alert.message.includes('Valuation Rate not found') || 
             alert.message.includes('valuation_rate'))) {
            return; // Suppress these alerts
        }
        // Allow other alerts through
        original_show_alert.call(this, alert);
    };
    
    try {
        // Map basic fields
        const basic_field_mapping = {
            'item_code': 'item'
        };
        
        Object.keys(basic_field_mapping).forEach(bom_field => {
            const spec_field = basic_field_mapping[bom_field];
            if (bom_data[bom_field]) {
                frm.set_value(spec_field, bom_data[bom_field]);
                populated_fields.push(spec_field);
            }
        });
        
        // Map custom fields
        const custom_field_mapping = {
            'custom_item_group': 'item_group',
            'custom_gemstone': 'gemstone',
            'custom_grade': 'grade',
            'custom_cut': 'cut',
            'custom_setting_type': 'setting_type',
            'custom_merch_color__attribute': 'merch_color_attribute',
            'custom_stone_pieces': 'stone_pieces',
            'custom_country_of_origin': 'country_of_origin',
            'custom_avg_carat_weight': 'avg_carat_weight',
            'custom_sku_weight': 'sku_carat_weight',
            'custom_metal_group': 'metal_group',
            'custom_metal_weight': 'avg_metal_weight',
            'custom_sku_metal_weight': 'sku_metal_weight',
            'custom_length': 'length',
            'custom_weight_perpiece': 'weight_per_piece'
        };
        
        Object.keys(custom_field_mapping).forEach(bom_field => {
            const spec_field = custom_field_mapping[bom_field];
            const value = bom_data[bom_field];
            
            if (value !== null && value !== undefined && value !== '') {
                frm.set_value(spec_field, value);
                populated_fields.push(spec_field);
            }
        });
        
        // Handle stone_treatment -> stone_treatments conversion
        if (bom_data.custom_stone_treatment) {
            frm.clear_table('stone_treatments');
            let row = frm.add_child('stone_treatments');
            row.stone_treatment = bom_data.custom_stone_treatment;
            frm.refresh_field('stone_treatments');
            populated_fields.push('stone_treatments');
        }
        
    } finally {
        // Always restore the original show_alert function
        setTimeout(() => {
            frappe.show_alert = original_show_alert;
        }, 1000); // Restore after 1 second to catch any delayed alerts
    }
    
    // Single success message - no verbose dialogs
    if (populated_fields.length > 0) {
        frappe.show_alert({
            message: `Fetched ${populated_fields.length} fields successfully`,
            indicator: 'green'
        });
        
        // Update country of origin options if gemstone was populated
        if (populated_fields.includes('gemstone') && bom_data.custom_gemstone) {
            setTimeout(() => {
                update_country_of_origin_options(frm);
                set_country_of_origin_requirements(frm);
            }, 500);
        }
        
        // Set field requirements
        set_country_of_origin_requirements(frm);
        handle_uom_requirements(frm);
        
    } else {
        frappe.msgprint('No data found to fetch from BOM Item');
    }
}
// ============ VALIDATION LOGIC ============

function validate_bom_item_spec(frm) {
    let has_error = false;
    const required_groups = ["Stone", "Pearl", "Bead"];
    
    // Validate country of origin for gemstones (consistent with BOM validation)
    if (required_groups.includes(frm.doc.item_group) && 
        frm.doc.gemstone && 
        !frm.doc.country_of_origin) {
        
        frappe.msgprint(`Please enter Country of Origin for gemstone "${frm.doc.gemstone}".`);
        has_error = true;
    }
    
    // Validate stone-specific fields (consistent with BOM validation)
    if (frm.doc.item_group === "Stone") {
        if (frm.doc.uom === "Carat" && (!frm.doc.avg_carat_weight || frm.doc.avg_carat_weight === 0)) {
            frappe.msgprint(`"Avg Carat Weight" is required when UOM is Carat for Stone items.`);
            has_error = true;
        }
        
        if (frm.doc.uom === "Piece" && (!frm.doc.stone_pieces || frm.doc.stone_pieces === 0)) {
            frappe.msgprint(`"Stone Pieces" is required when UOM is Piece for Stone items.`);
            has_error = true;
        }
    }
    
    // Validate that BOM and BOM Item ID are present
    if (!frm.doc.bom) {
        frappe.msgprint("BOM is required for BOM Item Spec.");
        has_error = true;
    }
    
    if (!frm.doc.bom_item_id) {
        frappe.msgprint("BOM Item ID is required for BOM Item Spec.");
        has_error = true;
    }
    
    if (has_error) {
        frappe.validated = false;
    }
}

