function sync_spec_to_bom_item_custom_fields(frm) {
    if (!frm.doc.bom_item_id) {
        frappe.msgprint('No BOM Item linked to this spec for syncing');
        return;
    }
    
    const field_mapping = {
        'item_group': 'custom_item_group',
        'gemstone': 'custom_gemstone',
        'grade': 'custom_grade',
        'cut': 'custom_cut',
        'setting_type': 'custom_setting_type',
        'merch_color_attribute': 'custom_merch_color__attribute',
        'stone_pieces': 'custom_stone_pieces',
        'country_of_origin': 'custom_country_of_origin',
        'avg_carat_weight': 'custom_avg_carat_weight',
        'sku_carat_weight': 'custom_sku_weight',
        'metal_group': 'custom_metal_group',
        'avg_metal_weight': 'custom_metal_weight',
        'sku_metal_weight': 'custom_sku_metal_weight',
        'length': 'custom_length',
        'weight_per_piece': 'custom_weight_perpiece'
    };
    
    let updates = {};
    Object.keys(field_mapping).forEach(spec_field => {
        if (frm.doc[spec_field]) {
            updates[field_mapping[spec_field]] = frm.doc[spec_field];
        }
    });
    
    // Handle stone_treatments multi-select table -> single field conversion
    if (frm.doc.stone_treatments && frm.doc.stone_treatments.length > 0) {
        const first_treatment = frm.doc.stone_treatments[0].stone_treatment;
        if (first_treatment) {
            updates['custom_stone_treatment'] = first_treatment;
        }
    }
    
    if (Object.keys(updates).length === 0) {
        frappe.msgprint('No spec fields to sync to BOM Item custom fields');
        return;
    }
    
    // Update BOM Item with custom field values
    frappe.call({
        method: 'frappe.client.set_value',
        args: {
            doctype: 'BOM Item',
            name: frm.doc.bom_item_id,
            fieldname: updates
        },
        callback: function(r) {
            if (r.message) {
                frappe.show_alert({
                    message: `Synced ${Object.keys(updates).length} fields to BOM Item`,
                    indicator: 'green'
                });
                
                refresh_specific_bom_item_row(frm.doc.bom, frm.doc.bom_item_id);
            }
        },
        error: function(err) {
            frappe.msgprint('Error syncing to BOM Item');
        }
    });
}

// ============ MASS UPDATE FUNCTION (NOT CALLED BY INDIVIDUAL SPEC BUTTONS) ============
// This function updates ALL BOM Items for a BOM - only called from BOM level operations
function mass_update_all_bom_item_descriptions_from_specs(bom_name) {
    console.log("MASS UPDATE: Updating ALL BOM Items for BOM:", bom_name);
    
    // Check BOM status before mass update
    frappe.db.get_value('BOM', bom_name, 'docstatus')
        .then(r => {
            if (r && r.message && r.message.docstatus !== 0) {
                frappe.show_alert({
                    message: `Cannot update descriptions - BOM ${bom_name} is ${r.message.docstatus === 1 ? 'submitted' : 'cancelled'}`,
                    indicator: 'orange'
                });
                return;
            }
            
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'BOM Item Spec',
                    filters: {'bom': bom_name},
                    fields: [
                        'name', 'bom_item_id', 'item', 'item_group', 'gemstone', 'stone_treatments',
                        'grade', 'cut', 'setting_type', 'merch_color_attribute', 
                        'stone_pieces', 'country_of_origin', 'sku_carat_weight', 
                        'avg_carat_weight', 'avg_metal_weight', 'sku_metal_weight',
                        'length', 'weight_per_piece', 'metal_group'
                    ]
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        let updated_count = 0;
                        const total_specs = r.message.length;
                        
                        frappe.show_alert({
                            message: `MASS UPDATE: Updating ${total_specs} BOM Item descriptions...`,
                            indicator: "blue"
                        });
                        
                        r.message.forEach((spec, index) => {
                            setTimeout(() => {
                                const description = build_description_from_current_spec(spec);
                                
                                frappe.call({
                                    method: 'frappe.client.set_value',
                                    args: {
                                        doctype: 'BOM Item',
                                        name: spec.bom_item_id,
                                        fieldname: 'description',
                                        value: description
                                    },
                                    callback: function() {
                                        updated_count++;
                                        if (updated_count === total_specs) {
                                            frappe.show_alert({
                                                message: `MASS UPDATE: Updated ${updated_count} BOM Item descriptions from specs`,
                                                indicator: "green"
                                            });
                                            
                                            // Refresh parent BOM if open (entire form since this is mass update)
                                            if (window.cur_frm && window.cur_frm.doc && window.cur_frm.doc.name === bom_name) {
                                                window.cur_frm.refresh_field('items');
                                                console.log("Mass update: Parent BOM form refreshed");
                                            }
                                        }
                                    },
                                    error: function(err) {
                                        console.error(`Error updating BOM Item ${spec.bom_item_id}:`, err);
                                        updated_count++; // Count failed updates too
                                        if (updated_count === total_specs) {
                                            frappe.show_alert({
                                                message: `MASS UPDATE: Completed (some may have failed due to permissions)`,
                                                indicator: "orange"
                                            });
                                        }
                                    }
                                });
                            }, index * 100); // Small delay to avoid overwhelming the system
                        });
                    } else {
                        frappe.msgprint('No BOM Item Specs found for this BOM');
                    }
                }
            });
        })
        .catch(err => {
            console.error("Error checking BOM status for mass update:", err);
            frappe.show_alert({
                message: `Error checking BOM status for ${bom_name}`,
                indicator: 'red'
            });
        });
}

