frappe.ui.form.on('Customer Worksheet', {
    refresh(frm) {
        if (frm.doc.docstatus < 2 && !frm.is_new()) {
            frm.add_custom_button('Verify Items', () => {
                const items = frm.doc.customer_worksheet_items || [];
                if (items.length === 0) {
                    frappe.msgprint("No items to verify.");
                    return;
                }

                const grouped = {};
                let grand_total_qty = 0;
                let grand_total_amount = 0;

                items.forEach(row => {
                    if (!row.item_code) return;

                    if (!grouped[row.item_code]) {
                        grouped[row.item_code] = {
                            item_name: row.item_name,
                            rows_count: 0,
                            total_qty: 0,
                            total_amount: 0,
                            rates: new Set()
                        };
                    }

                    let qty = flt(row.qty) || 1;
                    let rate = flt(row.rate) || 0;
                    let amount = rate * qty;


                    grouped[row.item_code].rows_count += 1;
                    grouped[row.item_code].total_qty += qty;
                    grouped[row.item_code].total_amount += amount;
                    if (rate) grouped[row.item_code].rates.add(rate);
                });

                const htmlRows = Object.entries(grouped).map(([item_code, data], idx) => {
                    grand_total_qty += data.total_qty;
                    grand_total_amount += data.total_amount;

                    let rate_display = "0.00";
                    if (data.rates.size === 1) {
                        rate_display = `$${Array.from(data.rates)[0].toFixed(2)}`;
                    } else if (data.rates.size > 1) {
                        rate_display = "Varies";
                    }

                    return `
                        <tr>
                            <td>${idx + 1}</td>
                            <td>${item_code}</td>
                            <td>${data.item_name}</td>
                            <td>${data.rows_count}</td>
                            <td>${data.total_qty}</td>
                            <td>${rate_display}</td>
                            <td>$${data.total_amount.toFixed(2)}</td>
                        </tr>
                    `;
                }).join("");

                const dialog_html = `
                    <div style="max-height: 400px; overflow-y: auto;">
                    <table class="table table-bordered">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Item Code</th>
                                <th>Item Name</th>
                                <th>Rows / Sizes</th>
                                <th>Qty</th>
                                <th>Selling Rate</th>
                                <th>Selling Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            ${htmlRows}
                        </tbody>
                    </table>
                    </div>
                    <div style="text-align: right; font-weight: bold; margin-top: 10px;">
                        Total Qty: ${grand_total_qty} <br/>
                        Total Amount: $${grand_total_amount.toFixed(2)}
                    </div>
                `;

                new frappe.ui.Dialog({
                    title: 'Verify Items',
                    size: 'extra-large',
                    fields: [
                        {
                            fieldname: 'summary_table',
                            fieldtype: 'HTML',
                            options: dialog_html
                        }
                    ]
                }).show();
            });
        }
    }
});

