        searchBOM: async function(frm) {
            try {
                if (!frm.doc.finished_goods_item) {
                    frappe.msgprint(__("Please select a Finished Goods Item first."));
                    return;
                }
        
                const { message: boms } = await frappe.call({
                    method: "frappe.client.get_list",
                    args: {
                        doctype: "BOM",
                        filters: { item: frm.doc.finished_goods_item },
                        order_by: "creation desc",
                        limit_page_length: 1,
                        fields: ["name"]
                    }
                });
        
                if (boms?.length) {
                    const bom_name = boms[0].name;
                    frm.set_value("existing_bom", bom_name);
        
                    // DEBUG LOG
                    console.log("Setting BOM No to:", bom_name);
        
                    (frm.doc.bom_items || []).forEach(row => {
                        console.log("Row:", row.name, "Setting bom_no to:", bom_name);
                        frappe.model.set_value(row.doctype, row.name, 'bom_no', bom_name); // change 'bom_no' if fieldname is different
                    });
        
                    // Refresh the table so changes reflect visually
                    frm.fields_dict['bom_items'].grid.refresh();
        
                } else {
                    this._addDefaultBOMItems(frm);
                    frappe.msgprint({
                        title: __("No BOM Found"),
                        message: __("No active BOM found for item {0}. Added default items.", [frm.doc.finished_goods_item]),
                        indicator: "orange"
                    });
                    await this.handleSearch(frm);
                }
        
            } catch (error) {
                console.error("BOM search error:", error);
                frappe.msgprint({
                    title: __("Error"),
                    message: __("Error searching for BOM: {0}", [error.message]),
                    indicator: "red"
                });
            }
        }

