        _addDefaultBOMItems: async function(frm) {
            const defaultItems = [
                { item_code: "CPF", qty: 1, uom: "Piece" },
                { item_code: "Setting", qty: 1, uom: "Piece" }
            ];
        
            frm.set_value("existing_bom", null);
            frm.clear_table("bom_items");
        
            for (const item of defaultItems) {
                try {
                    const r = await frappe.db.get_value("Item", item.item_code, ["stock_uom", "item_group"]);
                    const row = frm.add_child("bom_items");
                    row.item_code = item.item_code;
                    row.item_name = item.item_code;
                    row.qty = item.qty;
                    row.uom = r.message.stock_uom || item.uom;
                    row.custom_item_group = r.message.item_group || '';
                } catch (err) {
                    console.error(`Error fetching item group for ${item.item_code}:`, err);
                }
            }
        
            frm.refresh_field("bom_items");
        }
