        {
            fieldtype: 'HTML',
            fieldname: 'total_count_html',
            options: totalCountHtml
        },
        {
            fieldname: 'items_table',
            fieldtype: 'Table',
            label: 'Matching Items',
            cannot_add_rows: true,
            in_place_edit: false,
            read_only: false,
            fields: [
                { fieldtype: 'Data', fieldname: 'item_code', label: 'Item Code', in_list_view: true, read_only: 1 },
                { fieldtype: 'Data', fieldname: 'item_name', label: 'Item Name', in_list_view: true, read_only: 1 },
                { fieldtype: 'Data', fieldname: 'description', label: 'Description', read_only: 1 },
                { fieldtype: 'Select', fieldname: 'custom_gemstone', label: 'Gemstone', read_only: 1 }
            ],
            data: items,
            get_data: () => items
        }
    ];

    dialog = new frappe.ui.Dialog({
        size: 'large',
        title: 'Select Items to Create a new BOM',
        fields: fields,
        primary_action_label: 'Insert',
        primary_action: (values) => {
            const selected = dialog.fields_dict.items_table.grid.get_selected_children();

            if (selected.length === 0) {
                frappe.msgprint(__('Please select at least one item.'));
                return;
            }

            selected.forEach(item => {
                const child = frm.add_child('bom_items', {
                    item_code: item.item_code,
                    item_name: item.item_name,
                    description: item.description,
                    custom_item_group: item.item_group || '',
                    uom: item.stock_uom,
                    custom_gemstone: item.custom_gemstone,
                    custom_metal_group: item.custom_metal_group,
                    custom_country_of_origin: item.custom_country_of_origin,
                    custom_selling_rate: item.custom_selling_rate
                });

                if (item.item_group === "Services") {
                    child.qty = 1;
                    child.uom = "Piece";
                }
            });

            frm.refresh_field('bom_items');

            const hold_item_group = dialog.get_value('dialog_hold_item_group');
            frm.set_value('keyword_search', '');
            dialog.set_value('dialog_keyword_search', '');

            if (!hold_item_group) {
                frm.set_value('item_group_', '');
                dialog.set_value('dialog_item_group', '');
            }

            dialog.fields_dict.items_table.grid.df.data = [];
            dialog.fields_dict.items_table.grid.grid_rows = [];
            dialog.fields_dict.items_table.grid.wrapper.empty();
            dialog.fields_dict.items_table.grid.make();
            $(dialog.fields_dict.items_table.grid.wrapper).find('.grid-footer').hide();

            dialog.fields_dict.total_count_html.$wrapper.html(
                `<div style="text-align: right; font-weight: bold; margin-bottom: 8px;color:green;">
                    Items Inserted: ${selected.length}
                </div>`
            );

            frm.refresh_field('bom_status');
            frm.fields_dict.bom_status?.$wrapper?.html(
                `<div style="color:blue;"><strong>Items Inserted:</strong> ${selected.length}.</div>`
            );
        }
    });

    dialog.show();

    if (dialog.get_value('dialog_item_group') === 'Stone') {
        dialog.set_value('dialog_hold_item_group', 1);
    }

    setTimeout(() => {
        dialog.fields_dict.dialog_item_group.df.onchange = () => {
            const ig = dialog.get_value('dialog_item_group');
            dialog.set_value('dialog_hold_item_group', ig === 'Stone' ? 1 : 0);
        };
    }, 100);

    this.current_dialog = dialog;
}

