        updateBOMItemDescription: function(frm, cdt, cdn) {
            const row = locals[cdt][cdn];
            if (!row) return;
        
            const existing_desc = row.description || '';
            let updated_desc = existing_desc;
        
            const parts = [];
        
            if (row.item_name && !existing_desc.includes(`Item Name: ${row.item_name}`))
                parts.push(`Item Name: ${row.item_name}`);
            if (row.custom_item_group && !existing_desc.includes(`Item Group: ${row.custom_item_group}`))
                parts.push(`Item Group: ${row.custom_item_group}`);
            if (row.custom_gemstone && !existing_desc.includes(`Gemstone: ${row.custom_gemstone}`))
                parts.push(`Gemstone: ${row.custom_gemstone}`);
            if (row.custom_grade && !existing_desc.includes(`Grade: ${row.custom_grade}`))
                parts.push(`Grade: ${row.custom_grade}`);
            if (row.custom_cut && !existing_desc.includes(`Cut: ${row.custom_cut}`))
                parts.push(`Cut: ${row.custom_cut}`);
            if (row.custom_setting_type && !existing_desc.includes(`Setting Type: ${row.custom_setting_type}`))
                parts.push(`Setting Type: ${row.custom_setting_type}`);
            if (row.custom_merch_color && !existing_desc.includes(`Merch Color: ${row.custom_merch_color}`))
                parts.push(`Merch Color: ${row.custom_merch_color}`);
            if (row.custom_stone_pieces && !existing_desc.includes(`Pieces: ${row.custom_stone_pieces}`))
                parts.push(`Pieces: ${row.custom_stone_pieces}`);
            if (row.custom_country_of_origin && !existing_desc.includes(`Country: ${row.custom_country_of_origin}`))
                parts.push(`Country: ${row.custom_country_of_origin}`);
            if (row.custom_sku_weight && !existing_desc.includes(`SKU Weight: ${row.custom_sku_weight}`))
                parts.push(`SKU Weight: ${row.custom_sku_weight}`);
            if (row.custom_avg_carat_weight && !existing_desc.includes(`Avg Carat: ${row.custom_avg_carat_weight}`))
                parts.push(`Avg Carat: ${row.custom_avg_carat_weight}`);
            if (row.custom_metal_weight && !existing_desc.includes(`Metal Weight: ${row.custom_metal_weight}`))
                parts.push(`Metal Weight: ${row.custom_metal_weight}`);
            if (row.custom_length && !existing_desc.includes(`Length: ${row.custom_length}`))
                parts.push(`Length: ${row.custom_length}`);
            if (row.custom_weight_perpiece && !existing_desc.includes(`Weight/Piece: ${row.custom_weight_perpiece}`))
                parts.push(`Weight/Piece: ${row.custom_weight_perpiece}`);
            if (row.custom_our_responsibility && !existing_desc.includes(`Responsibility: ${row.custom_our_responsibility}`))
                parts.push(`Responsibility: ${row.custom_our_responsibility}`);
            if (row.custom_merch_color__attribute && !existing_desc.includes(`Merch Color: ${row.custom_merch_color__attribute}`))
                parts.push(`Merch Color: ${row.custom_merch_color__attribute}`);
        
            if (parts.length > 0) {
                updated_desc += (updated_desc ? ' | ' : '') + parts.join(' | ');
                frappe.model.set_value(cdt, cdn, 'description', updated_desc);
                frm.fields_dict["bom_items"].grid.refresh_row(cdn);
            }
        }
,

