        handleFormRender: function(frm, cdt, cdn) {
            const row = locals[cdt][cdn];
            this.updateCountryOfOriginOptions(frm, cdt, cdn);

            if (row.custom_coutry) {
                frappe.model.set_value(cdt, cdn, "custom_country_of_origin", row.custom_coutry);
            }
        },

        updateCountryOfOriginOptions: function(frm, cdt, cdn) {
            const row = locals[cdt][cdn];
            const grid_field = frm.fields_dict["bom_items"].grid;

            if (!row.custom_gemstone) {
                frappe.meta.get_docfield("Customer Worksheet BOM Creator Items", "custom_country_of_origin").get_query = () => ({});
                frappe.model.set_value(cdt, cdn, "custom_country_of_origin", null);
                grid_field.refresh();
                return;
            }

            frappe.call({
                method: "get_country_of_origin",
                args: {
                    gemstone: row.custom_gemstone
                },
                callback: function(r) {
                    if (r.exc) {
                        console.error("Error fetching countries:", r.exc);
                        return;
                    }

                    if (r.data) {
                        const unique_countries = [...new Set(r.data.map(entry => entry[1]))];
                        grid_field.update_docfield_property("custom_country_of_origin", "options", unique_countries);
                    }
                }
            });
        }
    };
    
