function updateBOMTotals(frm) {
    const items = frm.doc.customer_bom_items || [];

    if (!items.length) return;

    let total_selling = 0;

    items.forEach(row => {
        total_selling += flt(row.qty) * flt(row.custom_selling_rate || 0);
    });

    function showTotal(symbol = "") {
        if (frm.fields_dict.bom_item_total?.$wrapper) {
            frm.fields_dict.bom_item_total.$wrapper.html(`
                <div style="padding: 8px;">
                    <b>Total BOM Items:</b> ${items.length}<br>
                    <b>Selling Amount:</b> ${symbol} ${total_selling.toFixed(2)}
                </div>
            `);
        }
    }

    if (frm.doc.customer) {
        frappe.db.get_value("Customer", frm.doc.customer, "default_currency", (customerData) => {
            const currency = customerData?.default_currency;

            if (currency) {
                frappe.db.get_value("Currency", currency, "symbol", (currencyData) => {
                    const symbol = currencyData?.symbol || "";
                    showTotal(symbol);
                });
            } else {
                showTotal(""); 
            }
        });
    } else {
        showTotal("");
    }
}




    frappe.ui.form.on('Customer Worksheet', {
        bom_id(frm) {
            setTimeout(() => updateBOMTotals(frm), 500);
        },
        refresh(frm) {
            updateBOMTotals(frm);
        },
        validate(frm) {
            updateBOMTotals(frm);
        },
            finished_goods_item: function(frm) {
        if (!frm.doc.finished_goods_item) return;

        frappe.call({
            method: "frappe.client.get_list",
            args: {
                doctype: "BOM",
                filters: {
                    item: frm.doc.finished_goods_item,
                    // docstatus: 1,
                    is_default: 1
                },
                fields: ["name"],
                limit_page_length: 1
            },
            callback: function(r) {
                if (r.message && r.message.length > 0) {
                    frm.set_value("existing_bom", r.message[0].name);
                } else {
                    frm.set_value("existing_bom", "");
                    // frappe.msgprint("Default BOM not found for selected item.");
                }
            }
        });
    }
    });
    
    frappe.ui.form.on('Customer BOM Items', {
        qty: updateBOMTotals,
        rate: updateBOMTotals,
        custom_selling_rate: updateBOMTotals
    });

    
// ================= Calculation Logic ================
const calculateLogic = {
    calculateQtyAndAll: function(frm, cdt, cdn) {
        this.calculateQty(frm, cdt, cdn);
        this.calculateAll(frm, cdt, cdn);
        this.calculateExtra(frm, cdt, cdn);
    },

    calculateQty: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        let new_qty = 0;
    
        const item_group = (row?.custom_item_group || "").trim();
        const uom = (row.uom || "").toLowerCase();
    
        if (item_group === "Stone") {
            new_qty = (uom === "carat")
                ? flt(row.custom_avg_carat_weight)
                : flt(row.custom_stone_pieces);
        } else if (item_group === "Pearl") {
            new_qty = flt(row.custom_avg_carat_weight); 
        } else if (item_group === "Metal") {
            new_qty = flt(row.custom_metal_weight);
        }
    
        if (flt(row.qty) !== flt(new_qty)) {
            frappe.model.set_value(cdt, cdn, 'qty', new_qty);
        }

},
    calculateAll: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        if (!row) return;

        const qty = flt(row.qty) || 0;
        const rate = flt(row.rate) || 0;
        const sellingRate = flt(row.custom_selling_rate) || 0;

        const amount = qty * rate;
        const sellingAmount = qty * sellingRate;

        frappe.model.set_value(cdt, cdn, 'amount', amount);
        frappe.model.set_value(cdt, cdn, 'custom_selling_amount', sellingAmount);
    },

    calculateExtra: function(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        if (!row) return;

        const metalWeight = flt(row.custom_metal_weight || 0);
        const skuMetalWeight = metalWeight * 0.8;

        frappe.model.set_value(cdt, cdn, 'custom_sku_metal_weight', skuMetalWeight);
    },

    recalculateAllRows: function(frm) {
        (frm.doc.customer_bom_items || []).forEach(row => {
            const cdt = "Customer Worksheet BOM Creator Items";
            const cdn = row.name;
            this.calculateQtyAndAll(frm, cdt, cdn);
        });
    }
};



    BOMCreator.init();
