function init_smart_cache() {
	const session_id = get_session_id();
	const cache_version = '1.0';
	
	window.search_cache = {
		data: new Map(),
		session_id: session_id,
		version: cache_version,
		created_at: Date.now(),
		max_size: 50,
		ttl: 5 * 60 * 1000,
		
		get: function(key) {
			const entry = this.data.get(key);
			if (!entry) return null;
			
			if (Date.now() - entry.timestamp > this.ttl) {
				this.data.delete(key);
				return null;
			}
			
			entry.last_accessed = Date.now();
			return entry.data;
		},
		
		set: function(key, data) {
			if (this.data.size >= this.max_size) {
				this.cleanup_lru();
			}
			
			this.data.set(key, {
				data: data,
				timestamp: Date.now(),
				last_accessed: Date.now()
			});
		},
		
		cleanup_lru: function() {
			const entries = Array.from(this.data.entries())
				.sort((a, b) => a[1].last_accessed - b[1].last_accessed);
			
			const remove_count = Math.floor(this.max_size * 0.2);
			for (let i = 0; i < remove_count; i++) {
				this.data.delete(entries[i][0]);
			}
		},
		
		clear: function() {
			this.data.clear();
		}
	};
}

function get_session_id() {
	const navigation_type = performance.navigation ? performance.navigation.type : 0;
	const page_load_time = performance.timing ? performance.timing.navigationStart : Date.now();
	
	return `${frappe.session.user}_${page_load_time}_${navigation_type}`;
}

function clear_cache() {
	if (window.search_cache) {
		window.search_cache.clear();
	}
}

function add_search_mode_toggle(frm) {
	if (frm.$wrapper.find('.search-mode-toggle-btn').length > 0) {
		update_toggle_button_text(frm);
		return;
	}
	
	const initial_label = window.search_mode === 'enhanced' ? 
		' Enhanced ( Standard)' : ' Standard ( Enhanced)';
	
	const btn = frm.page.add_inner_button(initial_label, function() {
		toggle_search_mode(frm);
	});
	
	btn.addClass('search-mode-toggle-btn');
	btn.attr('data-search-toggle', 'true');
}

function update_toggle_button_text(frm) {
	const new_label = window.search_mode === 'enhanced' ? 
		' Enhanced ( Standard)' : ' Standard ( Enhanced)';
	
	const $toggle_btn = frm.$wrapper.find('.search-mode-toggle-btn');
	if ($toggle_btn.length > 0) {
		$toggle_btn.text(new_label);
	}
}

function toggle_search_mode(frm) {
	window.search_mode = window.search_mode === 'enhanced' ? 'standard' : 'enhanced';
	
	localStorage.setItem('bom_search_mode', window.search_mode);
	update_toggle_button_text(frm);
	
	if (window.search_mode === 'enhanced') {
		setup_enhanced_search(frm);
		update_all_placeholders(frm);
		frappe.show_alert({
			message: 'Enhanced Search Enabled - Multi-word search',
			indicator: 'green'
		});
	} else {
		cleanup_enhanced_search(frm);
		frappe.show_alert({
			message: 'Standard Search Enabled - Default ERPNext search', 
			indicator: 'blue'
		});
	}
}

function load_search_preference() {
	const saved_mode = localStorage.getItem('bom_search_mode');
	if (saved_mode && ['enhanced', 'standard'].includes(saved_mode)) {
		window.search_mode = saved_mode;
	}
}

function cleanup_enhanced_search(frm) {
	frm.$wrapper.off('.enhanced-search');
	hide_suggestions();
	clear_cache();
	
	setTimeout(() => {
		let $inputs = frm.$wrapper.find('[data-fieldname="item_code"] input');
		$inputs.each(function() {
			$(this).attr('placeholder', 'Item Code');
		});
	}, 100);
	
	$('.awesomplete ul').show();
}

function setup_enhanced_search(frm) {
	frm.$wrapper.off('.enhanced-search');
	
	frm.$wrapper.on('input.enhanced-search', '[data-fieldname="item_code"] input', function() {
		let $input = $(this);
		let search_text = $input.val().trim();
		
		clearTimeout(window.search_timeout);
		hide_suggestions();
		hide_standard_dropdown($input);
		
		if (search_text.length >= 3) {
			window.search_timeout = setTimeout(() => {
				perform_enhanced_search(search_text, $input, frm);
			}, 400);
		} else if (search_text.length === 0) {
			setTimeout(() => {
				if ($input.is(':focus') && $input.val().trim().length === 0) {
					perform_default_search($input, frm);
				}
			}, 200);
		} else {
			show_search_hint($input, 'Type at least 3 characters...');
		}
	});
	
	frm.$wrapper.on('focus.enhanced-search', '[data-fieldname="item_code"] input', function() {
		let $input = $(this);
		hide_standard_dropdown($input);
		
		let item_group = frm.doc.custom_item_group_to_filter_items || 'Raw Material';
		$input.attr('placeholder', `Search ${item_group} (min 3 chars)`);
		
		if ($input.val().trim().length === 0) {
			setTimeout(() => perform_default_search($input, frm), 300);
		}
	});
	
	frm.$wrapper.on('keydown.enhanced-search', '[data-fieldname="item_code"] input', function(e) {
		handle_keyboard_navigation(e);
	});
}

async function perform_enhanced_search(search_text, $input, frm) {
	try {
		show_loading($input);
		const item_group = frm.doc.custom_item_group_to_filter_items || "Raw Material";
		
		let results = await get_search_results(search_text, item_group);
		
		if ($input.val().trim() === search_text) {
			show_suggestions($input, results, search_text, item_group, frm);
		}
	} catch (error) {
		console.error('Enhanced search failed:', error);
		
		if ($input.val().trim() === search_text) {
			show_fallback_option($input, frm);
		}
	}
}

function show_fallback_option($input, frm) {
