async function get_search_results(search_text, item_group) {
	let words = search_text.toLowerCase().split(' ').filter(w => w.length >= 2);
	let cache_key = `${item_group}_${words.sort().join('_')}`;
	
	let cached_results = window.search_cache.get(cache_key);
	if (cached_results) {
		return cached_results;
	}
	
	try {
		let all_items = new Map();
		let base_filters = [
			['disabled', '=', 0],
			['is_stock_item', '=', 1],
			['item_group', 'descendants of (inclusive)', item_group]
		];
		
		let name_filters = [...base_filters];
		words.forEach(word => name_filters.push(['item_name', 'like', `%${word}%`]));
		
		let response1 = await frappe.call({
			method: 'frappe.client.get_list',
			args: {
				doctype: 'Item',
				fields: ['name', 'item_name', 'description'],
				filters: name_filters,
				limit_page_length: 40,
				order_by: 'item_name asc'
			}
		});
		
		if (response1.message) {
			response1.message.forEach(item => {
				all_items.set(item.name, {
					item_code: item.name,
					item_name: item.item_name || item.name,
					description: item.description || '',
					score: 100,
					match_source: 'item_name'
				});
			});
		}
		
		let desc_filters = [...base_filters];
		words.forEach(word => desc_filters.push(['description', 'like', `%${word}%`]));
		
		let response2 = await frappe.call({
			method: 'frappe.client.get_list',
			args: {
				doctype: 'Item',
				fields: ['name', 'item_name', 'description'],
				filters: desc_filters,
				limit_page_length: 25,
				order_by: 'item_name asc'
			}
		});
		
		if (response2.message) {
			response2.message.forEach(item => {
				if (all_items.has(item.name)) {
					let existing = all_items.get(item.name);
					existing.score = 150;
					existing.match_source = 'item_name + description';
				} else {
					all_items.set(item.name, {
						item_code: item.name,
						item_name: item.item_name || item.name,
						description: item.description || '',
						score: 75,
						match_source: 'description'
					});
				}
			});
		}
		
		let results = Array.from(all_items.values())
			.sort((a, b) => b.score - a.score)
			.slice(0, 15);
		
		window.search_cache.set(cache_key, results);
		
		return results;
		
	} catch (error) {
		console.error('Search API error:', error);
		throw error;
	}
}

async function perform_default_search($input, frm) {
	try {
		show_loading($input);
		const item_group = frm.doc.custom_item_group_to_filter_items || "Raw Material";
		let cache_key = `default_${item_group}`;
		
		let cached_results = window.search_cache.get(cache_key);
		if (cached_results) {
			show_suggestions($input, cached_results, '', item_group, frm);
			return;
		}
		
		let response = await frappe.call({
			method: 'frappe.client.get_list',
			args: {
				doctype: 'Item',
				fields: ['name', 'item_name', 'description'],
				filters: {
					'disabled': 0,
					'is_stock_item': 1,
					'item_group': ['descendants of (inclusive)', item_group]
				},
				limit_page_length: 8,
				order_by: 'creation desc'
			}
		});
		
		let results = [];
		if (response.message) {
			results = response.message.map(item => ({
				item_code: item.name,
				item_name: item.item_name || item.name,
				description: item.description || '',
				score: 1
			}));
			
			window.search_cache.set(cache_key, results);
		}
		
		show_suggestions($input, results, '', item_group, frm);
	} catch (error) {
		console.error('Default search error:', error);
		hide_suggestions();
	}
}

function apply_item_filters(frm) {
	const item_group = frm.doc.custom_item_group_to_filter_items || "Raw Material";
	
	frm.set_query("item", function() {
