async function updateBOM(frm, sourceTable) {
    const sourceItems = sourceTable === "customer_bom_items"
        ? frm.doc.customer_bom_items
        : frm.doc.bom_items;

    const bom_no = sourceItems.map(r => r.bom_no).find(v => !!v);
    if (!bom_no) {
        frappe.msgprint("No BOM number found in selected tab.");
        return;
    }

    try {
        const response = await frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "BOM",
                name: bom_no
            }
        });

        const bom_doc = response.message;

        if (bom_doc.docstatus === 1) {
            frappe.msgprint({
                title: "BOM is Submitted",
                message: "This BOM is submitted. Please create a new BOM.",
                indicator: "red"
            });

            setTimeout(() => {
                const updateBtn = Array.from(document.querySelectorAll('button')).find(btn => btn.innerText.trim() === 'Update BOM');
                const createBtn = Array.from(document.querySelectorAll('button')).find(btn => btn.innerText.trim() === 'Create BOM');
                if (updateBtn) updateBtn.style.display = 'none';
                if (createBtn) createBtn.style.display = 'inline-block';
            }, 300);
            return;
        }

        const new_items = sourceItems.map(row => ({
            item_code: row.item_code,
            item_name: row.item_name,
            qty: row.qty || 1,
            uom: row.uom,
            rate: row.rate || 0,
            amount: row.amount || 0,
            description: row.description,
            custom_item_group: row.custom_item_group || '',
            custom_selling_amount: row.custom_selling_amount || '',
            custom_gemstone: row.custom_gemstone,
            custom_stone_treatment: row.custom_stone_treatment,
            custom_merch_color__attribute: row.custom_merch_color__attribute,
            custom_country_of_origin: row.custom_country_of_origin,
            custom_grade: row.custom_grade,
            custom_stone_pieces: row.custom_stone_pieces,
            custom_avg_carat_weight: row.custom_avg_carat_weight,
            custom_sku_carat_weight: row.custom_sku_carat_weight,
            custom_cut: row.custom_cut,
            custom_setting_type: row.custom_setting_type,
            custom_metal_weight: row.custom_metal_weight,
            custom_sku_metal_weight: row.custom_sku_metal_weight,
            custom_metal_group: row.custom_metal_group,
            custom_selling_rate: row.custom_selling_rate,
            custom_sku_weight: row.custom_sku_weight,
            custom_ext_qty_required: row.custom_ext_qty_required,
            custom_oh: row.custom_oh,
            custom_net_balance_required: row.custom_net_balance_required,
            custom_our_responsibility: row.custom_our_responsibility,
            custom_bom_id: row.bom_no,
            doctype: "BOM Item",
            parent: bom_no,
            parenttype: "BOM",
            parentfield: "items"
        }));

        const stringify = obj => JSON.stringify(obj, Object.keys(obj).sort());
        const oldItems = (bom_doc.items || []).map(stringify);
        const freshItems = new_items.map(stringify);

        const isChanged = oldItems.length !== freshItems.length ||
            oldItems.some((item, index) => item !== freshItems[index]);

        if (!isChanged) {
            frappe.msgprint("No changes detected in BOM items. Skipping update.");
            return;
        }

        bom_doc.custom_bom_id = bom_no;
        bom_doc.items = new_items;

        await frappe.call({
            method: "frappe.client.save",
            args: { doc: bom_doc }
        });

        frappe.msgprint({
            title: "BOM Updated",
            message: `BOM <a href="/app/bom/${bom_doc.name}" target="_blank">${bom_doc.name}</a> has been updated.`,
            indicator: 'green'
        });
        frm.set_value('bom_id', '');
        frm.set-value('finished_goods_item', '')
        frm.set-value('existing_bom', ''),
        frm.set-value('item_group_', ''),
        frm.set-value('keyword_search', ''),
        frm.clear_table('bom_items')

    } catch (error) {
        console.error("BOM update error:", error);
        frappe.msgprint("Error while updating BOM.");
    }
}

async function createBOM(frm) {
    const activeTab = frm.__active_tab || "";
    if (activeTab !== "BOM Creator") {
        frappe.msgprint("BOM can only be created from the 'BOM Creator' tab.");
        return;
    }

    if (createBOM.creating_bom) return;
    createBOM.creating_bom = true;

    try {
        if (!frm.doc.bom_items || frm.doc.bom_items.length === 0) {
            frappe.msgprint("No items found in BOM Items table.");
            return;
        }

        if (!frm.doc.finished_goods_item) {
            frappe.msgprint("Please select a Finished Goods Item.");
            return;
        }

        const fg_item = frm.doc.finished_goods_item;

        const existing_boms = await frappe.db.get_list('BOM', {
            filters: {
                item: fg_item,
                is_default: 1,
                docstatus: 0
            },
            fields: ['name']
        });

        for (const bom of existing_boms) {
            await frappe.call({
                method: "frappe.client.set_value",
                args: {
                    doctype: "BOM",
                    name: bom.name,
                    fieldname: "is_default",
                    value: 0
                }
            });
        }

        const items = frm.doc.bom_items.map(row => ({
            item_code: row.item_code,
            item_name: row.item_name,
            qty: row.qty || 1,
            uom: row.uom || 'Nos',
            rate: row.rate || 0,
            amount: row.amount || 0,
            description: row.description,
            item_group: row.custom_item_group || '',
            custom_selling_rate: row.custom_selling_rate || '',
            custom_selling_amount: row.custom_selling_amount || '',
            custom_gemstone: row.custom_gemstone,
            custom_stone_treatment: row.custom_stone_treatment,
            custom_merch_color__attribute: row.custom_merch_color__attribute,
            custom_country_of_origin: row.custom_country_of_origin,
            custom_grade: row.custom_grade,
            custom_stone_pieces: row.custom_stone_pieces,
            custom_avg_carat_weight: row.custom_avg_carat_weight,
            custom_sku_carat_weight: row.custom_sku_carat_weight,
            custom_cut: row.custom_cut,
            custom_setting_type: row.custom_setting_type,
            custom_metal_weight: row.custom_metal_weight,
            custom_sku_metal_weight: row.custom_sku_metal_weight,
            custom_metal_group: row.custom_metal_group,
            custom_sku_weight: row.custom_sku_weight,
            custom_ext_qty_required: row.custom_ext_qty_required,
            custom_oh: row.custom_oh,
            custom_net_balance_required: row.custom_net_balance_required,
            custom_our_responsibility: row.custom_our_responsibility,
            custom_bom_id: row.bom_no
        }));

        const bom_doc = await frappe.call({
            method: "frappe.client.insert",
            freeze: true,
            freeze_message: "Creating BOM...",
            args: {
                doc: {
                    doctype: "BOM",
                    item: fg_item,
                    quantity: 1,
                    is_active: 1,
                    is_default: 1,
                    docstatus: 0,
                    items: items
                }
            }
        });

        const bom_name = bom_doc.message.name;
        await frappe.call({
            method: "frappe.client.set_value",
            args: {
                doctype: "BOM",
                name: bom_name,
                fieldname: "custom_bom_id",
                value: bom_name
            }
        });

        (frm.doc.customer_worksheet_items || []).forEach(row => {
            row.default_bom = row.item_code === fg_item ? bom_name : '';
        });

        (frm.doc.bom_items || []).forEach(row => {
            row.bom_no = bom_name;
        });

        (frm.doc.customer_bom_items || []).forEach(row => {
            row.bom_no = bom_name;
        });

        frm.refresh_field("bom_items");
        frm.refresh_field("customer_worksheet_items");
        frm.refresh_field("customer_bom_items");
        
        await frm.save();
        await frm.reload_doc();


        frm.fields_dict.bom_status?.$wrapper?.html(
            `<div style="color:green;"><strong>BOM Created:</strong> <a href="/app/bom/${bom_name}" target="_blank">${bom_name}</a></div>`
        );

        frappe.msgprint({
            title: "BOM Created",
            message: `New BOM created: <a href="/app/bom/${bom_name}" target="_blank">${bom_name}</a>`,
            indicator: 'green'
        });

    } catch (err) {
        console.error("Failed to create BOM:", err.message || err);
        frappe.msgprint("Error while creating BOM.");
    } finally {
        createBOM.creating_bom = false;
    }
}

function get_uom_for_group(group) {
    if (["Stone", "Pearl", "Bead"].includes(group)) return 'Carat';
    if (group === "Metal") return 'Gram';
    return 'Piece';
}

