function styleAndToggleButton(retry = 0) {
    const itemGroups = (frm.doc.customer_worksheet_items || []).map(row =>
        row.item_group?.toLowerCase?.().trim()
    );
    const hasRelevantGroup = itemGroups.includes("bracelet") || itemGroups.includes("bangle");

    const $dupBtn = $('button').filter(function () {
        return $(this).text().trim().toLowerCase() === 'duplicate bom';
    });

    if ($dupBtn.length) {
        $dupBtn.addClass('black-btn');
        $dupBtn.toggle(hasRelevantGroup);

        $dupBtn.off('click').on('click', () => {
            const matchingItems = (frm.doc.customer_worksheet_items || []).filter(row => {
                const group = row.item_group?.toLowerCase?.().trim();
                return group === "bracelet" || group === "bangle";
            });

            if (matchingItems.length === 0) {
                frappe.msgprint("No Bracelet or Bangle items found.");
                return;
            }

            const htmlOptions = matchingItems.map(row => `
                <div style="margin-bottom: 10px;">
                    <label style="display: flex; align-items: start; gap: 8px;">
                        <input type="radio" name="item_select" value="${row.name}" style="margin-top: 4px;">
                        <span style="flex: 1; word-break: break-word;">
                            ${row.item_code || ""} : ${row.item_name || ""} (${row.item_group || ""})
                        </span>
                    </label>
                </div>
            `).join('');

            const dialog = new frappe.ui.Dialog({
                title: "Select One to Duplicate",
                size: "small",
                fields: [
                    {
                        fieldtype: 'HTML',
                        fieldname: 'radio_html',
                        options: `<div style="max-height: 300px; overflow-y: auto;">${htmlOptions}</div>`
                    }
                ],
                primary_action_label: "Insert",
                primary_action: async () => {
                    const selectedRowName = dialog.$wrapper.find('input[name="item_select"]:checked').val();
                    if (!selectedRowName) {
                        frappe.msgprint("Please select one item.");
                        return;
                    }

                    const row = frm.doc.customer_worksheet_items.find(r => r.name === selectedRowName);
                    if (!row || !row.default_bom) {
                        frappe.msgprint("No default BOM found in selected item.");
                        return;
                    }

                    const confirmDialog = new frappe.ui.Dialog({
                        title: 'Confirm BOM Duplication',
                        fields: [
                            {
                                fieldtype: 'Section Break',
                                label: 'Existing Item Details'
                            },
                            {
                                fieldtype: 'HTML',
                                fieldname: 'existing_info',
                                options: `
                                    <div style="margin-bottom: 10px;">
                                        <div><b>Item Name:</b> ${row.item_name || ''}</div>
                                        <div><b>Vendor Product ID:</b> ${row.custom_vendor_product_id || ''}</div>
                                    </div>
                                `
                            },
                            {
                                fieldtype: 'Section Break',
                                label: 'New Item Details'
                            },
                            {
                                fieldtype: 'Data',
                                label: 'New Item Name',
                                fieldname: 'new_item',
                                reqd: 1
                            },
                            {
                                fieldtype: 'Data',
                                label: 'Vendor Product ID',
                                fieldname: 'new_vendor_product_id',
                                reqd: 1
                            }
                        ],
                        primary_action_label: 'Duplicate',
                        primary_action: async (values) => {
                        const new_item_name = values.new_item;
                        const new_vendor_id = values.new_vendor_product_id;
                        const default_bom = row.default_bom;
                    
                        try {
                            frappe.dom.freeze("Creating new item...");
                    
                            // 1. Get full item details of the selected row
                            const existing_item = await frappe.call({
                                method: "frappe.client.get",
                                args: {
                                    doctype: "Item",
                                    name: row.item_code
                                }
                            });
                    
                            const item_data = existing_item.message || {};
                    
                            let prefix = (item_data.item_group || "ITEM").substring(0, 3).toUpperCase();
                            
                            const existing_items = await frappe.call({
                                method: "frappe.client.get_list",
                                args: {
                                    doctype: "Item",
                                    filters: [["item_code", "like", `${prefix}-%`]],
                                    fields: ["item_code"],
                                    limit_page_length: 1000
                                }
                            });
                            
                            let max_number = 0;
                            
                            (existing_items.message || []).forEach(d => {
                                const match = d.item_code.match(new RegExp(`^${prefix}-(\\d+)$`));
                                if (match) {
                                    const num = parseInt(match[1]);
                                    if (num > max_number) max_number = num;
                                }
                            });
                            
                            let new_number = (max_number + 1).toString().padStart(4, '0');
                            let new_item_code = `${prefix}-${new_number}`;

                    
                            const new_item = await frappe.call({
                                method: "frappe.client.insert",
                                args: {
                                    doc: {
                                        doctype: "Item",
                                        item_code: new_item_code,
                                        item_name: new_item_name,
                                        item_group: item_data.item_group,
                                        custom_vendor_product_id: new_vendor_id,
                                        description: item_data.description,
                                        stock_uom: item_data.stock_uom,
                                        custom_customer_sku :item_data.custom_customer_sku,
                                        custom_metal_group: item_data.custom_metal_group,
                                        custom_material_id: item_data.custom_material_id,
                                        is_stock_item : item_data.is_stock_item,
                                        gst_hsn_code: item_data.gst_hsn_code,
                                        sales_uom:item_data.sales_uom,
                                        image:item_data.image,
                                        purchase_uom:item_data.purchase_uom
                                    }
                                }
                            });
                            let new_row = frm.add_child("customer_worksheet_items");

                            new_row.item_code = new_item_code;
                            new_row.item_name = new_item_name;
                            new_row.item_group = item_data.item_group;
                            new_row.custom_vendor_product_id = new_vendor_id;
                            
                            // Optional: If you want to auto-assign default BOM or any other values
                            new_row.default_bom = default_bom;
                            
                            frm.refresh_field("customer_worksheet_items");
                    
                            frappe.dom.unfreeze();
                            frappe.msgprint(`New item <a href="/app/item/${new_item_code}" target="_blank"><b>${new_item_code}</b></a> has been created.`);
                    
                            // 4. Now load BOM and insert items
                            frappe.dom.freeze("Loading BOM items...");
                    
                            const bom = await frappe.call({
                                method: "frappe.client.get",
                                args: {
                                    doctype: "BOM",
                                    name: default_bom
                                }
                            });
                    
                            frm.clear_table('bom_items');
                    
                            (bom.message.items || []).forEach(item => {
                                let child = frm.add_child('bom_items');
                    
                                child.item_code = item.item_code;
                                child.item_name = item.item_name;
                                child.custom_item_group = item.custom_item_group;
                                child.qty = item.qty;
                                child.uom = item.uom;
                                child.rate = item.rate || 0.0;
                                child.amount = item.amount || 0;
                                child.custom_selling_rate = item.custom_selling_rate || '';
                                child.custom_selling_amount = item.custom_selling_amount || '';
                                child.custom_gemstone = item.custom_gemstone;
                                child.description = item.description;
                                child.custom_stone_treatment = item.custom_stone_treatment;
                                child.custom_merch_color__attribute = item.custom_merch_color__attribute;
                                child.custom_country_of_origin = item.custom_country_of_origin;
                                child.custom_grade = item.custom_grade;
                                child.custom_stone_pieces = item.custom_stone_pieces;
                                child.custom_avg_carat_weight = item.custom_avg_carat_weight;
                                child.custom_sku_carat_weight = item.custom_sku_carat_weight;
                                child.custom_cut = item.custom_cut;
                                child.custom_setting_type = item.custom_setting_type;
                                child.custom_metal_weight = item.custom_metal_weight;
                                child.custom_sku_metal_weight = item.custom_sku_metal_weight;
                                child.custom_metal_group = item.custom_metal_group;
                                child.custom_sku_weight = item.custom_sku_weight;
                                child.custom_ext_qty_required = item.custom_ext_qty_required;
                                child.custom_oh = item.custom_oh;
                                child.custom_net_balance_required = item.custom_net_balance_required;
                                child.custom_our_responsibility = item.custom_our_responsibility;
                            });
                    
                            frm.refresh_field('bom_items');
                            confirmDialog.hide();
                            dialog.hide();
                            frm.set_value('finished_goods_item', new_item_code);
                    
                            frappe.dom.unfreeze();
                            frappe.msgprint(`Previous Item BOM <b>${default_bom}</b> has been fetched..`);
                    
                        } catch (err) {
                            frappe.dom.unfreeze();
                            console.error("Error:", err);
                            frappe.msgprint("Error during item creation or BOM duplication.");
                        }
                    }

                    });
                    confirmDialog.show();
                }
            });

            dialog.show();
            
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function (res) {
                    const item = res.message || {};
            
                    const html = `
                        <div style="margin-bottom: 10px;">
                            <div><b>Item Name:</b> ${item.item_name || row.item_name || ''}</div>
                            <div><b>Vendor Product ID:</b> ${item.custom_vendor_product_id || 'Not Available'}</div>
                        </div>
                    `;
            
                    // Inject into dialog
                    dialog.fields_dict.existing_info.$wrapper.html(html);
                }
            });

        });
    } else if (retry < 3) {
        setTimeout(() => styleAndToggleButton(retry + 1), 500);
    }
}


    setTimeout(() => {
