 update_documents: function (frm) {
    if (!frm.doc.items_details || frm.doc.items_details.length === 0) {
        frappe.msgprint("No items to update.");
        return;
    }

    let updated_orders = new Set();
    let updates_done = 0;
    let file_uploads_done = 0;

    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Sales Order",
            fields: ["name"],
            limit_page_length: 100000
        },
        callback: function (res) {
            const valid_sales_orders = new Set(res.message.map(so => so.name));
            let delivery_updates = [];

            frm.doc.items_details.forEach(row => {
                const so = row.sales_order;
                // Set delivery date only once per sales order
                let unique_sales_orders = new Set();
                
                frm.doc.items_details.forEach(row => {
                    if (row.sales_order) {
                        unique_sales_orders.add(row.sales_order);
                    }
                });
                
                unique_sales_orders.forEach(so => {
                    set_delivery_date(so, frm.doc.delivery_date);
                });

                if (so && row.item_code && valid_sales_orders.has(so)) {
                    delivery_updates.push({
                        sales_order: so,
                        item_code: row.item_code
                    });

                    if (!updated_orders.has(so)) {
                        updated_orders.add(so);

                        frappe.call({
                            method: "frappe.client.set_value",
                            args: {
                                doctype: "Sales Order",
                                name: so,
                                fieldname: {
                                    contact_person: frm.doc.contact_person,
                                    custom_department: frm.doc.department,
                                    // po_no: frm.doc.customer_po_id
                                }
                            },
                            callback: function () {
                                updates_done++;

                                frm.doc.items_details.forEach(r => {
                                    if (r.sales_order === so) {
                                        r.status = "Updated";
                                    }
                                });
                                frm.refresh_field("items_details");

                                if (frm.doc.po_attachment) {
                                    frappe.call({
                                        method: "frappe.client.insert",
                                        args: {
                                            doc: {
                                                doctype: "File",
                                                file_url: frm.doc.po_attachment,
                                                attached_to_doctype: "Sales Order",
                                                attached_to_name: so,
                                                folder: "Home/Attachments",
                                                is_private: 1
                                            }
                                        },
                                        callback: function () {
                                            file_uploads_done++;
                                            if (file_uploads_done === updated_orders.size) {
                                                frappe.msgprint('Details Updated');
                                            }
                                        }
                                    });
                                } else {
                                    if (updates_done === updated_orders.size) {
                                        frappe.msgprint('Details Updated');
                                    }
                                }
                            }
                        });
                    }
                }
            });

            update_item_data_and_dependencies(frm);
        }
    });
}


