 add_items: function (frm) {
    frappe.call({
        method: "get_items_with_draft_sales_order",
        args: {
            doctype: "Item",
            filters: { custom_vendor_product_id: ["!=", ""] },
            fields: ["name", "item_name", "custom_vendor_product_id", "item_group", "custom_customer_sku", "description"],
            limit_page_length: 100000
        },
        callback: function (r) {
            if (!r.message || r.message.length === 0) {
                frappe.msgprint("No Vendor IDs found.");
                return;
            }

            const all_items = r.message;
            const seen_ids = new Set();
            const unique_items = all_items.filter(item => {
                if (seen_ids.has(item.custom_vendor_product_id)) return false;
                seen_ids.add(item.custom_vendor_product_id);
                return true;
            });

            const highlightMatch = (text, query) => {
                if (!query) return text;
                const escaped = query.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
                const regex = new RegExp(`(${escaped})`, 'gi');
                return text.replace(regex, '<span style="background-color: yellow;">$1</span>');
            };

            const generate_table_rows = (items, query = "") =>
                items.map(item => `
                    <tr class="vendor-row">
                        <td style="text-align:center;"><input type="checkbox" class="vendor-checkbox" data-id="${item.custom_vendor_product_id}" /></td>
                        <td><b>${highlightMatch(item.custom_vendor_product_id, query)}</b></td>
                        <td>${highlightMatch(item.item_name, query)}</td>
                    </tr>
                `).join("");

            const html = `
                <div style="margin-bottom: 12px;">
                    <input type="text" id="vendor-search" placeholder="Search Here"
                        style="flex: 1; padding: 6px 10px; margin-right: 12px; border: 1px solid #ccc; border-radius: 4px;">
                    <button class="btn btn-secondary btn-md" id="vendor-clear-btn">Clear</button>
