function update_value_comparison(frm) {
    let child_qty_total = 0;
    let child_amount_total = 0;

    frm.doc.items_details.forEach(row => {
        child_qty_total += flt(row.qty);
        child_amount_total += flt(row.amount);
    });

    const po_qty_total = flt(frm.doc.total_po_items || 0);
    const po_amount_total = flt(frm.doc.total_po_amount || 0);

    const qty_match = (child_qty_total === po_qty_total);
    const amount_match = (child_amount_total === po_amount_total);

    if (
        child_qty_total === 0 || 
        child_amount_total === 0 || 
        (qty_match && amount_match)
    ) {
        frm.set_df_property("value_comparision", "hidden", 1);
        return;
    }

    frm.set_df_property("value_comparision", "hidden", 0);

    const qty_diff = child_qty_total - po_qty_total;
    const amount_diff = child_amount_total - po_amount_total;

    const qty_diff_color = qty_diff !== 0 ? 'red' : 'inherit';
    const amount_diff_color = amount_diff !== 0 ? 'red' : 'inherit';

    const html = `
        <div style="font-size: 14px; line-height: 1.6;">
            <div stylemargin-top: 4px;">
            <h4 style="margin-bottom: 1px;color:#525252;"><b>Comparison </b><br></h4>
                <b>Purchase Order</b>: ${po_qty_total} units (${format_currency(po_amount_total)})<br>
                <b>Sales Orders</b>: ${child_qty_total} units (${format_currency(child_amount_total)})<br>
                <b>Variance</b>: 
                    <span style="color: ${qty_diff_color};">${qty_diff >= 0 ? '+' : ''}${qty_diff} units</span> 
                    (<span style="color: ${amount_diff_color};">${amount_diff >= 0 ? '+' : ''}${format_currency(amount_diff)}</span>)
            </div>
        </div>
    `;

    frm.set_df_property("value_comparision", "options", html);
}


function update_totals(frm) {
    let total_qty = 0;
    let total_amt = 0;

    frm.doc.items_details.forEach(row => {
        total_qty += flt(row.qty);
        total_amt += flt(row.amount);
    });

    frm.set_value("total_po_items", total_qty);
    frm.set_value("total_po_amount", total_amt);

    update_value_comparison(frm); 
}

