function update_item_data_and_dependencies(frm) {
    frm.doc.items_details.forEach(row => {
        if (!row.item_code) return;

        // Update Item Master
        frappe.call({
            method: "frappe.client.set_value",
            args: {
                doctype: "Item",
                name: row.item_code,
                fieldname: {
                    ...(row.customer_sku ? { custom_customer_sku: row.customer_sku } : {}),
                    ...(row.item_name ? { item_name: row.item_name } : {}),
                    ...(row.description ? { description: row.description } : {})
                }
            },
            callback: function () {
                console.log(`Item ${row.item_code} updated.`);

                // Update BOM
                if (row.bom) {
                frappe.call({
                    method: "frappe.client.get",
                    args: {
                        doctype: "Item",
                        name: row.item_code
                    },
                    callback: function(itemRes) {
                        const updated_item_name = itemRes.message.item_name;
                        const updated_description = itemRes.message.description;
                
                        bom.items.forEach(i => {
                            if (i.item_code === row.item_code) {
                                i.item_name = updated_item_name;
                                i.description = updated_description;
                            }
                        });
                
                        frappe.call({
                            method: "frappe.client.save",
                            args: { doc: bom },
                            callback: function () {
                                console.log(`BOM ${row.bom} synced with Item master values.`);
                            }
                        });
                    }
                });

                }

                // Update Purchase Order
                if (row.purchase_order) {
                    frappe.call({
                        method: "frappe.client.get",
                        args: {
                            doctype: "Purchase Order",
                            name: row.purchase_order
                        },
                        callback: function(poRes) {
                            const po = poRes.message;
                            let updated = false;
                
                            po.items.forEach(po_item => {
                                if (po_item.item_code === row.item_code) {
                                    po_item.description = row.description;
                                    updated = true;
                                }
                            });
                
                            if (updated) {
                                frappe.call({
                                    method: "frappe.client.save",
                                    args: { doc: po },
                                    callback: function () {
                                        console.log(`PO ${row.purchase_order} description updated.`);
                                    }
                                });
                            }
                        }
                    });
                }

            }
        });
    });
}

