function fetch_item_info(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    if (!row.item_code) return;

    frappe.call({
        method: "get_item_related_info",
        args: { item_code: row.item_code },
        callback: function (r) {
            if (r.message) {
                const info = r.message;
                frappe.model.set_value(cdt, cdn, {
                    sales_order: info.sales_order,
                    delivery_date: info.delivery_date,
                    sales_order_status: info.sales_order_status,
                    purchase_order: info.purchase_order,
                    purchase_order_status: info.purchase_order_status,
                    bom: info.bom,
                    bom_status: info.bom_status,
                    qty: info.qty,
                    rate: info.rate,
                    amount: info.amount
                });

                setTimeout(() => {
                    frm.set_df_property("value_comparision", "hidden", 0);
                    update_value_comparison(frm);
                }, 300); // thoda wait so that set_value reflects in DOM
            }
        }
    });
}


